INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^1 {\\frac{{{e^{ - x}}}}{{1 + {e^{ - x}}}}} \\,dx = \\)','(a)\\(\\log \\left( {\\frac{{1 + e}}{e}} \\right) - \\frac{1}{e} + 1\\)','(b)\\(\\log \\left({\\frac{{1 + e}}{{2e}}} \\right) - \\frac{1}{e} + 1\\)','(c)\\(\\log \\left( {\\frac{{1 + e}}{{2e}}} \\right) + \\frac{1}{e} - 1\\)','(d)None ofthese ','2','DEFINITE INTEGRAL','2','Answer =  (b)Put \\(1 + {e^{ - x}} = t \\Rightarrow  - {e^{ - x}}dx = dt\\), then\\(= \\frac{2}{{\\sqrt 2 }}\\left[ {{e^t}\\sin t} \\right]_0^\\pi  = \\frac{2}{{\\sqrt 2}}[0] = 0.\\)we have\\(I = \\int_2^{1 + \\frac{1}{e}} {\\frac{{(t - 1)( - dt)}}{t}}  = \\int_2^{1 +\\frac{1}{e}} {\\left( {\\frac{1}{t} - 1} \\right)} \\,dt\\)\\(= \\left[ {{{\\log }_e}t - t} \\right]_2^{1 + \\frac{1}{e}} = {\\log _e}\\left( {1 +\\frac{1}{e}} \\right) - \\left( {1 + \\frac{1}{e}} \\right) - {\\log _e}2 + 2\\) \\(= {\\log _e}\\left( {\\frac{{e + 1}}{{2e}}} \\right) - \\frac{1}{e} + 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {\\frac{{\\sin x + \\cos x}}{{9 + 16\\sin 2x}}\\,dx = } \\)','(a)\\(\\frac{1}{{20}}\\log 3\\)','(b)\\(\\log 3\\)','(c)\\(\\frac{1}{{20}}\\log 5\\)','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a)Let \\(I = \\int_0^{\\pi /4} {\\frac{{\\sin x + \\cos x}}{{9 + 16\\sin 2x}}\\,} dx\\)Put \\(\\sin x - \\cos x = t\\), then \\((\\sin x + \\cos x)dx = dt\\)\\(I = \\int_{ - 1}^0 {\\frac{{dt}}{{9 + 16(1 - {t^2})}}}  = \\int_{ - 1}^0{\\frac{{dt}}{{25 - 16{t^2}}}}\\)\\(= \\frac{1}{{10}}\\int_{ - 1}^0 {\\left( {\\frac{1}{{5 - 4t}} + \\frac{1}{{5 + 4t}}}\\right)dt}\\)\\(= \\left| {\\frac{1}{{10}}.\\frac{1}{4}[\\log (5 + 4t) - \\log (5 - 4t)]\\,}\\right|_{ - 1}^0\\) \\(= \\frac{1}{{40}}(\\log 9 - \\log 1) = \\frac{1}{{20}}\\log 3.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\pi {\\rm{/4}}}^{\\pi {\\rm{/2}}} {{e^x}(\\log \\sin x + \\cot x)\\,dx = }\\)','(a)\\({e^{\\pi /4}}\\log 2\\)','(b)\\( - {e^{\\pi /4}}\\log 2\\)','(c)\\(\\frac{1}{2}{e^{\\pi /4}}\\log 2\\)','(d)\\( - \\frac{1}{2}{e^{\\pi /4}}\\log 2\\) ','3','DEFINITE INTEGRAL','2','Answer =  (c)Let \\(I = \\int_{\\pi /4}^{\\pi /2} {{e^x}(\\log \\sin x + \\cot x)dx} \\)\\(I = \\int_{\\pi /4}^{\\pi /2} {{e^x}\\log \\sin x\\,dx + \\int_{\\pi /4}^{\\pi /2}{{e^x}\\cot x\\,dx} }\\)\\(= \\int_{\\pi /4}^{\\pi /2} {{e^x}\\log \\sin xdx + [{e^x}\\log \\sin x]_{\\pi /4}^{\\pi/2}}\\)\\(- \\int_{\\pi /4}^{\\pi /2} {{e^x}\\log \\sin x\\,dx}\\)   ',CURRENT_TIMESTAMP);
