INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {\\frac{{dx}}{{1 + \\sin x}}}  = \\)','(a)0','(b)\\(\\frac{1}{2}\\)','(c)2','(d)\\(\\frac{3}{2}\\)    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_0^\\pi  {\\frac{{dx}}{{1 + \\sin x}}}  = \\int_0^\\pi  {\\frac{{1 - \\sinx}}{{{{\\cos }^2}x}}dx = \\int_0^\\pi  {({{\\sec }^2}x - \\sec x\\tan x)dx} } \\) \\(= [\\tan x - \\sec x]_0^\\pi  = [\\tan \\pi  - \\sec \\pi  + 1] = [0 + 1 + 1] = 2.\\)',CURRENT_TIMESTAMP);
