INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{\\cos x}}{{1 + \\cos x + \\sin x}}} \\,dx = \\)','(a)\\(\\frac{\\pi }{4} + \\frac{1}{2}\\log 2\\)','(b)\\(\\frac{\\pi }{4} + \\log 2\\)','(c)\\(\\frac{\\pi }{4} - \\frac{1}{2}\\log 2\\)','(d)\\(\\frac{\\pi }{4} - \\log 2\\)  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_0^{\\pi /2} {\\frac{{\\cos x}}{{1 + \\cos x + \\sin x}}} dx\\)\\(= \\int_0^{\\pi /2} {\\frac{{{{\\cos }^2}(x/2) - {{\\sin }^2}(x/2)}}{{2{{\\cos}^2}(x/2) + 2\\sin (x/2)\\cos (x/2)}}} dx\\)\\(= \\frac{1}{2}\\int_0^{\\pi /2} {\\frac{{1 - {{\\tan }^2}(x/2)}}{{1 + \\tan (x/2)}}}dx = \\frac{1}{2}\\int_0^{\\pi /2} {\\left[ {1 - \\tan \\left( {\\frac{x}{2}} \\right)}\\right]} dx\\)   \\(\\frac{\\pi }{4} + \\log \\frac{1}{{\\sqrt 2 }} = \\frac{\\pi }{4} - \\frac{1}{2}\\log 2.\\)',CURRENT_TIMESTAMP);
