INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{\\sin x\\cos x}}{{1 + {{\\sin }^4}x}}\\,dx = } \\)','(a)\\(\\frac{\\pi }{2}\\)','(b)\\(\\frac{\\pi }{4}\\)','(c)\\(\\frac{\\pi }{6}\\)','(d)\\(\\frac{\\pi }{8}\\)   ','4','DEFINITE INTEGRAL','2','Answer =  (d)Put \\({\\sin ^2}x = t \\Rightarrow dt = 2\\sin x\\cos x\\,dx\\)\\(= \\frac{1}{{36}}({\\pi ^2} + 16).\\)Now\\(\\int_0^{\\pi /2} {\\frac{{\\sin x\\cos x}}{{1 + {{\\sin }^4}x}}dx =\\frac{1}{2}\\int_0^1 {\\frac{1}{{1 + {t^2}}}dt = \\frac{1}{2}[{{\\tan }^{ - 1}}t]_0^1= \\frac{\\pi }{8}} } \\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {{{\\tan }^6}x{{\\sec }^2}x\\,dx = } \\)','(a)\\(\\frac{1}{7}\\)','(b)\\(\\frac{2}{7}\\)','(c)1','(d)None of these    ','1','DEFINITE INTEGRAL','2','Answer =  (a)Put \\(t = \\tan x \\Rightarrow dt = {\\sec ^2}x\\,dx\\)Now \\(\\int_0^{\\pi /4} {{{\\tan }^6}x{{\\sec }^2}xdx = \\int_0^1 {{t^6}dt =\\frac{1}{7}[{t^7}]_0^1 = \\frac{1}{7}} } \\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^2 {\\frac{{{x^3}\\,dx}}{{{{({x^2} + 1)}^{\\frac{3}{2}}}}}}  =\\)','(a)\\({(\\sqrt 2  - 1)^2}\\)','(b)\\(\\frac{{{{(\\sqrt 2  - 1)}^2}}}{{\\sqrt 2 }}\\)','(c)\\(\\frac{{\\sqrt 2  - 1}}{{\\sqrt 2 }}\\)','(d)None of these  ','4','DEFINITE INTEGRAL','2','Answer =  (d)Put \\(t = {x^2} + 1 \\Rightarrow dt = 2x\\,dx\\)\\(\\int_0^2 {\\frac{{{x^3}}}{{{{({x^2} + 1)}^{3/2}}}}dx = \\frac{1}{2}} \\int_1^5{\\frac{{(t - 1)}}{{{t^{3/2}}}}dt = \\frac{1}{2}\\int_1^5 {[{t^{ - 1/2}} - {t^{ -3/2}}]\\,dt} }\\)\\(= \\frac{1}{2}\\left[ {2\\sqrt t  + 2\\frac{1}{{\\sqrt t }}} \\right]_1^5 =\\frac{1}{2}\\left[ {2\\sqrt 5  + \\frac{2}{{\\sqrt 5 }} - 2 - 2} \\right]\\)    \\(= \\left[ {\\sqrt 5  + \\frac{1}{{\\sqrt 5 }} - 2} \\right] = \\frac{{6 - 2\\sqrt 5}}{{\\sqrt 5 }}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /6} {\\frac{{\\sin x}}{{{{\\cos }^3}x}}\\,dx = } \\)','(a)\\(\\frac{2}{3}\\)','(b)\\(\\frac{1}{6}\\)','(c)2','(d)\\(\\frac{1}{3}\\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b)Let \\(I = \\int_0^{\\pi /6} {\\frac{{\\sin x}}{{{{\\cos }^3}x}}dx = \\int_0^{\\pi /6}{\\,\\tan x{{\\sec }^2}x\\,dx} } \\)Put \\(t = \\tan x \\Rightarrow dt = {\\sec ^2}x\\,dx,\\) then we have    \\(I = \\int_0^{\\frac{1}{{\\sqrt 3 }}} {t\\,dt = } \\left[ {\\frac{{{t^2}}}{2}}\\right]_0^{\\frac{1}{{\\sqrt 3 }}} = \\frac{1}{6}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{\\sin x\\cos x\\,dx}}{{{{\\cos }^2}x + 3\\cos x + 2}}}  =\\)','(a)\\(\\log \\left( {\\frac{8}{9}} \\right)\\)','(b)\\(\\log \\left( {\\frac{9}{8}} \\right)\\)','(c)\\(\\log (8 \\times 9)\\)','(d)None of these ','2','DEFINITE INTEGRAL','2','Answer =  (b)Let \\(I = \\int_0^{\\pi /2} {\\frac{{\\sin x\\cos x.dx}}{{{{\\cos }^2}x + 3\\cos x +2}}} \\)We put \\(\\cos x = t \\Rightarrow  - \\sin x\\,dx = dt,\\)then\\(I = \\int_0^1 {\\frac{{t.dt}}{{{t^2} + 3t + 2}} = \\int_0^1 {\\left[ {\\frac{2}{{t +2}} - \\frac{1}{{t + 1}}} \\right]} } \\,dt\\)\\( = [2\\log (t + 2) - \\log (\\,t + 1)]_0^1\\)\\(= [2\\log 3 - \\log 2 - 2\\log 2]\\)    \\(= [2\\log 3 - 3\\log 2] = [\\log 9 - \\log 8] = \\log \\left( {\\frac{9}{8}} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the integral \\(\\int_0^{\\log 5} {\\frac{{{e^x}\\sqrt {{e^x} - 1}}}{{{e^x} + 3}}} \\,dx = \\)','(a)\\(3 + 2\\pi \\)','(b)\\(4 - \\pi \\)','(c)\\(2 + \\pi \\)','(d)None of these ','2','DEFINITE INTEGRAL','2','Answer =  (b)Put \\({e^x} - 1 = {t^2} \\Rightarrow {e^x}dx = 2t\\,dt\\)Also as \\(x = 0\\)to \\(\\log 5,t = 0\\)to 2Therefore, \\(\\int_0^{\\log 5} {\\frac{{{e^x}\\sqrt {{e^x} - 1} }}{{{e^x} + 3}}} dx= \\int_0^2 {\\frac{{2{t^2}}}{{{t^2} + 4}}dt} \\)    \\(= 2\\left[ {\\int_0^2 {1dt - 4\\int_0^2 {\\frac{{dt}}{{{t^2} + 4}}} } } \\right] = 4- \\pi .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the definite integral\\(\\int_0^1 {\\frac{{dx}}{{{x^2} + 2x\\cos \\alpha  + 1}}} \\) for \\(0 < \\alpha  <\\pi \\) is equal to','(a)\\(\\sin \\alpha \\)','(b)\\({\\tan ^{ - 1}}(\\sin \\alpha )\\)','(c)\\(\\alpha \\sin \\alpha \\)','(d)\\(\\frac{\\alpha }{2}{(\\sin \\alpha )^{ - 1}}\\) ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_0^1 {\\frac{{dx}}{{{x^2} + 2x\\cos \\alpha  + 1}} = \\int_0^1{\\frac{{dx}}{{{{(x + \\cos \\alpha )}^2} + 1 - {{\\cos }^2}\\alpha }}} } \\)\\(= \\int_0^1 {\\frac{{dx}}{{{{(x + \\cos \\alpha )}^2} + {{\\sin }^2}\\alpha }} =\\left[ {\\frac{1}{{\\sin \\alpha }}{{\\tan }^{ - 1}}\\frac{{x + \\cos \\alpha }}{{\\sin\\alpha }}} \\right]} _0^1\\) \\(= \\frac{1}{{\\sin \\alpha }}\\left( {{{\\tan }^{ - 1}}\\cot \\frac{\\alpha }{2} -{{\\tan }^{ - 1}}\\cot \\alpha } \\right) = \\frac{\\alpha }{2}.\\frac{1}{{\\sin \\alpha}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the integral \\(\\int_{ - \\pi }^\\pi  {\\sin mx\\sin nx\\,dx} \\) for \\(m\\ne n\\) \\((m,\\,\\,n \\in I),\\) is','(a)0','(b)\\(\\pi \\)','(c)\\(\\frac{\\pi }{2}\\)','(d)\\(2\\pi \\)   ','1','DEFINITE INTEGRAL','2','Answer =  (a)Let \\(I = 2\\int_0^\\pi  {\\sin mx\\sin nx\\,dx}  = \\int_0^\\pi  {[\\cos (m - n)x -\\cos (m + n)x]dx} \\)=\\(\\left[ {\\frac{{\\sin (m - n)x}}{{(m - n)}} - \\frac{{\\sin (m +n)x}}{{(m + n)}}} \\right]_0^\\pi \\)Since,\\(= \\left[ {\\frac{{\\sin (m - n)\\pi }}{{(m - n)}} - \\frac{{\\sin (m + n)\\pi }}{{(m+ n)}}} \\right] = 0.\\)\\(\\sin (m - n)\\pi  = 0 = \\sin (m + n)\\pi \\) for \\(m \\ne n\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The greater of \\(\\int_0^{\\pi /2} {\\frac{{\\sin x}}{x}\\,dx} \\) and \\(\\frac{\\pi}{2},\\) is','(a)\\(\\frac{\\pi }{2}\\)','(b)\\(\\int_0^{\\pi /2} {\\frac{{\\sin x}}{x}\\,dx} \\)','(c)Nothing can be said','(d)None of these    ','1','DEFINITE INTEGRAL','2','Answer =  (a)Since \\(\\sin x < x\\) for \\(0 < x \\le \\pi /2\\)So, \\(\\int_0^{^{\\pi /2}} {\\frac{{\\sin x}}{x}dx < \\int_0^{\\pi /2} {1dx =\\frac{\\pi }{2}} } \\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The integral \\(\\int_{ - 1}^3 {\\left( {{{\\tan }^{ - 1}}\\frac{x}{{{x^2} + 1}} +{{\\tan }^{ - 1}}\\frac{{{x^2} + 1}}{x}} \\right)} \\,dx = \\)','(a)\\(\\pi \\)','(b)\\(2\\pi \\)','(c)\\(3\\pi \\)','(d)None of these   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_{ - 1}^3 {\\left\\{ {{{\\tan }^{ - 1}}\\left( {\\frac{x}{{{x^2} + 1}}}\\right) + {{\\tan }^{ - 1}}\\left( {\\frac{{{x^2} + 1}}{x}} \\right)} \\right\\}} dx\\)\\(= \\int_{ - 1}^3 {\\left\\{ {{{\\tan }^{ - 1}}\\left( {\\frac{x}{{{x^2} + 1}}}\\right) + {{\\cot }^{ - 1}}\\left( {\\frac{x}{{{x^2} + 1}}} \\right)} \\right\\}} dx\\)    \\(= \\int_{ - 1}^3 {\\frac{\\pi }{2}dx = } 2\\pi .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\({I_1} = \\int_e^{{e^2}} {\\frac{{dx}}{{\\log x}}} \\) and \\({I_2} = \\int_1^2{\\frac{{{e^x}}}{x}\\,dx,} \\) then','(a)\\({I_1} = {I_2}\\)','(b)\\({I_1} > {I_2}\\)','(c)\\({I_1} < {I_2}\\)','(d)None of these  ','1','DEFINITE INTEGRAL','2','Answer =  (a)Put \\(\\log x = u\\)in \\({I_1},\\)so that \\(dx = x\\,du = {e^u}du\\)Also as \\(x = e\\)to \\({e^2},u = 1\\)to 2Thus, \\({I_1} = \\int_1^2 {\\frac{{{e^u}}}{u}du = \\int_1^2 {\\frac{{{e^x}}}{x}dx} }\\). Hence, \\({I_1} = {I_2}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - \\pi /4}^{\\pi /2} {{e^{ - x}}\\sin x\\,dx}  = \\)','(a)\\( - \\frac{1}{2}{e^{ - \\pi /2}}\\)','(b)\\( - \\frac{{\\sqrt 2 }}{2}{e^{ - \\pi/4}}\\)','(c) \\(   - \\sqrt 2 ({e^{ - \\pi /4}} + {e^{ - \\pi /4}})\\)','(d)0  ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_{ - \\pi /4}^{\\pi /4} {{e^{ - x}}\\sin x\\,dx}  = \\left[ {\\frac{{{e^{ -x}}}}{2}( - \\sin x - \\cos x)} \\right]_{ - \\pi /4}^{\\pi /2}\\)\\(= \\frac{1}{2}[{e^{ - x}}( - \\sin x - \\cos x)]_{ - \\pi /4}^{\\pi /2}\\)  \\(= \\frac{1}{2}\\left[ {{e^{ - \\pi /2}}( - 1 - 0) - \\left\\{ {{e^{\\pi /4}}\\left({\\frac{1}{{\\sqrt 2 }} - \\frac{1}{{\\sqrt 2 }}} \\right)} \\right\\}} \\right] =  -\\frac{{{e^{ - \\pi /2}}}}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{1 + 2\\cos x}}{{{{(2 + \\cos x)}^2}}} = } \\)','(a)\\(\\frac{\\pi }{2}\\)','(b)\\(\\pi \\)','(c)\\(\\frac{1}{2}\\)','(d)None of these    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_0^{\\pi /2} {\\frac{{(1 + 2\\cos x)}}{{{{(2 + \\cos x)}^2}}}dx =\\int_0^{\\pi /2} {\\frac{{2(\\cos x + 2) - 3}}{{{{(2 + \\cos x)}^2}}}dx} } \\)\\(= 2\\int_0^{\\pi /2} {\\frac{{dx}}{{2 + \\cos x}} - 3\\int_0^{\\pi /2}{\\frac{{dx}}{{{{(2 + \\cos x)}^2}}}} }\\)\\( = 4\\int_0^1 {\\frac{{dt}}{{3 + {t^2}}} - 6\\int_0^1 {\\frac{{1 + {t^2}}}{{{{(3 +{t^2})}^2}}}dt} } \\)\\(=  - 2\\int_0^1 {\\frac{{dt}}{{3 + {t^2}}} + 12\\int_0^1 {\\frac{{dt}}{{{{(3 +{t^2})}^2}}}} }\\)\\(\\left[ {{\\rm{Put}}\\,\\,\\tan \\frac{x}{2} = t} \\right],\\)\\(=  - 2\\int_0^1 {\\frac{{dt}}{{3 + {t^2}}} + 12} \\left[{\\frac{1}{6}.\\frac{t}{{{t^2} + 3}}} \\right]_0^1 + \\frac{1}{6}\\int_0^1{\\frac{{dt}}{{3 + {t^2}}}}\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {\\frac{{dx}}{{1 - 2a\\cos x + {a^2}}}} \\,\\)=','(a)\\(\\frac{\\pi }{{2(1 - {a^2})}}\\)','(b)\\(\\pi (1 - {a^2})\\)','(c)\\(\\frac{\\pi }{{1 - {a^2}}}\\)','(d)None of these ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_0^\\pi  {\\frac{{dx}}{{(1 + {a^2})\\left( {{{\\cos }^2}\\frac{x}{2} +{{\\sin }^2}\\frac{x}{2}} \\right) - 2a\\left( {{{\\cos }^2}\\frac{x}{2} - {{\\sin}^2}\\frac{x}{2}} \\right)}}} \\)\\(= 2\\left[ {\\frac{t}{{{t^2} + 3}}} \\right]_0^1 = \\frac{1}{2}.\\)\\(= \\int_0^\\pi  {\\frac{{dx}}{{{{(1 - a)}^2}{{\\cos }^2}\\frac{x}{2} + {{(1 +a)}^2}{{\\sin }^2}\\frac{x}{2}}}}\\)\\( = \\frac{2}{{{{(1 + a)}^2}}}\\int_0^\\infty  {\\frac{{dt}}{{{{\\left\\{ {(1 - a)/(1+ a)} \\right\\}}^2} + {t^2}}}} \\); {where \\(t = \\tan \\frac{x}{2}\\)}\\(= \\frac{2}{{{{(1 + a)}^2}}}\\frac{{(1 + a)}}{{(1 - a)}}\\left[ {{{\\tan }^{ -1}}\\left( {\\frac{{1 + a}}{{1 - a}}.t} \\right)} \\right]_0^\\infty\\) \\(= \\frac{2}{{(1 - {a^2})}}[{\\tan ^{ - 1}}\\infty  - {\\tan ^{ - 1}}0] = \\frac{\\pi}{{1 - {a^2}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^1 {{{(1 - x)}^9}dx = } \\)','(a)1','(b)\\(\\frac{1}{{10}}\\)','(c) \\(  \\frac{{11}}{{10}}\\)','(d) 2   ','2','DEFINITE INTEGRAL','2','Answer =  (b)Required value =\\(\\left[ {\\frac{{ - {{(1 - x)}^{10}}}}{{10}}} \\right]_0^1 =\\frac{1}{{10}}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /3} {\\cos 3x\\,dx = } \\)','(a) \\(\\pi\\)','(b) 0','(c)\\(\\frac{\\pi }{2}\\)','(d)\\(\\frac{\\pi }{4}\\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b)Required value is \\(\\left[ {\\frac{{\\sin 3x}}{3}} \\right]_0^{\\pi /3} = 0\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^{\\pi /4} {\\frac{{1 + \\tan x}}{{1 - \\tan x}}\\,dx} \\)is','(a)\\( - \\frac{1}{2}\\log 2\\)','(b)\\(\\frac{1}{4}\\log 2\\)','(c)\\(\\frac{1}{3}\\log 2\\)','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_0^{\\pi /4} {\\frac{{1 + \\tan x}}{{1 - \\tan x}}dx = \\int_0^{\\pi /4}{\\tan \\left( {\\frac{\\pi }{4} + x} \\right)\\,dx} } \\)  \\(= \\left[ {\\log \\left\\{ {\\sec \\left( {\\frac{\\pi }{4} + x} \\right)} \\right\\}}\\right]_0^{ - \\pi /4} =  - \\frac{1}{2}\\log 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^1 {\\frac{{dx}}{{{e^x} + {e^{ - x}}}}} \\) is','(a)\\({\\tan ^{ - 1}}\\left( {\\frac{{1 - e}}{{1 + e}}} \\right)\\)','(b)\\({\\tan ^{ -1}}\\left( {\\frac{{e - 1}}{{e + 1}}} \\right)\\)','(c)\\(\\frac{\\pi }{4}\\)','(d)\\({\\tan ^{ - 1}}e + \\frac{\\pi }{4}\\)    ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^1 {\\frac{{dx}}{{{e^x} + {e^{ - x}}}} = \\int_0^1 {\\frac{{{e^x}}}{{1 +{e^{2x}}}}dx} } \\)Now put \\({e^x} = t \\Rightarrow {e^x}dx = dt\\)Also as \\(x = 0\\)to 1, \\(t = 1\\)to e, then reduced form is    \\(\\int_1^e {\\frac{{dt}}{{1 + {t^2}}} = [{{\\tan }^{ - 1}}t]_1^e}  = {\\tan ^{ -1}}\\left( {\\frac{{e - 1}}{{e + 1}}} \\right),\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_1^e {\\frac{{1 + \\log x}}{x}\\,dx = } \\)','(a)\\(\\frac{3}{2}\\)','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{e}\\)','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_1^e {\\frac{{1 + \\log x}}{x}\\,} dx = \\int_1^e {\\frac{1}{x}dx +\\int_1^e {\\frac{{\\log x}}{x}dx} } \\)\\(\\Rightarrow{}\\) \\([{\\log _e}x]_1^e + \\left[ {\\frac{{{{(\\log x)}^2}}}{2}}\\right]_1^e = \\frac{3}{2}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\int_0^1 {x\\log \\left( {1 + \\frac{x}{2}} \\right)} \\,dx = a + b\\log\\frac{2}{3},\\) then','(a)\\(a = \\frac{3}{2},\\,\\,\\,b = \\frac{3}{2}\\)','(b)\\(a = \\frac{3}{4},\\,\\,\\,b =  -\\frac{3}{4}\\)','(c)\\(a = \\frac{3}{4},\\,\\,\\,b = \\frac{3}{2}\\)','(d)\\(a = b\\)  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\left[ {{{\\tan }^{ - 1}}x - {{\\tan }^{ - 1}}y = {{\\tan }^{ - 1}}\\left( {\\frac{{x- y}}{{1 + xy}}} \\right)} \\right].\\)Integrate it by parts taking \\(\\log \\left( {1 + \\frac{x}{2}} \\right)\\)as firstfunction \\( = \\left[ {\\log \\left( {1 + \\frac{x}{2}} \\right)\\frac{{{x^2}}}{2}}\\right]_0^2 - \\int_0^1 {\\frac{1}{{1 + \\frac{x}{2}}}\\frac{1}{2}\\frac{{{x^2}}}{2}}dx\\)\\(= \\frac{1}{2}\\log \\frac{3}{2} - \\frac{1}{2}\\int_0^1 {\\frac{{{x^2}}}{{x + 2}}dx}\\)\\(= \\frac{1}{2}\\log \\frac{3}{2} - \\frac{1}{2}\\left[ {\\frac{1}{2} - 2 + 4\\log 3 -4\\log 2} \\right] = \\frac{3}{4} + \\frac{3}{2}\\log \\frac{2}{3}\\)On comparing with the given value \\(a = \\frac{3}{4},b = \\frac{3}{2}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^1 {\\frac{{dx}}{{\\sqrt {1 + x}  - \\sqrt x }} = } \\)','(a)\\(\\frac{{2\\sqrt 2 }}{3}\\)','(b)\\(\\frac{{4\\sqrt 2 }}{3}\\)','(c)\\(\\frac{{8\\sqrt 2 }}{3}\\)','(d)None of these  ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_0^1 {\\frac{{dx}}{{\\sqrt {1 + x}  - \\sqrt x }} = \\int_0^1{\\frac{{(\\sqrt {1 + x}  + \\sqrt x )dx}}{{(\\sqrt {1 + x}  - \\sqrt x )(\\sqrt {1 +x}  + \\sqrt x )}}} } \\) \\(= \\int_0^1 {\\frac{{(\\sqrt {1 + x}  + \\sqrt x )}}{{1 + x - x}}} dx = \\int_0^1{\\sqrt {1 + x\\,} dx}  + \\int_0^1 {\\sqrt x } dx = \\frac{{4\\sqrt 2 }}{3}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {\\frac{{4\\sin 2\\theta \\,d\\theta }}{{{{\\sin }^4}\\theta  +{{\\cos }^4}\\theta }}}  = \\)','(a)\\(\\pi /4\\)','(b)\\(\\pi /2\\)','(c)\\(\\pi \\)','(d)None of these   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(4\\int_0^{\\pi /4} {\\frac{{\\sin 2\\theta \\,d\\theta }}{{{{\\sin }^4}\\theta  +{{\\cos }^4}\\theta }} = 4\\int_0^{\\pi /4} {\\frac{{2\\sin \\theta \\cos \\theta\\,d\\theta }}{{{{\\sin }^4}\\theta  + {{\\cos }^4}\\theta }}} } \\)\\(= 4\\int_0^{\\pi /4} {\\frac{{2\\tan \\theta {{\\sec }^2}\\theta \\,d\\theta }}{{{{\\tan}^4}\\theta  + 1}}}\\){Dividing numerator and denominator by \\({\\cos ^4}\\theta \\)}Now put \\({\\tan ^2}\\theta  = t \\Rightarrow 2\\tan \\theta {\\sec ^2}\\theta\\,d\\theta  = dt\\), then the reduced form is \\(4\\int_0^1 {\\frac{{dt}}{{{t^2} +1}}}  = 4[{\\tan ^{ - 1}}t]_0^1 = 4\\left[ {\\frac{1}{4}\\pi  - 0} \\right] = \\pi \\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^1 {\\frac{{{e^x}(x - 1)}}{{{{(x + 1)}^3}}}\\,dx = } \\)','(a)\\(\\frac{e}{4}\\)','(b)\\(\\frac{e}{4} - 1\\)','(c)\\(\\frac{e}{4} + 1\\)','(d)None of these  ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^1 {\\frac{{{e^x}(x - 1)}}{{{{(x + 1)}^3}}}} dx = \\int_0^1{\\frac{{{e^x}(x + 1 - 2)}}{{{{(x + 1)}^3}}}\\,} dx\\) \\(\\int_0^1 {\\frac{{{e^x}}}{{{{(x + 1)}^2}}}} dx - 2\\int_0^1 {\\frac{{{e^x}}}{{{{(x+ 1)}^3}}}} dx = \\left[ {\\frac{{{e^x}}}{{{{(x + 1)}^2}}}} \\right]_0^1 =\\frac{e}{4} - 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(x({x^4} + 1)\\varphi (x) = 1,\\) then \\(\\int_1^2 {\\varphi (x)\\,dx = } \\)','(a)\\(\\frac{1}{4}\\log \\frac{{32}}{{17}}\\)','(b)\\(\\frac{1}{2}\\log \\frac{{32}}{{17}}\\)','(c)\\(\\frac{1}{4}\\log \\frac{{16}}{{17}}\\)','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a)Here \\(\\varphi (x) = \\frac{1}{{x({x^4} + 1)}} = \\frac{1}{x} -\\frac{{{x^3}}}{{{x^4} + 1}}\\)\\(\\Rightarrow{}\\) \\(\\int_1^2 {\\varphi (x)dx = \\int_1^2 {\\,\\left( {\\frac{1}{x} -\\frac{{{x^3}}}{{{x^4} + 1}}} \\right)} \\,dx} \\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{1/4}^{1/2} {\\frac{{dx}}{{\\sqrt {x - {x^2}} }} = } \\)','(a)\\(\\pi \\)','(b)\\(\\frac{\\pi }{2}\\)','(c)\\(\\frac{\\pi }{3}\\)','(d)\\(\\frac{\\pi }{6}\\) ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_{1/4}^{1/2} {\\frac{{dx}}{{\\sqrt {x - {x^2}} }} = \\int_{1/4}^{1/2}{\\frac{{dx}}{{\\sqrt {{{\\left( {\\frac{1}{2}} \\right)}^2} - {{\\left( {x -\\frac{1}{2}} \\right)}^2}} }}} }  = \\left[ {{{\\sin }^{ - 1}}\\left({\\frac{{\\frac{{2x - 1}}{2}}}{{1/2}}} \\right)} \\right]_{1/4}^{1/2}\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^2 {\\frac{{{3^{\\sqrt x }}}}{{\\sqrt x }}} \\,dx\\) is','(a) \\(\\frac{2}{{\\log 3}}.({3^{\\sqrt 2 }} - 1)\\)','(b) 0','(c)\\(2.\\frac{{\\sqrt 2 }}{{\\log 3}}\\)','(d)\\(\\frac{{{3^{\\sqrt 2 }}}}{{\\sqrt 2 }}\\)    ','1','DEFINITE INTEGRAL','2','Answer =  (a)Put \\(\\sqrt x  = t\\)or \\(\\frac{1}{{\\sqrt x }}dx = 2\\)dt\\(= |\\log x|_1^2 - \\left| {\\frac{1}{4}\\log ({x^2} + 1)} \\right|_1^2 =\\frac{1}{4}\\log \\frac{{32}}{{17}}.\\)Also,\\(= [{\\sin ^{ - 1}}(2x - 1)]_{1/6}^{1/2} = \\pi /6.\\)as \\(x = 0\\)to 2 so, \\(t = 0\\)to \\(\\sqrt 2 \\)Therefore, \\(\\int_0^2 {\\frac{{{3^{\\sqrt x }}}}{{\\sqrt x }}\\,} dx =2\\int_0^{\\sqrt 2 } {{3^t}} dt = 2\\left[ {\\frac{{{3^t}}}{{\\log 3}}}\\right]_0^{\\sqrt 2 }\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{2\\pi } {\\,\\,(\\sin x + \\cos x)\\,dx = } \\)','(a)0','(b)2','(c) - 2','(d) 1    ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_0^{2\\pi } {(\\sin x + \\cos x)dx = [ - \\cos x + \\sin x]_0^{2\\pi } = 0}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {\\frac{{\\sec x}}{{1 + 2{{\\sin }^2}x}}} \\) is equal to','(a)\\(\\frac{1}{3}\\left[ {\\log (\\sqrt 2  + 1) + \\frac{\\pi }{{2\\sqrt 2 }}}\\right]\\)','(b)\\(\\frac{1}{3}\\left[ {\\log (\\sqrt 2  + 1) - \\frac{\\pi }{{2\\sqrt 2 }}}\\right]\\)','(c)\\(3\\left[ {\\log (\\sqrt 2  + 1) - \\frac{\\pi }{{2\\sqrt 2 }}}\\right]\\)','(d)\\(3\\left[ {\\log (\\sqrt 2  + 1) + \\frac{\\pi }{{2\\sqrt 2 }}} \\right]\\)  ','1','DEFINITE INTEGRAL','2','Answer =  (a) Let \\(I = \\int_0^{\\pi /4} {\\frac{{\\cos x}}{{{{\\cos }^2}x(1 + 2{{\\sin}^2}x)}}} {\\rm{ }}dx\\)\\(= \\frac{2}{{\\log 3}}({3^{\\sqrt 2 }} - 1).\\)\\(= \\int_0^{\\pi /4} {\\frac{{\\cos x\\,dx}}{{(1 - {{\\sin }^2}x)(1 + 2{{\\sin}^2}x)}}}\\)\\(= \\frac{1}{3}\\int_0^{1/\\sqrt 2 } {\\left( {\\frac{1}{{1 - {t^2}}} + \\frac{2}{{1 +2{t^2}}}} \\right)} \\,dt\\)By partial fractions, where \\(t = \\sin x\\)\\(= \\frac{1}{3}\\left[ {\\frac{1}{{2.1}}\\log \\frac{{1 + t}}{{1 - t}} +\\frac{2}{{\\sqrt 2 }}{{\\tan }^{ - 1}}t\\sqrt 2 } \\right]_0^{1/\\sqrt 2 }\\)\\(= \\frac{1}{3}\\left[ {\\frac{1}{2}\\log \\frac{{(\\sqrt 2  + 1)}}{{(\\sqrt 2  - 1)}}+ \\sqrt 2 {{\\tan }^{ - 1}}1} \\right]\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^{\\pi /2} {\\frac{{\\sin x}}{{1 + {{\\cos }^2}x}}\\,dx} \\)is   ','(a)\\(\\pi /2\\)','(b)\\(\\pi /4\\)','(c)\\(\\pi /3\\)','(d)\\(\\pi /6\\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_0^{\\pi /2} {\\frac{{\\sin x}}{{1 + {{\\cos }^2}x}}} dx\\)\\( = \\frac{1}{3}\\left[ {\\frac{1}{2}\\log {{(\\sqrt 2  + 1)}^2} + \\sqrt 2.\\frac{\\pi }{4}} \\right]\\)\\(= \\frac{1}{3}\\left[ {\\log (\\sqrt 2  + 1) + \\frac{\\pi }{{2\\sqrt 2 }}} \\right].\\)Put \\(\\cos x = t\\)\\(\\Rightarrow{}\\) \\( - \\sin x\\,dx = dt\\)Then \\(I = \\int_1^0 {\\frac{{ - dt}}{{1 + {t^2}}} = \\int_0^1 {\\frac{{dt}}{{1 +{t^2}}} = [{{\\tan }^{ - 1}}t]_0^1 = \\frac{\\pi }{4}} } \\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_1^2 {\\log x\\,dx} \\) is ','(a)\\(\\log 2/e\\)','(b)\\(\\log 4\\)','(c)\\(\\log 4/e\\)','(d)\\(\\log 2\\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_1^2 {\\log xdx = [x\\log x - x]_1^2 = 2\\log 2 - 2 + 1} \\)        ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_3^5 {\\frac{{{x^2}}}{{{x^2} - 4}}\\,dx} \\) is','(a)\\(2 - {\\log _e}\\left( {\\frac{{15}}{7}} \\right)\\)','(b)\\(2 + {\\log _e}\\left( {\\frac{{15}}{7}} \\right)\\)','(c)\\(2 + 4{\\log _e}3 - 4{\\log _e}7 + 4{\\log _e}5\\)','(d)\\(2 - {\\tan ^{ - 1}}\\left( {\\frac{{15}}{7}} \\right)\\)    ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_3^5 {\\left( {1 + \\frac{4}{{{x^2} - 4}}} \\right)} \\,dx.\\)Nowproceed yo\\(= \\log 4 - 1 = \\log 4 - \\log e = \\log \\frac{4}{e}.\\)urself. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {\\frac{{dx}}{{{{\\cos }^4}x - {{\\cos }^2}x{{\\sin }^2}x + {{\\sin}^4}x}} = }\\)','(a)\\(\\frac{\\pi }{2}\\)','(b)\\(\\frac{\\pi }{4}\\)','(c)\\(\\frac{\\pi }{3}\\)','(d)None of these  ','1','DEFINITE INTEGRAL','2','Answer =  (a)Divide \\({N^r}\\) and \\({D^r}\\)by \\({\\cos ^4}x\\)\\(\\therefore{}\\) \\(I = \\int_0^{\\pi /4} {\\frac{{{{\\sec }^2}x{{\\sec }^2}x\\,dx}}{{1- {{\\tan }^2}x + {{\\tan }^4}x}}} \\)Put \\(\\tan x = t\\)and \\({\\sec ^2}xdx = dt\\)and adjust the limits, we get  \\(I =\\int_0^1 {\\frac{{(1 + {t^2})}}{{{t^4} - {t^2} + 1}}} \\,dt\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^{{{\\sin }^2}x} {{{\\sin }^{ - 1}}\\sqrt t \\,dt +\\int_0^{{{\\cos }^2}x} {{{\\cos }^{ - 1}}\\sqrt t \\,dt} } \\) is','(a)\\(\\frac{\\pi }{2}\\)','(b) 1','(c)\\(\\frac{\\pi }{4}\\)','(d)None of these    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(= \\left[ {{{\\tan }^{ - 1}}\\frac{{{t^2} - 1}}{t}} \\right]_0^1 = {\\tan ^{ -1}}(0) - {\\tan ^{ - 1}}( - \\infty ) = \\frac{\\pi }{2}.\\)We have \\(I = \\int_0^{{{\\sin }^2}x} {{{\\sin }^{ - 1}}\\sqrt t \\,dt +\\int_0^{{{\\cos }^2}x} {{{\\cos }^{ - 1}}\\sqrt t } \\,dt} \\)Putting \\(t = {\\sin ^2}u\\)in the first integral and \\(t = {\\cos ^2}v\\)in thesecond integral, we have\\(I = \\int_0^x {u\\sin \\,2u\\,du\\, - \\int_{\\pi /2}^x {v\\sin 2v\\,dv} }\\)\\(= \\int_0^{\\pi /2} {u\\sin 2udu + \\int_{\\pi /2}^x {u\\sin 2u\\,du - \\int_{\\pi /2}^x{v\\sin 2v\\,dv} } }\\)\\(I = \\int_0^{\\pi /2} {u\\sin 2udu = \\left( {\\frac{{ - u\\cos 2u}}{2}} \\right)}_0^{\\pi /2} + \\frac{1}{2}\\int_0^{\\pi /2} {\\cos 2u\\,du}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If for non-zero \\(x,\\) \\(af(x) + bf\\left( {\\frac{1}{x}} \\right) = \\frac{1}{x} -5,\\) where \\(a \\ne b,\\) then \\(\\int_1^2 {f(x)\\,dx = } \\)','(a)\\(\\frac{1}{{({a^2} + {b^2})}}\\left[ {a\\log 2 - 5a + \\frac{7}{2}b} \\right]\\)','(b)\\(\\frac{1}{{({a^2} - {b^2})}}\\left[ {a\\log 2 - 5a + \\frac{7}{2}b} \\right]\\)','(c)\\(\\frac{1}{{({a^2} - {b^2})}}\\left[ {a\\log 2 - 5a - \\frac{7}{2}b} \\right]\\)','(d)\\(\\frac{1}{{({a^2} + {b^2})}}\\left[ {a\\log 2 - 5a - \\frac{7}{2}b} \\right]\\) ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(= \\left( {\\frac{{ - u\\cos 2u}}{2}} \\right)_0^{\\pi /2} + \\frac{1}{4}(\\sin2u)_0^{\\pi /2} = \\frac{\\pi }{4}.\\)\\(af(x) + bf\\left( {\\frac{1}{x}} \\right) = \\frac{1}{x} - 5\\) (for each \\(x \\ne0\\))\\(\\ldots{} \\)..(i)Replacing x by \\(\\frac{1}{x}\\)in (i), we get \\(af\\left( {\\frac{1}{x}} \\right) +bf(x) = x - 5\\)\\(\\ldots{} \\)..(ii)Eliminating \\(f\\left( {\\frac{1}{x}} \\right)\\)from (i) and (ii), we get\\(({a^2} - {b^2})f(x) = \\frac{a}{x} - bx - 5a + 5b\\)\\(\\Rightarrow{}\\) \\(({a^2} - {b^2})\\int_1^2 {f(x)} dx = \\left[ {\\left( {a\\log|x| - \\frac{b}{2}{x^2} - 5(a - b)x} \\right)} \\right]_1^2\\)\\(= a\\log 2 - 2b - 10(a - b) - a\\log 1 + \\frac{b}{2} + 5(a - b)\\)\\(= a\\log 2 - 5a + \\frac{7}{2}b\\)\\(\\Rightarrow{}\\) \\(\\int_1^2 {f(x)dx = \\frac{1}{{{a^2} - {b^2}}}\\left[ {a\\log 2- 5a + \\frac{7}{2}b} \\right]} \\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\({I_n} = \\int_0^{\\pi /4} {{{\\tan }^n}\\theta \\,d\\theta ,} \\) then \\({I_8} +{I_6}\\) equals','(a)\\(\\frac{1}{4}\\)','(b)\\(\\frac{1}{5}\\)','(c)\\(\\frac{1}{6}\\)','(d)\\(\\frac{1}{7}\\) ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\({I_n} = \\int_0^{\\pi /4} {({{\\sec }^2}\\theta }  - 1){\\tan ^{n - 2}}\\theta\\,d\\theta \\)\\({I_n} = \\int_0^{\\pi /4} {{{\\sec }^2}\\theta {{\\tan }^{n - 2}}\\theta \\,d\\theta } - \\int_0^{\\pi /2} {{{\\tan }^{n - 2}}\\theta } \\,d\\theta\\)\\({I_n} = \\left[ {\\frac{{{{\\tan }^{n - 1}}\\theta }}{{n - 1}}} \\right]_0^{\\pi /4} -{I_{n - 2}} \\Rightarrow {I_n} + {I_{n - 2}} = \\frac{1}{{n - 1}}\\)Hence \\({I_8} + {I_6} = \\frac{1}{{8 - 1}} = \\frac{1}{7}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {} \\sec x\\log (\\sec x + \\tan x)\\,dx =\\)','(a)\\(\\frac{1}{2}{[\\log (1 + \\sqrt 2 )]^2}\\)','(b)\\({[\\log (1 + \\sqrt 2 )]^2}\\)','(c)\\(\\frac{1}{2}{[\\log (\\sqrt 2  - 1)]^2}\\)','(d)\\(\\frac{1}{2}{[\\log (\\sqrt 2  -1)]^2}\\)  ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_0^{\\pi /4} {\\sec x\\log (\\sec x + \\tan x)dx} \\)Put \\(\\log (\\sec x + \\tan x) = t \\Rightarrow \\sec x\\,dx = dt\\) \\(\\Rightarrow I = \\int_0^{\\log (\\sqrt 2  + 1)} {t\\,dt = \\left[{\\frac{{{t^2}}}{2}} \\right]} _0^{\\log (\\sqrt 2  + 1)} = \\frac{{{{[\\log (\\sqrt 2 + 1)]}^2}}}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {\\frac{{{{\\sec }^2}x}}{{(1 + \\tan x)(2 + \\tan x)}}} \\,dx =\\)','(a)\\({\\log _e}\\left( {\\frac{2}{3}} \\right)\\)','(b)\\({\\log _e}3\\)','(c)\\(\\frac{1}{2}{\\log _e}\\left( {\\frac{4}{3}} \\right)\\)','(d)\\({\\log _e}\\left({\\frac{4}{3}} \\right)\\)   ','4','DEFINITE INTEGRAL','2','Answer =  (d)Put \\(1 + \\tan x = t \\Rightarrow {\\sec ^2}x\\,dx = dt\\)\\(\\therefore \\,\\,\\,\\int_0^{\\pi /4} {\\frac{{{{\\sec }^2}x}}{{(1 + \\tan x)(2 + \\tanx)}}dx}\\)\\(= \\int_1^2 {\\frac{{dt}}{{t(1 + t)}}}  = \\int_1^2 {\\frac{{dt}}{t} - \\int_1^2{\\frac{{dt}}{{1 + t}}} }  = [\\log t - \\log (1 + t)]_1^2\\)   \\(= {\\log _e}2 - {\\log _e}3 + {\\log _e}2 = {\\log _e}\\frac{4}{3}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{2/3} {\\frac{{dx}}{{4 + 9{x^2}}} = } \\)','(a)\\(\\frac{\\pi }{{12}}\\)','(b)\\(\\frac{\\pi }{{24}}\\)','(c) \\(   \\frac{\\pi }{4}\\)','(d) 0 ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^{2/3} {\\frac{{dx}}{{4 + 9{x^2}}} = \\frac{1}{9}\\int_0^{2/3}{\\frac{{dx}}{{{{(2/3)}^2} + {x^2}}}} } \\) \\(= \\frac{1}{9} \\times \\frac{1}{{2/3}}\\left( {{{\\tan }^{ - 1}}\\frac{x}{{2/3}}}\\right)_0^{2/3} = \\frac{\\pi }{4} \\times \\frac{1}{6} = \\frac{\\pi }{{24}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^1 {\\frac{{{x^4} + 1}}{{{x^2} + 1}}\\,dx} \\) is','(a)\\(\\frac{1}{6}(3\\pi  - 4)\\)','(b)\\(\\frac{1}{6}(3 - 4\\pi )\\)','(c)\\(\\frac{1}{6}(3\\pi  + 4)\\)','(d)\\(\\frac{1}{6}(3 + 4\\pi )\\)   ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_0^1 {\\frac{{{x^4} + 1}}{{{x^2} + 1}}dx = \\int_0^1 {\\frac{{{x^4} -1}}{{{x^2} + 1}}dx + 2\\int_0^1 {\\frac{{dx}}{{1 + {x^2}}}} } } \\)\\(\\Rightarrow{}\\)  \\(I = \\int_0^1 {({x^2} - 1)} dx + 2\\int_0^1 {\\frac{{dx}}{{1 +{x^2}}}} \\)\\(\\Rightarrow{}\\)  \\(I = \\left[ {\\frac{{{x^3}}}{3} - x} \\right]_0^1 + 2\\,[{\\tan^{ - 1}}x]_0^1\\) \\( =  - \\frac{2}{3} + \\frac{\\pi }{2} = \\frac{{(3\\pi  -4)}}{6}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^a {{x^2}\\sin {x^3}\\,dx} \\) equals ','(a)\\((1 - \\cos {a^3})\\)','(b)\\(3(1 - \\cos {a^3})\\)','(c)\\( - \\frac{1}{3}(1 - \\cos {a^3})\\)','(d)\\(\\frac{1}{3}(1 - \\cos {a^3})\\)   ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_0^a {{x^2}\\sin {x^3}dx} \\); Put \\({x^3} = t \\Rightarrow {x^2}dx =\\frac{{dt}}{3}\\)\\(\\therefore \\,\\,\\,I = \\frac{1}{3}\\int_0^{{a^3}} {\\sin t\\,dt}  =  -\\frac{1}{3}[\\cos t]_0^{{a^3}} =  - \\frac{1}{3}[\\cos {a^3} - 1]\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {','(a)\\(\\sqrt 2 \\pi \\)','(b)\\(\\frac{\\pi }{2}\\)','(c)\\(\\frac{\\pi }{{\\sqrt 2 }}\\)','(d)\\(2\\pi \\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^{\\pi /4} {[\\sqrt {\\tan x}  + \\sqrt {\\cot x]} } dx = \\int_0^{\\pi/4} {\\frac{{\\sin x + \\cos x}}{{\\sqrt {\\sin x\\cos x} }}dx} \\)\\(= \\frac{1}{3}[1 - \\cos {a^3}].\\)\\(= \\sqrt 2 \\int_0^{\\pi /4} {\\frac{{\\sin x + \\cos x}}{{\\sqrt {1 - {{(\\sin x -\\cos x)}^2}} }}dx}\\)Put \\(\\sin x - \\cos x = t\\); \\((\\cos x + \\sin x)dx = dt\\)\\(\\therefore \\,\\,\\,I = \\sqrt 2 \\int_{ - 1}^0 {\\frac{{dt}}{{\\sqrt {1 - {t^2}} }}}\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^1 {\\sqrt {\\frac{{1 - x}}{{1 + x}}} } \\,dx\\) equals          ','(a)\\(\\left( {\\frac{\\pi }{2} - 1} \\right)\\)','(b)\\(\\left( {\\frac{\\pi }{2} + 1}\\right)\\)','(c)\\(\\frac{\\pi }{2}\\)','(d)\\((\\pi  + 1)\\)    ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_0^1 {\\sqrt {\\frac{{1 - x}}{{1 + x}}} dx = \\int_0^1 {\\sqrt{\\frac{{1 - x}}{{1 + x}}} } .\\frac{{\\sqrt {1 - x} }}{{\\sqrt {1 - x} }}dx} \\)\\(I = \\sqrt 2 [{\\sin ^{ - 1}}t]_{ - 1}^0 = \\sqrt 2 [0 - ( - \\pi /2)] = \\frac{\\pi}{{\\sqrt 2 }}.\\)\\(= \\int_0^1 {\\frac{{1 - x}}{{\\sqrt {1 - {x^2}} }}dx = \\int_0^1{\\frac{{dx}}{{\\sqrt {1 - {x^2}} }}}  - \\int_0^1 {\\frac{x}{{\\sqrt {1 - {x^2}}}}\\,} dx}\\)  \\(I = [{\\sin ^{ - 1}}x]_0^1 + [\\sqrt {1 - {x^2}} ]_0^1 = \\frac{\\pi }{2} - 1.\\)',CURRENT_TIMESTAMP);
