INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,1}^{\\,x} {\\frac{{\\log {x^2}}}{x}\\,dx = } \\)','(a)\\({(\\log x)^2}\\)','(b)\\(\\frac{1}{2}{(\\log x)^2}\\)','(c)\\(\\frac{{\\log {x^2}}}{2}\\)','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_1^x {\\frac{{2\\log x}}{x}dx} \\)Let \\(\\log x = t\\) \\(\\Rightarrow{}\\) \\(\\frac{{dx}}{x} = dt\\) \\(\\therefore I = 2\\int_0^{\\log x} {t\\,dt = 2\\,\\left[ {\\frac{{{t^2}}}{2}} \\right]}_0^{\\log x} = {(\\log x)^2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{1}{2}(e - 3)\\)','(a)\\(\\pi ab\\)','(b)\\({\\pi ^2}ab\\)','(c)\\(\\frac{\\pi }{{ab}}\\)','(d)\\(\\frac{\\pi }{{2ab}}\\) ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_0^{\\pi /2} {\\frac{{dx}}{{{a^2}{{\\cos }^2}x + {b^2}{{\\sin }^2}x}}.}\\)Dividing the numerator and denominator by \\({\\cos ^2}x,\\) we get\\(I = \\int_0^{\\pi /2} {\\frac{{\\frac{1}{{{{\\cos }^2}x}}dx}}{{{a^2} +{b^2}\\frac{{{{\\sin }^2}x}}{{{{\\cos }^2}x}}}} = \\int_0^{\\pi /2} {\\frac{{{{\\sec}^2}x}}{{{a^2} + {b^2}{{\\tan }^2}x}}dx} } .\\)Substituting \\(b\\,\\,\\tan x = t\\) and \\(b\\,\\,{\\sec ^2}x\\,dx = dt\\) and limit when\\(x = 0\\), then \\(t = 0\\) and when \\(x = \\frac{\\pi }{2},\\)then \\(t = \\infty ,\\)therefore, \\(I = \\int_0^\\infty  {\\frac{{\\frac{{dt}}{b}}}{{{a^2} + {t^2}}}}  =\\frac{1}{b}\\left[ {\\frac{1}{a}{{\\tan }^{ - 1}}\\left( {\\frac{t}{a}} \\right)}\\right]_0^\\infty \\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\left( {\\int_{\\,0}^{\\,a} {x\\,dx} } \\right) \\le (a + 4),\\) then ','(a)\\(\\frac{{24}}{5}\\pi \\)','(b)\\( - 2 \\le a \\le 4\\)','(c)\\( - 2 \\le a \\le 0\\)','(d)\\(a \\le  - 2\\,\\,{\\rm{or}}\\,\\,a \\ge 4\\)    ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^a {\\,\\,x\\,dx \\le a + 4} \\)\\( \\Rightarrow \\frac{{{a^2}}}{2} \\le a +4\\)\\( \\Rightarrow {a^2} \\le 2a + 8\\)\\(\\Rightarrow {a^2} - 2a - 8 \\le 0\\) \\( \\Rightarrow (a - 4)(a + 2) \\le 0\\)\\(\\Rightarrow  - 2 \\le a \\le 4.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\, - \\,1}^{\\,3} {\\,{{\\tan }^{ - 1}}\\left( {\\frac{x}{{{x^2}+ 1}}} \\right) + {{\\tan }^{ - 1}}\\left( {\\frac{{{x^2} + 1}}{x}} \\right)\\,dx} \\)is','(a)\\(2\\pi \\)','(b)\\(\\pi \\)','(c)\\(\\frac{{21}}{5}\\pi \\)','(d)\\(\\frac{\\pi }{4}\\)    ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_{ - 1}^3 {\\left[ {{{\\tan }^{ - 1}}\\left( {\\frac{x}{{{x^2} + 1}}}\\right) + {{\\cot }^{ - 1}}\\left( {\\frac{x}{{{x^2} + 1}}} \\right)} \\right]\\,dx} \\)\\( = \\int_{ - 1}^3 {\\left( {\\frac{\\pi }{2}} \\right)\\,dx = \\left[ {\\frac{{\\pi\\,x}}{2}} \\right]_{ - 1}^3 = 2\\pi } \\),  \\(\\left( {{{\\tan }^{ - 1}}(x) + {{\\cot}^{ - 1}}(x) = \\frac{\\pi }{2}} \\right)\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\, - \\,1}^{\\,0} {\\frac{{dx}}{{{x^2} + 2x + 2}} = } \\)','(a)0','(b)\\(\\pi /4\\)','(c)\\(\\pi /2\\)','(d)\\( - \\pi /4\\)    ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_{ - 1}^0 {\\frac{{dx}}{{{x^2} + 2x + 2}}} \\)\\( = \\int_{ - 1}^0{\\frac{{dx}}{{{{(x + 1)}^2} + 1}}} \\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\,0}^{\\,1} {\\frac{{{{\\tan }^{ - 1}}x}}{{1 + {x^2}}}dx} \\)is ','(a)\\(\\pi /4\\)','(b)\\({\\pi ^2}/32\\)','(c)1','(d)None of these  ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\( = [{\\tan ^{ - 1}}(x + 1)]_{ - 1}^0\\)\\(= [{\\tan ^{ - 1}}1 - {\\tan ^{ - 1}}0] = \\frac{\\pi }{4}.\\)\\(I = \\int_0^1 {\\frac{{{{\\tan }^{ - 1}}x}}{{1 + {x^2}}}dx} \\);  Put \\({\\tan ^{ -1}}x = t\\) \\(\\Rightarrow{}\\) \\(\\frac{1}{{1 + {x^2}}}dx = dt\\)  \\(\\therefore I = \\int_{\\,0}^{\\,\\pi /4} {t\\,dt} \\)\\( = \\left[{\\frac{{{t^2}}}{2}} \\right]_0^{\\pi /4}\\)\\(= \\frac{{{\\pi ^2}}}{{32}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_1^{\\sqrt 3 } {\\frac{1}{{1 + {x^2}}}dx} \\) is equal to ','(a)\\(\\pi /12\\)','(b)\\(\\pi /6\\)','(c)\\(\\pi /4\\)','(d)\\(\\pi /3\\)    ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_1^{\\sqrt 3 } {\\frac{1}{{1 + {x^2}}}dx = [{{\\tan }^{ -1}}x]_{\\,1}^{\\sqrt 3 } = \\frac{\\pi }{3} - \\frac{\\pi }{4} = \\frac{\\pi }{{12}}} \\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,1}^{\\,3} {(x - 1)(x - 2)(x - 3)dx = } \\)','(a)3','(b)2','(c)1','(d)0    ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_{\\,1}^{\\,3} {(x - 1)(x - 2)(x - 3)dx} \\).\\(I = \\int_{\\,1}^{\\,3} {({x^3} - 6{x^2} + 11x - 6)\\,dx = \\left[ {\\frac{{{x^4}}}{4}- \\frac{{6{x^3}}}{3} + \\frac{{11{x^2}}}{2} - 6x} \\right]_{\\,1}^{\\,3} = 0.}\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,2}^{\\,3} {\\frac{{dx}}{{{x^2} - x}} = } \\)','(a)\\(\\log (2/3)\\)','(b)\\(\\log (1/4)\\)','(c)\\(\\log (4/3)\\)','(d)\\(\\log (8/3)\\)  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_2^3 {\\frac{{dx}}{{{x^2} - x}}} \\)\\( = \\int_2^3 {\\frac{{dx}}{{x\\,(x- 1)}}} \\)\\( = \\int_2^3 {\\left[ {\\frac{1}{{x - 1}} - \\frac{1}{x}} \\right]} \\,dx\\)\\( = \\int_2^3 {\\frac{1}{{(x - 1)}}} \\,dx - \\int_2^3 {\\frac{1}{x}dx} \\)\\(= [\\log (x - 1)]_2^3 - [\\log x]_2^3\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,8}^{\\,15} {\\frac{{dx}}{{(x - 3)\\sqrt {x + 1} }} = } \\)','(a)\\(\\frac{1}{2}\\log \\frac{5}{3}\\)','(b)\\(\\frac{1}{3}\\log \\frac{5}{3}\\)','(c)\\(\\frac{1}{2}\\log \\frac{3}{5}\\)','(d)\\(\\frac{1}{5}\\log \\frac{3}{5}\\)    ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_8^{15} {\\frac{{dx}}{{(x - 3)\\sqrt {x + 1} }}} \\)\\( = [\\log 2 - \\log 1] - [\\log 3 - \\log 2]\\)\\(= 2\\log 2 - \\log 3 = \\log \\frac{4}{3}.\\)Put \\(x = {\\tan ^2}\\theta  \\Rightarrow \\theta  = {\\tan ^{ - 1}}\\sqrt x \\)\\(dx = 2\\tan \\theta {\\sec ^2}\\theta \\,d\\theta\\)\\(\\therefore \\)\\(I = \\int_{{{\\tan }^{ - 1}}\\sqrt 8 }^{{{\\tan }^{ - 1}}\\sqrt {15} } {\\frac{{2\\tan\\theta {{\\sec }^2}\\theta \\,}}{{({{\\tan }^2}\\theta  - 3)\\sqrt {{{\\tan }^2}\\theta + 1} }}d\\theta }\\)\\(= \\int_{{{\\tan }^{ - 1}}\\sqrt 8 }^{{{\\tan }^{ - 1}}\\sqrt {15} } {\\frac{{2\\tan\\theta {{\\sec }^2}\\theta \\,}}{{({{\\sec }^2}\\theta  - 4)\\sec \\theta }}d\\theta }\\)\\(= \\int_{{{\\tan }^{ - 1}}\\sqrt 8 }^{{{\\tan }^{ - 1}}\\sqrt {15} } {\\frac{{2\\tan\\theta \\sec \\theta }}{{({{\\sec }^2}\\theta  - 4)}}\\,d\\theta }\\)\\(= \\int_{{{\\tan }^{ - 1}}\\sqrt 8 }^{{{\\tan }^{ - 1}}\\sqrt {15} } {\\frac{{2\\tan\\theta \\sec \\theta }}{{(\\sec \\theta  - 2)(\\sec \\theta  + 2)}}\\,d\\theta }\\)\\(= \\left[ {\\frac{1}{2}\\log \\frac{{(\\sec \\theta  - 2)}}{{(\\sec \\theta  + 2)}}}\\right]_{{{\\tan }^{ - 1}}\\sqrt 8 }^{{{\\tan }^{ - 1}}\\sqrt {15} }\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\,0}^{\\,\\pi } {\\,\\left| {\\,{{\\sin }^3}\\theta \\,}\\right|\\,d\\theta } \\) is ','(a)0','(b)3/8','(c)4/3','(d)\\(\\pi \\)    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^\\pi  {|{{\\sin }^3}\\theta |} \\,d\\theta \\)S\\(= \\frac{1}{2}\\left[ {\\log \\frac{2}{6} - \\log \\frac{1}{5}} \\right] =\\frac{1}{2}\\log \\frac{5}{3}.\\)ince \\(\\sin \\theta \\) is positive in interval \\((0,\\pi )\\)\\(\\therefore I = \\int_0^\\pi  {{{\\sin }^3}\\theta \\,d\\theta  = \\int_0^\\pi  {\\sin\\theta (1 - {{\\cos }^2}\\theta )\\,\\,d\\theta } }\\)\\(= \\int_0^\\pi  {\\sin \\theta \\,d\\theta  + \\int_0^\\pi  {( - \\sin \\theta )\\,{{\\cos}^2}\\theta \\,d\\theta } }\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,1} {\\,\\sin \\left( {2{{\\tan }^{ - 1}}\\sqrt {\\frac{{1 + x}}{{1 -x}}} } \\right)\\,dx = } \\)','(a)\\(\\pi /6\\)','(b)\\(\\pi /4\\)','(c)\\(\\pi /2\\)','(d)\\(\\pi \\) ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^1 {\\sin \\left( {2{{\\tan }^{ - 1}}\\sqrt {\\frac{{1 + x}}{{1 - x}}} }\\right)\\,dx} \\)Put\\(= [ - \\cos \\theta ]_0^\\pi  + \\left( {\\frac{{{{\\cos }^3}\\theta }}{3}}\\right)_0^\\pi  = \\frac{4}{3}.\\)\\(x = \\cos \\theta ,\\)then \\(\\sin \\left[ {2{{\\tan }^{ - 1}}\\sqrt {\\frac{{1 + \\cos\\theta }}{{1 - \\cos \\theta }}} } \\right]\\)\\(= \\sin \\,\\,\\left[ {2{{\\tan }^{ - 1}}\\left( {\\cot \\frac{\\theta }{2}} \\right)}\\right]\\)\\(= \\sin \\left[ {2{{\\tan }^{ - 1}}\\left[ {\\tan \\left( {\\frac{\\pi }{2} -\\frac{\\theta }{2}} \\right)} \\right]} \\right] = \\sin \\left[ {2\\,\\left( {\\frac{\\pi}{2} - \\frac{\\theta }{2}} \\right)} \\right]\\)\\(= \\sin (\\pi  - \\theta ) = \\sin \\theta  = \\sqrt {1 - {{\\cos }^2}\\theta }  =\\sqrt {1 - {x^2}}\\)Now, \\(\\int_0^1 {\\sin \\left( {2{{\\tan }^{ - 1}}\\sqrt {\\frac{{1 + x}}{{1 - x}}} }\\right)} \\,dx = \\int_0^1 {\\sqrt {1 - {x^2}} \\,dx} \\)\\(= \\left[ {\\frac{1}{2}x\\sqrt {1 - {x^2}} } \\right]_0^1 + \\frac{1}{2}[{\\sin ^{ -1}}x]_0^1 = \\frac{\\pi }{4}.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,3} {\\,\\frac{{3x + 1}}{{{x^2} + 9}}dx = } \\)','(a)\\(\\log (2\\sqrt 2 ) + \\frac{\\pi }{{12}}\\)','(b)\\(\\log (2\\sqrt 2 ) + \\frac{\\pi}{2}\\)','(c)\\(\\log (2\\sqrt 2 ) + \\frac{\\pi }{6}\\)','(d)\\(\\log (2\\sqrt 2 ) + \\frac{\\pi }{3}\\)   ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_0^3 {\\frac{{3x + 1}}{{{x^2} + 9}}dx = \\frac{3}{2}} \\int_0^3{\\frac{{2x}}{{{x^2} + 9}}dx + } \\int_0^3 {\\frac{{dx}}{{{x^2} + 9}}} \\)\\(= \\left[ {\\frac{3}{2}\\log ({x^2} + 9) + \\frac{1}{3}{{\\tan }^{ - 1}}\\left({\\frac{x}{3}} \\right)} \\right]_0^3\\)\\(= \\frac{3}{2}(\\log 18 - \\log 9) + \\frac{1}{3}\\left( {\\frac{\\pi }{4}} \\right)\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_1^2 {\\frac{{dx}}{{x(1 + {x^4})}}} \\)is','(a)\\(\\frac{1}{4}\\log \\frac{{17}}{{32}}\\)','(b)\\(\\frac{1}{4}\\log \\frac{{17}}{2}\\)','(c)\\(\\log \\frac{{17}}{2}\\)','(d)\\(\\frac{1}{4}\\log \\frac{{32}}{{17}}\\)   ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_1^2 {\\frac{{dx}}{{x(1 + {x^4})}} = \\int_1^2 {\\frac{{dx}}{{{x^5}\\left({1 + \\frac{1}{{{x^4}}}} \\right)}}} } \\)\\(= \\frac{3}{2}\\log 2 + \\frac{\\pi }{{12}} = \\log (2\\sqrt 2 ) + \\frac{\\pi }{{12}}.\\)Put \\(\\left( {1 + \\frac{1}{{{x^4}}}} \\right) = z \\Rightarrow \\frac{{ -4}}{{{x^5}}}dx = dz\\)\\(\\Rightarrow{}\\) \\(\\frac{{ - 1}}{4}\\int_2^{17/16} {\\frac{{dz}}{z} = \\left[{\\frac{{ - 1}}{4}\\log z} \\right]_2^{17/16}} \\)=\\(\\frac{1}{4}\\log 2 -\\frac{1}{4}\\log \\frac{{17}}{{16}}\\)\\(\\Rightarrow{}\\) \\(I = \\frac{1}{4}\\log \\left( {\\frac{{32}}{{17}}} \\right)\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of\\(\\int_2^3 {\\frac{{x + 1}}{{{x^2}(x - 1)}}dx} \\)is','(a)\\(2\\log 2 - \\frac{1}{6}\\)','(b)\\(\\log \\frac{{16}}{9} - \\frac{1}{6}\\)','(c)\\(\\log \\frac{4}{3} - \\frac{1}{6}\\)','(d)\\(\\log \\frac{{16}}{9} + \\frac{1}{6}\\) ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_2^3 {\\frac{{x + 1}}{{{x^2}(x - 1)}}} dx = \\int_2^3 {\\left({\\frac{A}{{{x^2}}} + \\frac{B}{x} + \\frac{C}{{x - 1}}} \\right)} \\,dx\\)\\(A(x - 1) + B(x)(x - 1) + C({x^2}) = x + 1\\)Put \\(x = 0,\\,1,\\, - 1,\\)we get \\(A =  - 1,B =  - 2,C = 2\\)\\(\\Rightarrow{}\\)  \\(I =  - \\int_2^3 {\\frac{{dx}}{{{x^2}}} - 2\\int_2^3{\\frac{{dx}}{x} + 2\\int_2^3 {\\frac{{dx}}{{x - 1}}} } } \\)\\(\\Rightarrow{}\\)  \\(I = \\left[ {\\frac{1}{x}} \\right]_2^3 - 2[\\log x]_2^3 +2[\\log (x - 1)]_2^3\\)\\(\\Rightarrow{}\\)  \\(I = \\frac{1}{3} - \\frac{1}{2} - 2\\log \\frac{3}{2} + 2\\log2\\)\\(\\Rightarrow{}\\) \\(I = \\log \\frac{{16}}{9} - \\frac{1}{6}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_1^e {\\log x\\,dx} \\)is','(a) 0','(b) 1','(c)\\(e - 1\\)','(d)\\(e + 1\\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_1^e {\\,\\log x\\,dx} \\)\\(\\Rightarrow{}\\) \\(I = \\int_e^1 {\\,1.\\logx\\,dx} \\)\\(\\Rightarrow{}\\) \\(I = [x\\log x - x]_1^e = (e\\log e - e) - (0 - 1)\\)\\(\\Rightarrow{}\\) \\(I = 1\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(I = \\int_0^{\\pi /2} {\\frac{{{{(\\sin x + \\cos x)}^2}}}{{\\sqrt {1 +\\sin 2x} }}{\\rm{ }}} dx\\)is ','(a)3','(b)1','(c)2','(d)0 ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^{\\pi /2} {\\frac{{{{(\\sin x + \\cos x)}^2}}}{{\\sqrt {1 + \\sin 2x}}}dx} \\)\\( = \\int_0^{\\pi /2} {\\frac{{{{(\\sin x + \\cos x)}^2}}}{{\\sqrt {{{(\\sin x+ \\cos x)}^2}} }}dx} \\)\\(I = \\int_0^{\\pi /2} {(\\sin x + \\cos x)dx = ( - \\cos x + \\sin x)_0^{\\pi /2}}\\)\\(I = 1 - ( - 1) = 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /8} {{{\\cos }^3}4\\theta d\\theta }  = \\)','(a)\\(\\frac{2}{3}\\)','(b)\\(\\frac{1}{4}\\)','(c)\\(\\frac{1}{3}\\)','(d)\\(\\frac{1}{6}\\)   ','4','DEFINITE INTEGRAL','2','Answer =  (d) Let  \\(I = \\int_0^{\\pi /8} {{{\\cos }^3}4\\theta \\,d\\theta  = \\int_0^{\\pi /8}{\\,{{\\cos }^2}4\\theta .\\cos 4\\theta \\,d\\theta } } \\)\\(I = \\int_0^{\\pi /8} {\\,(1 - {{\\sin }^2}4\\theta )\\cos 4\\theta \\,d\\theta }\\)Put \\(\\sin 4\\theta  = t \\Rightarrow \\cos 4\\theta \\,d\\theta  = \\frac{{dt}}{4}\\)When \\(\\theta  = 0 \\to \\frac{\\pi }{8},\\) then \\(t = 0 \\to 1\\)\\(\\therefore{}\\) \\(I = \\frac{1}{4}\\int_0^1 {(1 - {t^2})dt = \\frac{1}{4}} \\left[{t - \\frac{{{t^3}}}{3}} \\right]_0^1 = \\frac{1}{6}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_3^8 {\\frac{{2 - 3x}}{{x\\sqrt {(1 + x)} }}{\\rm{ }}} dx\\)is equal to','(a)\\(2\\log \\,\\left( {3/2{e^3}} \\right)\\)','(b)\\(\\log (3/{e^3})\\)','(c)\\(4\\log (3/{e^3})\\)','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a)We have \\(\\int_3^8 {\\frac{{2 - 3x}}{{x\\sqrt {1 + x} }}dx = I} \\)Put \\(1 + x = {t^2} \\Rightarrow dx = 2t\\,dt\\)When \\(x = 3 \\to 8,\\) then \\(t = 2 \\to 3\\)\\(\\therefore{}\\) \\(I = 2\\int_2^3 {\\frac{{5 - 3{t^2}}}{{{t^2} - 1}}dt} \\); \\(I =2\\int_2^3 {\\left( {\\frac{2}{{{t^2} - 1}} - 3} \\right)} \\,dt\\)\\(I = 2\\left[ {\\frac{2}{{2.1}}\\log \\frac{{t - 1}}{{t + 1}} - 3t}\\right]_2^3\\);\\(I = 2\\log \\left( {\\frac{3}{{2{e^3}}}} \\right)\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^1 {{x^2}{e^x}dx} \\)is equal to ','(a)\\(e - 2\\)','(b)\\(e + 2\\)','(c)\\({e^2} - 2\\)','(d)\\({e^2}\\)   ','1','DEFINITE INTEGRAL','2','Answer =  (a)We have, \\(I = \\int_0^1 {{x^2}{e^x}dx} \\)\\(\\Rightarrow{}\\) \\(I =[{x^2}.{e^x}]_0^1 - \\int_0^1 {2x.{e^x}dx} \\)\\(\\Rightarrow{}\\) \\(I = e - 2[x{e^x} - {e^x}]_0^1\\)\\(\\Rightarrow{}\\)\\(I = e -2\\,[e - e - (0 - 1)]\\)\\(\\Rightarrow{}\\)  \\(I = e - 2\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\({I_1} = \\int_1^2 {\\frac{{dx}}{{\\sqrt {1 + {x^2}} }}} \\)and\\({I_2} =\\int_1^2 {\\frac{{dx}}{x}} \\) then ','(a)\\({I_1} > {I_2}\\)','(b)\\({I_2} > {I_1}\\)','(c)\\({I_1} = {I_2}\\)','(d)\\({I_1} > 2{I_2}\\)  ','2','DEFINITE INTEGRAL','2','Answer =  (b)We have, \\((1 + {x^2}) > {x^2},\\forall x\\); \\(\\sqrt {1 + {x^2}}  > x,\\forall\\,x \\in (1,\\,2)\\)\\(\\Rightarrow{}\\) \\(\\frac{1}{{\\sqrt {1 + {x^2}} }} < \\frac{1}{x},\\,\\,\\forall x\\in (1,\\,2)\\)\\(\\Rightarrow{}\\) \\(\\int_1^2 {\\frac{{dx}}{{\\sqrt {1 + {x^2}} }} <\\int_1^{\\,2} {\\frac{{dx}}{x}} } \\)\\(\\Rightarrow{}\\)  \\({I_1} < {I_2}\\)\\(\\Rightarrow{}\\) \\({I_2} > {I_1}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{1/e}^{\\tan x} {\\frac{{t\\,dt}}{{1 + {t^2}}}}  +\\int_{1/e}^{\\cot x} {\\frac{{dt}}{{t(1 + {t^2})}}}  = \\) [IIT Screening]','(a) - 1','(b) 1','(c)0','(d)None of these    ','2','DEFINITE INTEGRAL','2','Answer =  (b)On integrating both functions, we get\\(= \\frac{1}{2}\\left| {\\log (1 + {t^2})} \\right|_{1/e}^{\\tan x} + \\left| {\\left\\{{\\log t - \\frac{1}{2}\\log (1 + {t^2})} \\right\\}} \\right|_{1/e}^{\\cot x}\\)\\(= \\frac{1}{2}\\left[ {\\log {{\\sec }^2}x - \\log \\left( {1 + \\frac{1}{{{e^2}}}}\\right)} \\right] + \\log \\cot x - \\log \\left( {\\frac{1}{e}} \\right)\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int\\limits_{\\pi /4}^{3\\pi /4} {\\frac{{dx}}{{1 + \\cos x}}} \\) is equal to ','(a)2','(b)\\( - 2\\)','(c)\\(\\frac{1}{2}\\)','(d)\\( - \\frac{1}{2}\\) ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_{\\pi /4}^{3\\pi /4} {\\frac{{dx}}{{1 + \\cos x}}} \\)\\( - \\frac{1}{2}\\left\\{ {\\log ({\\rm{cose}}{{\\rm{c}}^2}x) - \\log \\left( {1 +\\frac{1}{{{e^2}}}} \\right)} \\right\\}\\)\\(=  - \\log \\left( {\\frac{1}{e}} \\right) = \\log e = 1.\\)\\(\\int_{\\pi /4}^{3\\pi /4} {\\frac{{1 - \\cos x}}{{1 - {{\\cos }^2}x}}} \\,dx =\\int_{\\pi /4}^{3\\pi /4} {\\frac{{1 - \\cos x}}{{{{\\sin }^2}x}}} \\,dx\\)\\(= \\int_{\\pi /4}^{3\\pi /4} {({\\rm{cose}}{{\\rm{c}}^2}x}  - \\cotx{\\rm{cosec}}\\,x)\\,dx\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_1^{{e^2}} {\\frac{{dx}}{{x{{(1 + \\ln x)}^2}}}} \\) is','(a)\\(2/3\\)','(b)\\(1/3\\)','(c)\\(3/2\\)','(d)\\(\\ln 2\\)    ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_1^{{e^2}} {\\frac{{dx}}{{x{{(1 + \\ln x)}^2}}}} \\)Let \\((1 + \\ln x) = t\\) \\(\\Rightarrow{}\\)  \\(dt = \\frac{1}{x}dx\\)Now, when \\(x = 1 \\to {e^2}\\), then\\(= \\,( - \\cot x + {\\rm{cosec}}\\,x{\\rm{)}}_{\\pi {\\rm{/4}}}^{{\\rm{3}}\\pi{\\rm{/4}}} = 2.\\)\\(t = 1 \\to 3\\)  \\(\\therefore \\)\\(I = \\int_1^3 {\\frac{{dt}}{{{t^2}}} = \\left[ {\\frac{{ - 1}}{t}} \\right]_1^3 =  -\\left[ {\\frac{1}{3} - 1} \\right]}  = \\frac{2}{3}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\pi /4}^{\\pi /2} {{\\rm{cose}}{{\\rm{c}}^2}xdx = } \\)','(a) - 1','(b) 1','(c)0','(d)\\(\\frac{1}{2}\\)    ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_{\\pi /4}^{\\pi /2} {{\\rm{cose}}{{\\rm{c}}^2}} x\\;dx = \\left[ { - \\cot x}\\right]_{\\pi /4}^{\\pi /2}\\) \\( =  - \\left[ {\\cot \\frac{\\pi }{2} - \\cot \\frac{\\pi}{4}} \\right] = 1\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\int_{\\log 2}^x {\\frac{{du}}{{{{({e^u} - 1)}^{1/2}}}}}  = \\frac{\\pi }{6}\\),then \\({e^x} = \\) ','(a)1','(b)2','(c)4','(d)-1   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_{\\log 2}^x {\\frac{{du}}{{{{({e^u} - 1)}^{1/2}}}}}  = \\frac{\\pi }{6}\\)\\(\\Rightarrow{}\\) \\(\\int_1^{\\sqrt {{e^x} - 1} } {\\frac{{2t}}{{1 + {t^2}}}} \\;dt= \\frac{\\pi }{6}\\)as \\({e^u} - 1 = {t^2}\\)\\(\\Rightarrow{}\\) \\(2({\\tan ^{ - 1}}t)_1^{\\sqrt {{e^x} - 1} } = \\frac{\\pi }{6}\\)\\(\\Rightarrow{}\\) \\({\\tan ^{ - 1}}\\sqrt {{e^x} - 1}  - \\frac{\\pi }{4} = \\frac{\\pi}{{12}}\\)\\(\\Rightarrow{}\\) \\(\\sqrt {{e^x} - 1}  = \\tan \\frac{\\pi }{3}\\) \\(\\Rightarrow{}\\)\\(\\sqrt {{e^x} - 1}  = \\sqrt 3 \\) \\(\\Rightarrow{}\\) \\({e^x} = 4\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(g(1) = g(2)\\), then \\(\\int_1^2 {{{\\left','(a)1','(b)2','(c)0','(d)None of these  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_1^2 {{{[f\\{ g(x)\\} ]}^{ - 1}}f[g(x)]\\,g(x)\\;dx} \\)Let \\(f\\{ g(x)\\}  = z\\) \\(\\Rightarrow{}\\) \\(f\\{ g(x)\\} \\;g(x)\\;dx = dz\\)When \\(x = 1,\\;z = f\\;\\{ g\\;(1)\\} \\)When \\(x = 2,\\;z = f\\;\\{ g\\;(2)\\} \\)\\(\\therefore \\)\\(I = \\int_{f\\{ g(1)\\} }^{f\\{ g(2)\\} } {\\frac{1}{z}dz}  = |\\log z|_{f\\{ g(1)\\}}^{f\\{ g(2)\\} }\\)\\( \\Rightarrow \\)I\\( = \\log f\\;\\{ g(2)\\}  - \\log f\\;\\{ g(1)\\}  = 0\\), (g(2)=g(1)).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  x \\,f\\,(\\sin x)\\,dx = \\)','(a)\\(\\pi \\int_0^\\pi  {f(\\sin x)\\,dx} \\)','(b)\\(\\frac{\\pi }{2}\\int_0^\\pi  {f(\\sinx)\\,dx} \\)','(c)\\(\\frac{\\pi }{2}\\int_0^{\\pi /2} {f(\\sin x)\\,dx} \\)','(d)None of these  ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^\\pi  x f(\\sin x)dx = \\frac{\\pi }{2}\\int_0^\\pi  {f(\\sin x)dx} \\)Since \\(\\int_0^a {xf(x)dx = \\frac{1}{2}a\\int_0^a {f(x)dx,} } \\)if \\(f(a - x) =f(x)\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 4}^4 {|x + 2|\\,dx}  =\\)','(a)50','(b)24','(c)20','(d)None of these  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_{ - 4}^4 {|x + 2|dx = \\int_{ - 4}^{ - 2} { - (x + 2)dx + \\int_{ - 2}^4{\\,(x + 2)dx} } } \\) \\(= \\left| {\\frac{{ - {x^2}}}{2} - 2x} \\right|_{ - 4}^{ - 2} + \\,\\,\\left|{\\frac{{{x^2}}}{2} + 2x} \\right|_{ - 2}^4 = 20.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{\\sqrt {\\cot x} }}{{\\sqrt {\\cot x}  + \\sqrt {\\tan x}}}\\,dx = }\\)','(a)\\(\\pi \\)','(b)\\(\\frac{\\pi }{2}\\)','(c)\\(\\frac{\\pi }{4}\\)','(d)\\(\\frac{\\pi }{3}\\) ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^{\\pi /2} {\\frac{{\\sqrt {\\cot x} }}{{\\sqrt {\\cot x}  + \\sqrt {\\tanx} }}dx} \\).....(i)\\(= \\int_0^{\\pi /2} {\\frac{{\\sqrt {\\cot \\left( {\\frac{\\pi }{2} - x} \\right)}}}{{\\sqrt {\\cot \\left( {\\frac{\\pi }{2} - x} \\right)}  + \\sqrt {\\tan \\left({\\frac{\\pi }{2} - x} \\right)} }}dx}\\)\\( = \\int_0^{\\pi /2} {\\frac{{\\sqrt {\\tan x} }}{{\\sqrt {\\tan x}  + \\sqrt {\\cotx} }}dx} \\).....(ii)Now adding (i) and (ii), we get\\(2I = \\int_0^{\\pi /2} {\\frac{{\\sqrt {\\cot x}  + \\sqrt {\\tan x} }}{{\\sqrt {\\tan x} + \\sqrt {\\cot x} }}dx = [x]_0^{\\pi /2} \\Rightarrow I = \\frac{\\pi }{4}} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{d\\theta }}{{1 + \\tan \\theta }}}  = \\) ','(a)\\(\\pi \\)','(b)\\(\\frac{\\pi }{2}\\)','(c)\\(\\frac{\\pi }{3}\\)','(d)\\(\\frac{\\pi }{4}\\)  ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_0^{\\pi /2} {\\frac{{d\\theta }}{{1 + \\tan \\theta }} = \\int_0^{\\pi /2}{\\frac{{d\\theta }}{{1 + \\tan \\left( {\\frac{\\pi }{2} - \\theta } \\right)}}} } \\)\\(= \\int_0^{\\pi /2} {\\frac{{d\\theta }}{{1 + \\cot \\theta }}} \\)On adding,     \\(2I = \\int_0^{\\pi /2} {\\left( {\\frac{1}{{1 + \\tan \\theta }} +\\frac{1}{{1 + \\cot \\theta }}} \\right)\\,d\\theta } \\)=\\(\\int_0^{\\pi /2} {d\\theta  = [\\theta ]_0^{\\pi /2} = \\frac{\\pi}{2} \\Rightarrow I = \\frac{\\pi }{4}} \\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\int_a^x {{t^3}{e^t}\\,dt\\,,} \\) then \\(\\frac{d}{{dx}}\\,f(x) = \\) ','(a)\\({e^x}({x^3} + 3{x^2})\\)','(b)\\({x^3}{e^x}\\)','(c)\\({a^3}{e^a}\\)','(d)None of these ','2','DEFINITE INTEGRAL','2','Answer =  (b) \\(f(x) = \\int_a^x {{t^3}{e^t}dt = \\int_a^0 {{t^3}.{e^t}dt + \\int_0^x{{t^3}{e^t}\\,\\,dt} } } \\)   \\(\\Rightarrow \\frac{{df(x)}}{{dx}} = \\frac{d}{{dx}}\\left( {\\int_a^0{{t^3}.{e^t}dt} } \\right) + \\frac{d}{{dx}}\\left( {\\int_0^x {{t^3}.{e^t}\\,dt} }\\right) = {x^3}{e^x}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 1}^1 {x\\,|x|\\,} dx = \\)','(a)1','(b)0','(c)2','(d)\\( - 2\\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b) Let \\(f(x) = x|x|\\). Then \\(f( - x) =  - x| - x| =  - x|x| =  - f(x)\\)Therefore \\(\\int_{ - 1}^1 {x|x|dx = 0} \\),(By the property of definite integral). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {x\\log \\sin x} \\,dx =\\)','(a)\\(\\frac{\\pi }{2}\\log \\frac{1}{2}\\)','(b)\\(\\frac{{{\\pi ^2}}}{2}\\log \\frac{1}{2}\\)','(c)\\(\\pi \\log \\frac{1}{2}\\)','(d)\\({\\pi ^2}\\log \\frac{1}{2}\\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_0^\\pi  {x\\log \\sin x\\,dx} \\)\\(\\ldots{} \\)..(i)= \\(\\int_0^\\pi  {(\\pi  - x)\\log \\sin (\\pi  - x)\\,dx} \\)\\(\\ldots{} \\)..(ii)By adding (i) and (ii), we get\\(2I = \\int_0^\\pi  \\pi  \\log \\sin x\\,dx \\Rightarrow I = \\frac{{2\\pi}}{2}\\int_0^{\\pi /2} {\\log \\sin \\,x\\,dx}\\)   ',CURRENT_TIMESTAMP);
