INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {} \\log \\sin x\\,dx = \\)        [MP PET ; RPET , 96, 97]','(a)\\( - \\left( {\\frac{\\pi }{2}} \\right)\\log 2\\)','(b)\\(\\pi \\log \\frac{1}{2}\\)','(c)\\( - \\pi \\log \\frac{1}{2}\\)','(d)\\(\\frac{\\pi }{2}\\log 2\\)  ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_0^{\\pi /2} {\\log \\sin x\\,dx = \\int_0^{\\pi /2} {\\,\\,\\log \\cos x\\,dx} }\\)\\(\\Rightarrow{}\\) \\(2I = \\int_0^{\\pi /2} {\\log \\sin x\\cos x\\,dx}  = \\int_0^{\\pi/2} {\\log \\sin 2x\\,dx}  - \\int_0^{\\pi /2} {\\,\\,\\log 2dx} \\)\\( = \\frac{1}{2}\\int_0^\\pi  {\\log \\sin tdt - \\frac{\\pi }{2}\\log2} \\),     (Putting \\(2x = t\\))\\(= \\frac{1}{2}.2\\int_0^{\\pi /2} {\\log \\sin t\\,dt - \\frac{\\pi }{2}\\log 2}\\)\\( \\Rightarrow 2I = I - \\frac{\\pi }{2}\\log 2 \\Rightarrow I = \\frac{{ - \\pi}}{2}\\log 2\\),\\(\\left\\{ {\\int_a^b {f(x)dx = \\int_a^b {f(t)dt} } } \\right\\}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{\\cos x - \\sin x}}{{1 + \\sin x\\cos x}}} \\,dx =\\)','(a)2','(b)\\( - 2\\)','(c)0','(d)None of these   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_0^{\\pi /2} {\\frac{{\\cos x - \\sin x}}{{1 + \\sin x\\cos x}}dx = I}\\)\\(\\ldots{} \\)..(i)Now \\(I = \\int_0^{\\pi /2} {\\frac{{\\cos \\left( {\\frac{\\pi }{2} - x} \\right) -\\sin \\left( {\\frac{\\pi }{2} - x} \\right)}}{{1 + \\sin \\left( {\\frac{\\pi }{2} - x}\\right)\\cos \\left( {\\frac{\\pi }{2} - x} \\right)}}\\,dx} \\)= \\(\\int_0^{\\pi /2} {\\frac{{\\sin x - \\cos x}}{{1 + \\sin x\\cosx}}\\,\\,dx} \\).....(ii)On adding, \\(2I = 0 \\Rightarrow I = 0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 1}^1 {\\log \\frac{{2 - x}}{{2 + x}}\\,dx}  = \\)   ','(a)2','(b)1','(c) - 1','(d) 0  ','4','DEFINITE INTEGRAL','2','Answer =  (d)Let \\(f(x) = \\log \\left( {\\frac{{2 - x}}{{2 + x}}} \\right)\\)\\(\\Rightarrow f( - x) = \\log {\\left( {\\frac{{2 - x}}{{2 + x}}} \\right)^{ - 1}} = - \\log \\left( {\\frac{{2 - x}}{{2 + x}}} \\right) =  - f(x)\\)   \\(\\therefore \\)\\(\\int_{ - 1}^1 {\\log \\left( {\\frac{{2 - x}}{{2 + x}}} \\right)\\,\\,dx = 0} .\\)',CURRENT_TIMESTAMP);
