INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - \\pi /2}^{\\pi /2} {\\log \\left( {\\frac{{2 - \\sin \\theta }}{{2 + \\sin\\theta }}} \\right)\\,d\\theta  = }\\)','(a)0','(b)1','(c)2','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a) Since \\(f( - \\theta ) = \\log {\\left( {\\frac{{2 - \\sin \\theta }}{{2 + \\sin\\theta }}} \\right)^{ - 1}} =  - \\log \\left( {\\frac{{2 - \\sin \\theta }}{{2 + \\sin\\theta }}} \\right) =  - f(\\theta )\\)\\(\\therefore \\) \\(f(x)\\) is an odd function of \\(x\\).Therefore, \\(2\\int_0^{\\pi /2} {\\log \\left( {\\frac{{2 - \\sin \\theta }}{{2 + \\sin\\theta }}} \\right){\\rm{ }}d\\theta  = 0} \\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ {\\begin{array}{*{20}{c}}{4x + 3\\,,}&{{\\rm{if}}}&{1 \\le x \\le 2}\\\\{3x + 5\\,,}&{{\\rm{if}}}&{2 < x \\le 4}\\end{array}} \\right.\\) then \\(\\int_1^4 {\\,f(x)} \\,dx = \\)','(a)80','(b)20','(c) - 20','(d) 37   ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_1^4 {f(x)dx = \\int_1^2 {(4x + 3)dx + \\int_2^4 {(3x + 5)\\,dx} } } \\)        ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /4} {\\log (1 + \\tan \\theta )\\,d\\theta  = }\\)[SCRA ; Karnataka CET , 05]','(a)\\(\\frac{\\pi }{4}\\log 2\\)','(b)\\(\\frac{\\pi }{4}\\log \\frac{1}{2}\\)','(c)\\(\\frac{\\pi }{8}\\log 2\\)','(d)\\(\\frac{\\pi }{8}\\log \\frac{1}{2}\\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^{\\pi /4} {\\,\\,\\,\\log (1 + \\tan \\theta )d\\theta }\\)\\(\\Rightarrow{}\\) \\(I = \\int_0^{\\pi /4} {\\log \\left\\{ {1 + \\tan \\left({\\frac{\\pi }{4} - \\theta } \\right)} \\right\\}} \\,d\\theta \\)\\(\\Rightarrow{}\\)  I = \\(\\int_0^{\\pi /4} {\\log \\left( {1 + \\frac{{1 - \\tan\\theta }}{{1 + \\tan \\theta }}} \\right)\\,d\\theta } \\)\\(= \\left| {2{x^2} + 3x} \\right|_1^2 + \\left| {\\frac{{3{x^2}}}{2} + 5x}\\right|_2^4 = 37.\\)\\(\\Rightarrow{}\\)  I = \\(\\int_0^{\\pi /4} {\\log 2d\\theta  - \\int_0^{\\pi /4} {\\log(1 + \\tan \\theta )\\,d\\theta } } \\)    \\(\\Rightarrow I = \\frac{1}{2}\\int_0^{\\pi /4} {\\log 2d\\theta  = \\frac{{\\log2}}{2}|\\theta |_0^{\\pi /4} = \\frac{\\pi }{8}\\log 2} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{2\\pi } {\\frac{{\\sin 2\\theta }}{{a - b\\,\\cos \\theta }}\\,d\\theta  = }\\)','(a)1','(b)2','(c) \\( \\frac{\\pi }{4}\\)','(d) 0    ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_0^{2\\pi } {\\frac{{\\sin 2\\theta }}{{a - b\\cos \\theta }}d\\theta  =\\int_0^{2\\pi } {\\frac{{\\sin (2\\pi  - 2\\theta )}}{{a - b\\cos (2\\pi  - \\theta)}}d\\theta } } \\)\\(\\Rightarrow{}\\)  I \\( =  - \\int_0^{2\\pi } {\\frac{{\\sin 2\\theta }}{{a - b\\cos\\theta }}d\\theta } \\) \\(\\Rightarrow \\,\\,2I = 0 \\Rightarrow \\,\\,\\int_0^{2\\pi } {\\frac{{\\sin 2\\theta}}{{a - b\\cos \\theta }}d\\theta  = 0} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^1 {f(1 - x)\\,dx} \\) has the same value as the integral','(a)\\(\\int_0^1 {f(x)\\,dx} \\)','(b)\\(\\int_0^1 {f( - x)\\,dx} \\)','(c)\\(\\int_0^1 {f(x - 1)\\,dx} \\)','(d)\\(\\int_{ - 1}^1 {f(x)\\,dx} \\)    ','1','DEFINITE INTEGRAL','2','Answer =  (a)Put \\(1 - x = t \\Rightarrow  - dx = dt\\). Also as \\(x = 0\\)to 1, \\(t = 1\\)to0Therefore, \\(\\int_0^1 {f(1 - x)dx = \\int_1^0 {f(t)( - dt)} }  = \\int_0^1{f(t)dt = \\int_0^1 {f(x)dx} } \\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The smallest interval \\([a,\\,\\,b]\\) such that \\(\\int_0^1 {\\frac{{dx}}{{\\sqrt {1+ {x^4}} }}}  \\in [a,\\,\\,b]\\) is given by','(a)\\(\\left[ {\\frac{1}{{\\sqrt 2 }},\\,\\,1} \\right]\\)','(b)\\([0,\\,\\,1]\\)','(c)\\(\\left[ {\\frac{1}{2},\\,\\,2} \\right]\\)','(d)\\(\\left[ {\\frac{3}{4},\\,\\,1}\\right]\\)  ','1','DEFINITE INTEGRAL','2','Answer =  (a)Let \\(I = \\int_0^1 {\\frac{{dx}}{{\\sqrt {1 + {x^4}} }}} \\)Here, \\(0 \\le x \\le 1 \\Rightarrow 1 \\le (1 + {x^4}) \\le 2\\)\\(\\Rightarrow{}\\) \\(1 \\le \\sqrt {1 + {x^4}}  \\le \\sqrt 2  \\Rightarrow\\frac{1}{{\\sqrt 2 }} \\le \\frac{1}{{\\sqrt {1 + {x^4}} }} \\le 1\\)\\(\\Rightarrow{}\\) \\(\\frac{1}{{\\sqrt 2 }} \\le \\int_0^1 {\\frac{{dx}}{{\\sqrt {1 +{x^4}} }} \\le 1} \\)Hence \\(\\left[ {\\frac{1}{{\\sqrt 2 }},\\,1} \\right]\\) is the smallest interval,such that \\(I \\in \\left[ {\\frac{1}{{\\sqrt 2 }},\\,\\,1} \\right]\\).Note: If \\(m = \\)least value of \\(f(x)\\)and M= greatest value of \\(f(x)\\)in [a,b], then \\(m(b - a) \\le \\int_a^b {f(x)dx \\le M(b - a)} \\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Assume that \\(f\\) is continuous everywhere, then \\(\\frac{1}{c}\\int_{ac}^{bc}{f\\left( {\\frac{x}{c}} \\right)} \\,dx = \\)','(a)\\(\\int_a^b {f\\left( {\\frac{x}{c}} \\right)} \\,dx\\)','(b)\\(\\frac{1}{c}\\int_a^b{f(x)\\,dx} \\)','(c)\\(\\int_a^b {f(x)\\,dx} \\)','(d)None of these    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\frac{1}{c}\\int_{ac}^{bc} {f(x/c)dx} \\)Put \\(\\frac{x}{c} = t \\Rightarrow dx = c\\,dt\\) and \\(x = bc \\Rightarrow t = b\\)\\(x = ac \\Rightarrow t = a\\) then, \\(I = \\int_a^b {f(t)dt = \\int_a^b {f(x)dx} }\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\, - 1/2}^{\\,1/2} {(\\cos x)\\,\\left','(a)0','(b)1','(c)\\({e^{1/2}}\\)','(d)\\(2{e^{1/2}}\\) ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_{ - 1/2}^{1/2} {(\\cos x)\\left[ {\\log \\left( {\\frac{{1 - x}}{{1 +x}}} \\right)} \\right]dx} \\)  \\(\\ldots{} \\)..(i)\\(\\therefore{}\\) \\(I = \\int_{ - 1/2}^{1/2} {\\cos ( - x)\\left[ {\\log \\left({\\frac{{1 + x}}{{1 - x}}} \\right)} \\right]} \\,dx\\)\\(\\Rightarrow{}\\) \\(I =  - \\int_{ - 1/2}^{1/2} {\\cos x\\left[ {\\log \\left({\\frac{{1 - x}}{{1 + x}}} \\right)} \\right]} \\,dx\\)       \\(\\ldots{} \\)..(ii)Adding (i) and (ii), we get\\(2I = \\int_{\\, - 1/2}^{\\,1/2} {\\cos \\,x\\,\\left[ {\\log \\,\\left( {\\frac{{1 - x}}{{1+ x}}} \\right)} \\right]} \\,dx - \\int_{\\, - 1/2}^{\\,1/2} {\\cos \\,x\\,\\left[ {\\log\\,\\left( {\\frac{{1 - x}}{{1 + x}}} \\right)} \\right]\\,\\,dx}\\)or \\(2I = 0\\)or I = 0.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\,0}^{\\,1} {\\,\\frac{{dx}}{{x + \\sqrt {1 - {x^2}} }}} \\) is','(a)\\(\\frac{\\pi }{3}\\)','(b)\\(\\frac{\\pi }{2}\\)','(c)\\(\\frac{1}{2}\\)','(d)\\(\\frac{\\pi }{4}\\)    ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_0^1 {\\frac{{dx}}{{x + \\sqrt {1 - {x^2}} }} = \\int_0^{\\pi /2}{\\frac{{\\cos \\theta \\,d\\theta }}{{\\sin \\theta  + \\cos \\theta }}} } \\)\\( =\\frac{\\pi }{4}\\),(Put \\(x = \\sin \\theta ,\\,dx = \\cos \\theta \\,d\\theta \\)). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\int_{ - 1}^1 {f(x)\\,dx = 0} \\), then ','(a)\\(f(x) = f( - x)\\)','(b)\\(f( - x) =  - f(x)\\)','(c)\\(f(x) = 2f(x)\\)','(d)None of these  ','2','DEFINITE INTEGRAL','2','Answer =  (b)It is a fundamental property.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 1}^1 {|1 - x|dx}  = \\)','(a)- 2','(b)0','(c)2','(d)4 ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_{\\, - 1}^{\\,1} {|1 - x|} \\,dx = \\int_{\\, - 1}^1 {(1 - x)\\,dx}  =\\left[ {x - \\frac{{{x^2}}}{2}} \\right]_{ - 1}^1 = 2\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If n is a positive integer and [x] is the greatest integer not exceeding x, then\\(\\int_0^n {\\,\\,\\{ x - [x]\\} \\,dx} \\) equals','(a)\\({n^2}/2\\)','(b)\\(n(n - 1)/2\\)','(c)\\(n\\,/\\,2\\)','(d)\\(\\frac{{{n^2}}}{2} - n\\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(x - [x]\\) is a periodic function with period 1.\\(\\therefore \\int_0^n {\\left\\{ {x - [x]} \\right\\}\\,dx = n\\int_0^1 {(x -[x])\\,\\,dx} }\\)   \\( = n\\left[ {\\int_0^1 {x\\,\\,dx - \\int_0^1 {[x]\\,dx} } } \\right]\\)\\(= n\\left[ {\\left( {\\frac{{{x^2}}}{2}} \\right)_0^1 - 0} \\right] = \\frac{n}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {x{{\\sin }^3}x\\,dx}  = \\)','(a)\\(\\frac{{4\\pi }}{3}\\)','(b)\\(\\frac{{2\\pi }}{3}\\)','(c)0','(d)None of these  ','2','DEFINITE INTEGRAL','2','Answer =  (b)Let \\(I = \\int_0^\\pi  {x{{\\sin }^3}x\\,dx} \\)\\(\\ldots{} \\)..(i)Also \\(I = \\int_0^\\pi  {(\\pi  - x){{\\sin }^3}x\\,\\,dx} \\)\\(\\ldots{} \\)..(ii)Adding (i) and (ii), we get\\(2I = \\pi \\int_0^\\pi  {{{\\sin }^3}x} \\,\\,dx = \\frac{\\pi }{4}\\int_0^\\pi  {\\{ 3\\sinx - \\sin 3x\\} dx}\\)\\(= \\frac{\\pi }{4}\\left[ { - 3\\cos x + \\frac{{\\cos 3x}}{3}} \\right]_0^\\pi  =\\frac{\\pi }{4}\\left[ {3 - \\frac{1}{3} + 3 - \\frac{1}{3}} \\right] = \\frac{{4\\pi}}{3}\\)Hence, \\(I = \\frac{{2\\pi }}{3}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 2}^2 {|1 - {x^2}|\\,dx = }\\)','(a)2','(b)4','(c)6','(d)8 ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(f(2a - x) = f(x)\\)\\(+ \\int_1^2 {|1 - {x^2}|dx}\\)= \\( - \\int_{ - 2}^{ - 1} {(1 - {x^2})\\,dx + \\int_{ - 1}^1 {(1 - {x^2})\\,dx -\\int_1^2 {(1 - {x^2})\\,dx} } } \\)= \\(\\frac{4}{3} + \\frac{4}{3} + \\frac{4}{3}\\)= 4. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{\\sqrt {\\cos x} }}{{\\sqrt {\\sin x}  + \\sqrt {\\cos x}}}\\,dx = } \\)  ','(a)0','(b)\\(\\frac{\\pi }{2}\\)','(c)\\(\\frac{\\pi }{4}\\)','(d)None of these ','3','DEFINITE INTEGRAL','2','Answer =  (c)Let \\(I = \\int_0^{\\pi /2} {\\,\\,\\frac{{\\sqrt {\\cos x} }}{{\\sqrt {\\sin x}  +\\sqrt {\\cos x} }}dx} \\)  .....(i)and \\(I = \\int_0^{\\pi /2} {\\frac{{\\sqrt {\\cos \\left( {\\frac{\\pi }{2} - x}\\right)} }}{{\\sqrt {\\sin \\left( {\\frac{\\pi }{2} - x} \\right)}  + \\sqrt {\\cos\\left( {\\frac{\\pi }{2} - x} \\right)} }}dx} \\)\\(I = \\int_0^{\\pi /2} {\\frac{{\\sqrt {\\sin x} }}{{\\sqrt {\\cos x + } \\sqrt{\\sin x} }}} \\,dx\\)\\(\\ldots{} \\)..(ii)Adding (i) and (ii), we get   \\(2I = \\int_0^{\\pi /2} {(1)dx = \\frac{\\pi }{2} \\Rightarrow I = \\frac{\\pi }{4}} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{x\\sin x\\cos x}}{{{{\\cos }^4}x + {{\\sin }^4}x}}} \\,dx =\\)','(a)0','(b)\\(\\frac{\\pi }{8}\\)','(c)\\(\\frac{{{\\pi ^2}}}{8}\\)','(d)\\(\\frac{{{\\pi ^2}}}{{16}}\\) ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_0^{\\pi /2} {\\frac{{x\\sin x\\cos x}}{{{{\\cos }^4}x + {{\\sin}^4}x}}dx} \\) .....(i)\\( = \\int_0^{\\pi /2} {\\frac{{\\left( {\\frac{\\pi }{2} - x} \\right)\\cos x\\sinx}}{{{{\\sin }^4}x + {{\\cos }^4}x}}} \\).....(ii)By adding (i) and (ii), we get \\(2I = \\frac{\\pi }{2}\\int_0^{\\pi /2} {\\frac{{\\cosx\\sin x}}{{{{\\cos }^4}x + {{\\sin }^4}x}}} \\)dx\\(\\Rightarrow{}\\) \\(I = \\frac{\\pi }{4}\\int_0^{\\pi /2} {\\frac{{\\tan x{{\\sec}^2}x}}{{1 + {{\\tan }^4}x}}dx} \\)Now, Put \\({\\tan ^2}x = t\\), we get    \\(I = \\frac{\\pi }{8}\\int_0^\\infty  {\\frac{{dt}}{{1 + {t^2}}} = \\frac{\\pi}{8}[{{\\tan }^{ - 1}}t]_0^\\infty  = \\frac{{{\\pi ^2}}}{{16}}} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The correct evaluation of \\(\\int_0^{\\pi /2} {\\left| {\\,\\sin \\left( {x -\\frac{\\pi }{4}} \\right)\\,} \\right|\\,dx} \\) is','(a)\\(2 + \\sqrt 2 \\)','(b)\\(2 - \\sqrt 2 \\)','(c) \\(- 2 + \\sqrt 2\\)','(d) 0    ','2','DEFINITE INTEGRAL','2','Answer =  (b)Let \\(I = \\int_0^{\\pi /2} {\\left| {\\sin \\left( {x - \\frac{\\pi }{4}} \\right)\\,}\\right|} \\,dx\\)\\(x - \\frac{\\pi }{4}\\)is -ve when \\(x \\le \\frac{\\pi }{4}\\)and +ve when \\(x >\\frac{\\pi }{4}\\) \\( =  - \\int_0^{\\pi /4} {\\sin \\left( {x - \\frac{\\pi }{4}} \\right)dx + \\int_{\\pi/4}^{\\pi /2} {\\sin \\left( {x - \\frac{\\pi }{4}} \\right)dx} } \\)\\(= 2 - \\sqrt 2 .\\)',CURRENT_TIMESTAMP);
