INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\,\\,\\,\\,\\,|\\sin x - \\cos x|\\,dx = }\\)','(a)0','(b)\\(2(\\sqrt 2  - 1)\\)','(c)\\(\\sqrt 2  - 1\\)','(d)\\(2(\\sqrt 2  + 1)\\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^{\\pi /2} {\\,\\,\\,\\,|\\sin x - \\cos x|dx} \\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {|\\cos x|\\,dx = } \\)','(a) \\(\\pi\\)','(b) 0','(c)2','(d)1    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(= \\int_0^{\\pi /4} { - (\\sin x - \\cos x} )dx + \\int_{\\pi /4}^{\\pi /2} {\\,(\\sin x- \\cos x)dx}  = 2(\\sqrt 2  - 1).\\)\\(\\int_0^\\pi  {\\,\\,|\\cos x|} \\,dx = 2\\int_0^{\\pi /2} {|\\cos x|dx = 2[\\sinx]_0^{\\pi /2} = 2} \\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the integral \\(\\int_{ - \\pi /4}^{\\pi /4} {{{\\sin }^{ - 4}}x} \\,dx\\)is','(a)3/2','(b)-8/3','(c)3/8','(d)8/3 ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_{ - \\pi /4}^{\\pi /4} {{{\\sin }^{ - 4}}x\\,dx = 2\\int_0^{\\pi /4}{\\frac{{{{\\cos }^4}x}}{{{{\\sin }^4}x}}{{\\sec }^4}x\\,dx} }  = 2\\int_0^{\\pi /4}{\\frac{{{{\\sec }^4}xdx}}{{{{\\tan }^4}x}}} \\)Put \\(\\tan x = t\\), we get \\(2\\int_0^1 {\\frac{{1 + {t^2}}}{{{t^4}}}dt} \\)   \\( = 2\\left[ {\\int_0^1 {{t^{ - 4}}dt + \\int_0^1 {{t^{ - 2}}dt} } } \\right]\\)\\(= 2\\left[ {\\left| { - \\frac{1}{{3{t^3}}}} \\right|_0^1 + \\left| { - \\frac{1}{t}}\\right|_0^1} \\right] =  - \\frac{8}{3}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{1.5} {[{x^2}]\\,dx} \\), where \\([\\,\\,.\\,\\,]\\)denotes the greatestinteger function, equals      [IIT ; DCE , 01]','(a)\\(2 + \\sqrt 2 \\)','(b)\\(2 - \\sqrt 2 \\)','(c)\\( - 2 + \\sqrt 2 \\)','(d)\\( - 2 - \\sqrt 2 \\)   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^{1.5} {[{x^2}]dx = \\int_0^1 {[{x^2}]dx + \\int_1^{\\sqrt 2 } {[{x^2}]dx+ \\int_{\\sqrt 2 }^{1.5} {[{x^2}]dx} } } } \\)  \\(= 0 + \\int_1^{\\sqrt 2 } {1dx + \\int_{\\sqrt 2 }^{1.5} {2dx = \\sqrt 2  - 1 + 3 -2\\sqrt 2  = 2 - \\sqrt 2 } } .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {\\frac{{x\\tan x}}{{\\sec x + \\tan x}}} \\,dx = \\)','(a)\\(\\frac{\\pi }{2} - 1\\)','(b)\\(\\pi \\left( {\\frac{\\pi }{2} + 1} \\right)\\)','(c)\\(\\frac{\\pi }{2} + 1\\)','(d)\\(\\pi \\left( {\\frac{\\pi }{2} - 1} \\right)\\) ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_0^\\pi  {\\frac{{x\\tan x}}{{\\sec x + \\tan x}}dx = \\int_0^\\pi {\\frac{{(\\pi  - x)\\tan (\\pi  - x)}}{{\\sec (\\pi  - x) + \\tan (\\pi  - x)}}} dx} \\)\\(\\Rightarrow{}\\) \\(2I = \\frac{\\pi }{2}\\int_0^\\pi  {\\frac{{\\tan x}}{{\\sec x +\\tan x}}dx = \\frac{\\pi }{2}\\int_0^\\pi  {\\frac{{\\sin x}}{{1 + \\sin x}}dx} } \\)=\\(\\frac{\\pi }{2}\\left[ {\\int_0^\\pi  {1dx - \\int_0^\\pi {\\frac{{dx}}{{1 + \\sin x}}} } } \\right]\\)On solving, we get \\(I = \\frac{{{\\pi ^2}}}{2} - \\pi  = \\pi \\left( {\\frac{\\pi}{2} - 1} \\right)\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {\\frac{{x\\,\\tan x}}{{\\sec x + \\cos x}}} \\,dx =\\)','(a)\\(\\frac{{{\\pi ^2}}}{4}\\)','(b)\\(\\frac{{{\\pi ^2}}}{2}\\)','(c)\\(\\frac{{3{\\pi ^2}}}{2}\\)','(d)\\(\\frac{{{\\pi ^2}}}{3}\\)  ','1','DEFINITE INTEGRAL','2','Answer =  (a)Let  I =\\(\\int_0^\\pi  {\\frac{{x\\tan x}}{{\\sec x + \\cos x}}dx}  = \\int_0^\\pi {\\frac{{(\\pi  - x)\\tan (\\pi  - x)}}{{\\sec (\\pi  - x) + \\cos (\\pi  - x)}}dx} \\)It gives \\(I = \\frac{\\pi }{2}\\int_0^\\pi  {\\frac{{\\sin x}}{{1 + {{\\cos }^2}x}}}dx\\)Now put \\(\\cos x = t\\)and solve, we get \\(I = \\frac{\\pi }{2} \\times \\frac{\\pi}{2} = \\frac{{{\\pi ^2}}}{4}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 1}^1 {{{\\sin }^3}x{{\\cos }^2}x\\,dx = } \\)  ','(a)0','(b)1','(c)\\(\\frac{1}{2}\\)','(d) 2 ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_{ - 1}^1 {{{\\sin }^3}x{{\\cos }^2}x\\,dx = 0} \\),Since the function is an odd function.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For any integer \\(n,\\) the integral\\(\\int_0^\\pi  {{e^{{{\\sin }^2}x}}{{\\cos }^3}(2n + 1)x\\,dx = } \\)','(a) - 1','(b) 0','(c)1','(d)\\(\\pi \\) ','2','DEFINITE INTEGRAL','2','Answer =  (b)Let \\(f(x) = \\int_0^\\pi  {{e^{{{\\sin }^2}x}}{{\\cos }^3}(2n + 1)x.dx} \\)Since \\(\\cos (2n + 1)(\\pi  - x) = \\cos [(2n + 1)\\pi  - (2n + 1)x]\\)\\( =  - \\cos (2n + 1)x\\)and \\({\\sin ^2}(\\pi  - x) = {\\sin ^2}x\\)Hence by the property of definite integral,\\(\\int_0^\\pi  {{e^{{{\\sin }^2}x}}{{\\cos }^3}(2n + 1)x\\,dx = 0} \\),  \\([f(2a - x)=  - f(x)]\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{1/e}^e {|\\log x|\\,dx = } \\)','(a)\\(1 - \\frac{1}{e}\\)','(b)\\(2\\,\\left( {1 - \\frac{1}{e}} \\right)\\)','(c)\\({e^{ - 1}} - 1\\)','(d)None of these   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_{1/e}^e {|\\log x|dx = \\int_{1/e}^1 { - \\log x\\,dx + \\int_1^e {\\,\\logx\\,dx} } } \\)\\(= [x - x\\log x]_{1/e}^1 + [x\\log x - x]_1^e\\)  \\( = (1 - 0) - \\left\\{ {\\frac{1}{e} - \\frac{1}{e}( - 1)} \\right\\} + e - e + 1\\)\\(= 2 - \\frac{2}{e} = 2\\left( {1 - \\frac{1}{e}} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,\\pi /2} {\\{ x - ','(a)\\(\\frac{{{\\pi ^2}}}{8}\\)','(b)\\(\\frac{{{\\pi ^2}}}{8} - 1\\)','(c)\\(\\frac{{{\\pi ^2}}}{8} - 2\\)','(d)None of these    ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(\\int_0^{\\pi /2} {\\{ x - [\\sin x]\\} \\,dx = \\int_0^{\\pi /2} {xdx -\\int_{\\,0}^{\\,\\pi /2} {[\\sin x]\\,\\,dx} } } \\)\\( = \\left( {\\frac{{{x^2}}}{2}} \\right)_0^{\\pi /2}\\)\\( = \\frac{{{\\pi ^2}}}{8}\\),    \\([\\int_{\\,0}^{\\,\\pi /2} {[\\sin x]\\,dx = 0} ]\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the integral \\(I = \\int_{\\,0}^{\\,1} {\\,x{{(1 - x)}^n}dx} \\) is','(a)\\(\\frac{1}{{n + 1}}\\)','(b)\\(\\frac{1}{{n + 2}}\\)','(c)\\(\\frac{1}{{n + 1}} - \\frac{1}{{n + 2}}\\)','(d)\\(\\frac{1}{{n + 1}} + \\frac{1}{{n+ 2}}\\) ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^1 {x{{(1 - x)}^n}dx} \\)\\(- I = \\int_0^1 { - x{{(1 - x)}^n}dx = \\int_0^1 {(1 - x - 1){{(1 - x)}^n}dx} }\\)\\(= \\int_0^1 {{{(1 - x)}^{n + 1}}dx - \\int_0^1 {{{(1 - x)}^n}dx} }\\)\\(= \\left[ {\\frac{{{{(1 - x)}^{n + 2}}}}{{ - (n + 2)}}} \\right]_0^1 - \\left[{\\frac{{{{(1 - x)}^{n + 1}}}}{{ - (n + 1)}}} \\right]_0^1 = \\frac{1}{{n + 2}} -\\frac{1}{{n + 1}}\\)\\(\\Rightarrow I = \\frac{1}{{n + 1}} - \\frac{1}{{n + 2}}.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_\\pi ^{2\\pi } {','(a)\\( - \\pi \\)','(b)\\( - 2\\pi \\)','(c)\\( - \\frac{{5\\pi }}{3}\\)','(d)\\(\\frac{{5\\pi }}{3}\\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_\\pi ^{2\\pi } {[2\\sin x]dx = \\int_\\pi ^{\\pi  + (\\pi /6)} {( - 1)dx +\\int_{\\pi  + (\\pi /6)}^{\\pi  + (\\pi /2)} {\\,( - 2)dx} } } \\)\\(+ \\int_{\\pi  + (\\pi /2)}^{\\pi  + (\\pi /2) + (\\pi /3)} {\\,( - 2)dx + \\int_{\\pi + (\\pi /2) + (\\pi /3)}^{2\\pi } {\\,( - 1)dx} }\\)\\(=  - \\frac{\\pi }{6} - 2\\left[ {\\frac{\\pi }{2} - \\frac{\\pi }{6}} \\right] -2\\left[ {\\frac{\\pi }{3}} \\right] - 1\\left[ {\\frac{\\pi }{2} - \\frac{\\pi }{3}}\\right]\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x)\\) is a continuous periodic function with period \\(T,\\) then theintegral \\(I = \\int_a^{a + T} {f(x)\\,dx} \\) is','(a)Equal to \\(2a\\)','(b)Equal to \\(3a\\)','(c)Independent of \\(a\\)','(d)None of these   ','3','DEFINITE INTEGRAL','2','Answer =  (c)C\\( =  - \\frac{\\pi }{6} - \\frac{{2\\pi }}{3} - \\frac{{2\\pi }}{3} - \\frac{\\pi}{6}\\)\\(=  - \\frac{\\pi }{6} - \\frac{{8\\pi }}{6} - \\frac{\\pi }{6} =  - \\frac{{10\\pi}}{6} =  - \\frac{{5\\pi }}{3}.\\)onsider the function \\(g(a) = \\int_a^{a + T} {f(x)dx} \\)\\(= \\int_a^0 {f(x)dx + \\int_0^T {f(x)dx + \\int_T^{a + T} {\\,\\,f(x)dx} } }\\)Putting \\(x - T = y\\) in last integral, we get \\(\\int_T^{a + T} {f(x)dx =\\int_0^a {f(y + T)dy = \\int_0^a {f(y)dy} } } \\)\\(\\Rightarrow{}\\) \\(g(a) = \\int_a^0 {f(x)dx + \\int_0^1 {f(x)dx + \\int_0^a{f(x)dx} } } \\)\\( = \\int_0^T {f(x)dx} \\)Hence g(a) is independent of a.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\int_0^\\pi  {x\\,f({{\\cos }^2}x + {{\\tan }^4}x)\\,dx} \\) \\( = k\\int_0^{\\pi/2} {f({{\\cos }^2}x + {{\\tan }^4}x)\\,dx,} \\) then the value of \\(k\\) is','(a)\\(\\frac{\\pi }{2}\\)','(b)\\(\\pi \\)','(c)\\( - \\frac{\\pi }{2}\\)','(d)None of these   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^\\pi  {xf} ({\\cos ^2}x + {\\tan ^4}x)dx = k\\int_0^{^{\\pi /2}}{f({{\\cos }^2}x + {{\\tan }^4}x)dx} \\)By the property of definite integral\\(I = \\int_0^\\pi  x f({\\cos ^2}x + {\\tan ^4}x)dx\\)\\(\\ldots{} \\)..(i)\\( = \\int_0^\\pi  {(\\pi  - x)f({{\\cos }^2}x + {{\\tan }^4}x)dx}\\)\\(\\ldots{} \\)..(ii)Adding (i) and (ii), we have\\(2I = \\pi \\int_0^\\pi  {f({{\\cos }^2}x + {{\\tan }^4}x)dx}\\)\\(\\Rightarrow{}\\)  \\(2I = 2\\pi \\int_0^{\\pi /2} {f\\,({{\\cos }^2}x + {{\\tan}^4}x)dx} \\)\\(\\Rightarrow{}\\)  \\(I = \\pi \\int_0^{\\pi /2} {f({{\\cos }^2}x + {{\\tan }^4}x)dx}\\)On comparing with given integral, we get \\(k = \\pi \\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 3}^3 {\\frac{{{x^2}\\sin x}}{{1 + {x^6}}}\\,dx = }\\)','(a)4','(b)2','(c)0','(d)None of these ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_{ - 3}^3 {\\frac{{{x^2}\\sin x}}{{1 + {x^6}}}} dx = 0\\). By the propertyof definite integral, \\(\\int_{ - a}^a {f(x)dx = 0} \\), when \\(f(x) =  - f( -x)\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_0^{\\pi /2} {\\frac{{dx}}{{1 + {{\\tan }^3}x}}} \\) is    [IIT; DCE , 01]','(a)0','(b)1','(c)\\(\\frac{\\pi }{2}\\)','(d)\\(\\frac{\\pi }{4}\\)  ','4','DEFINITE INTEGRAL','2','Answer =  (d) \\(I = \\int_0^{\\pi /2} {\\frac{{dx}}{{1 + {{\\tan }^3}x}} = \\int_0^{\\pi /2}{\\frac{{{{\\cos }^3}x}}{{{{\\sin }^3}x + \\cos {x^3}}}} } dx\\)  \\(\\ldots{} \\)..(i)\\( = \\int_0^{\\pi /2} {\\frac{{{{\\sin }^3}x}}{{{{\\cos }^3}x + {{\\sin }^3}x}}dx}\\)                 .....(ii)Adding (i) and (ii), we get \\(2I = \\int_0^{\\pi /2} {dx \\Rightarrow I = \\frac{\\pi}{4}.} \\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\pi /4}^{3\\pi /4} {\\frac{\\varphi }{{1 + \\sin \\varphi}}\\,d\\varphi ,} \\) is','(a)\\(\\pi \\tan \\frac{\\pi }{8}\\)','(b)\\(\\log \\tan \\frac{\\pi }{8}\\)','(c)\\(\\tan \\frac{\\pi }{8}\\)','(d)None of these  ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_{\\pi /4}^{3\\pi /4} {\\frac{\\varphi }{{1 + \\sin \\varphi }}d\\varphi } = \\int_{\\pi /4}^{3\\pi /4} {\\frac{{\\pi  - \\varphi }}{{1 + \\sin (\\pi  - \\varphi)}}d\\varphi } \\)\\(\\left\\{ {\\frac{\\pi }{4} + \\frac{{3\\pi }}{4} = \\pi } \\right\\}\\)\\(\\Rightarrow{}\\) \\(2I = \\int_{\\pi /4}^{3\\pi /4} {\\frac{\\pi }{{1 + \\sin \\varphi}}d\\varphi } \\)On simplification, we get \\(I = \\pi (\\sqrt 2  - 1) = \\pi \\tan \\frac{\\pi }{8}.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(a + b - x) = f(x),\\) then \\(\\int_a^b {x\\,f(x)\\,dx = } \\)','(a)\\(\\frac{{a + b}}{2}\\int_a^b {f(b - x)\\,dx} \\)','(b)\\(\\frac{{a + b}}{2}\\int_a^b{f(x)\\,dx} \\)','(c)\\(\\frac{{b - a}}{2}\\int_a^b {f(x)\\,dx} \\)','(d)None of these   ','2','DEFINITE INTEGRAL','2','Answer =  (b)Since \\(I = \\int_a^b {xf(x)dx = \\int_a^b {(a + b - x)f(a + b - x)dx} } \\)\\(\\Rightarrow{}\\)    \\(I = \\int_a^b {(a + b)} f(x)dx - \\int_a^b {xf(x)dx} \\)\\(\\left\\{ {f(a + b - x) = f(x){\\kern 1pt} {\\rm{given}}} \\right\\}\\)\\(\\Rightarrow{}\\) \\(2I = (a + b)\\int_a^b {f(x)dx} \\)\\(\\Rightarrow{}\\) \\(I =\\int_a^b {x\\,f(x)dx = \\frac{{a + b}}{2}\\int_a^b {f(x)dx} } \\).  ',CURRENT_TIMESTAMP);
