INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{1}{{1 + \\sqrt {\\tan x} }}} \\,dx = \\)  ','(a)\\(\\frac{\\pi }{2}\\)','(b)\\(\\frac{\\pi }{4}\\)','(c)\\(\\frac{\\pi }{6}\\)','(d) 1    ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^{\\pi /2} {\\frac{1}{{1 + \\sqrt {\\tan x} }}dx = \\int_0^{\\pi /2}{\\frac{{\\sqrt {\\cos x} }}{{\\sqrt {\\cos x}  + \\sqrt {\\sin x} }}dx = \\frac{\\pi}{4}} } \\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{ - 1}^1 {\\frac{{\\sin x - {x^2}}}{{3 - |x|}}\\,dx} \\)is','(a)0','(b)\\(2\\int_0^1 {\\frac{{\\sin x}}{{3 - |x|}}\\,dx} \\)','(c)\\(2\\int_0^1 {\\frac{{ - {x^2}}}{{3 - |x|}}} \\,dx\\)','(d)\\(2\\int_0^1 {\\frac{{\\sinx - {x^2}}}{{3 - |x|}}\\,dx} \\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(= [x]_2^3 = 3 - 2 = 1 \\Rightarrow I = \\frac{1}{2}.\\)\\(I = \\int_{ - 1}^1 {\\,\\frac{{\\sin x - {x^2}}}{{3\\,\\, - |x|}}\\,} dx = \\int_{ -1}^1 {\\,\\frac{{\\sin x}}{{3 - |x|}}} \\,dx - \\int_{ - 1}^1 {\\,\\frac{{{x^2}}}{{3 -|x|}}} \\,dx\\)Here, \\(f(x) = \\frac{{\\sin x}}{{3 - |x|}}\\) is an odd function but \\(f(x) =\\frac{{{x^2}}}{{3 - |x|}}\\) is an even function ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 1}^1 {{{\\sin }^{11}}x\\,dx} \\) is equal to','(a)\\(\\frac{{10}}{{11}}.\\frac{8}{9}.\\frac{6}{7}.\\frac{4}{5}.\\frac{2}{3}\\)','(b)\\(\\frac{{10}}{{11}}.\\frac{8}{9}.\\frac{6}{7}.\\frac{4}{5}.\\frac{2}{3}.\\frac{\\pi}{2}\\)','(c)1','(d)0    ','4','DEFINITE INTEGRAL','2','Answer =  (d)S\\(\\therefore \\,\\,I =  - \\int_{ - 1}^1 {\\frac{{{x^2}}}{{3 - |x|}}\\,} dx =  -2\\int_0^1 {\\frac{{{x^2}}}{{3 - |x|}}} \\,dx\\)\\(= 2\\int_0^1 {\\frac{{ - {x^2}}}{{3 - |x|}}\\,} dx.\\)ince \\(f(x) = {\\sin ^{11}}x\\)is an odd function, therefore \\(\\int_{ - 1}^1{{{\\sin }^{11}}x\\,\\,dx}  = 0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\((n - m)\\) is odd and \\(|m|\\, \\ne \\,|n|,\\) then \\(\\int_0^\\pi  {\\cos mx\\sinnx} \\,dx\\) is','(a) \\(\\frac{{2n}}{{{n^2} - {m^2}}}\\)','(b) 0','(c)\\(\\frac{{2n}}{{{m^2} - {n^2}}}\\)','(d)\\(\\frac{{2m}}{{{n^2} - {m^2}}}\\)  ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\frac{1}{2}\\int_0^\\pi  {[\\,\\sin (m + n)x - \\sin (m - n)x]dx} \\)\\(=  - \\frac{1}{2}\\left[ {\\frac{{\\cos (m + n)x}}{{m + n}} - \\frac{{\\cos (m -n)x}}{{m - n}}} \\right]_0^\\pi\\)\\(=  - \\frac{1}{2}\\left[ {\\left\\{ {\\frac{{{{( - 1)}^{m + n}}}}{{m + n}} -\\frac{{{{( - 1)}^{m - n}}}}{{m - n}} - } \\right\\} - \\left\\{ {\\frac{1}{{m + n}} -\\frac{1}{{m - n}}} \\right\\}} \\right]\\)Since n - m is odd, therefore \\(n + m\\)must be odd, so \\({( - 1)^{m + n}} = {(- 1)^{m - n}} =  - 1\\).Also, since \\(|m| \\ne |n|,m + n \\ne 0,m - n \\ne 0\\)\\(\\therefore{}\\) \\(I = \\frac{1}{{m + n}} - \\frac{1}{{m - n}} = \\frac{{m + n - m- n}}{{{m^2} - {n^2}}} = \\frac{{2n}}{{{n^2} - {m^2}}}\\). ',CURRENT_TIMESTAMP);
