INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,2\\pi } {|\\sin x|\\,dx = }\\)','(a)0','(b)1','(c)2','(d)4 ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_0^{2\\pi } {|\\sin x|dx = \\int_0^\\pi  {\\sin x\\,dx + \\int_\\pi ^{2\\pi } {- \\sin x\\,dx} } } \\)\\(\\{ \\,\\,{\\cos ^3}(\\pi  - x) =  - {\\cos ^3}x\\} .\\)   \\(= [ - \\cos x]_0^\\pi  + [\\cos x]_\\pi ^{2\\pi } = 1 + 1 + 1 + 1 = 4.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 3}^3 {\\frac{{{x^2}\\sin 2x}}{{{x^2} + 1}}\\,dx = }\\)','(a)0','(b)1','(c)\\(2{\\log _e}3\\)','(d)None of these   ','1','DEFINITE INTEGRAL','2','Answer =  (a)Obviously the function \\(\\frac{{{x^2}\\sin 2x}}{{{x^2} + 1}}\\)is odd, so \\(\\int_{ - 3}^3 {\\frac{{{x^2}\\sin 2x}}{{{x^2} + 1}}\\,} dx = 0.\\)',CURRENT_TIMESTAMP);
