INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x)\\) is an odd function of \\(x,\\) then \\(\\int_{ - \\frac{\\pi}{2}}^{\\frac{\\pi }{2}} {f(\\cos x)\\,dx} \\) is equal to','(a)0','(b)\\(\\int_0^{\\frac{\\pi }{2}} {f(\\cos x)\\,dx} \\)','(c)\\(2\\int_0^{\\frac{\\pi }{2}} {f(\\sin x)\\,dx} \\)','(d)\\(\\int_0^\\pi  {f(\\cos x)\\,dx}\\)  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(f(\\cos x)\\)is an even function.\\(f(\\cos ( - x)) = f(\\cos x)\\)  \\(\\therefore \\,\\,\\int_{ - \\pi /2}^{\\pi /2} {f(\\cos x)dx = 2\\int_0^{\\pi /2}{f(\\cos x)dx} } \\)\\(= 2\\int_0^{\\pi /2} {f(\\sin x)dx} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^\\pi  {{{\\sin }^2}x\\,dx} \\) is equal to','(a)\\(\\pi \\)','(b)\\(\\frac{\\pi }{2}\\)','(c)0','(d)None of these   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_0^\\pi  {{{\\sin }^2}x\\,dx = 2\\int_0^{\\pi /2} {{{\\sin }^2}x\\,dx} }\\),\\(\\{ \\,\\,\\int_0^{2a} {f(x) = 2\\int_0^a {f(a - x)dx} } \\), if \\(f(2a - x) =f(x)\\)}  \\(I = 2 \\times \\frac{1}{2} \\times \\frac{\\pi }{2} = \\frac{\\pi }{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_0^{\\pi /2} {\\frac{{\\sin x}}{{\\sin x + \\cos x}}\\,dx} \\) equals','(a)\\(\\frac{\\pi }{2}\\)','(b)\\(\\frac{\\pi }{3}\\)','(c)\\(\\frac{\\pi }{4}\\)','(d)\\(\\frac{\\pi }{6}\\)  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^{\\pi /2} {\\frac{{\\sin x.dx}}{{\\sin x + \\cos x}}}  = \\int_0^{\\pi/2} {\\frac{{\\cos x.dx}}{{\\cos x + \\sin x}}} \\),\\(\\,\\,\\left( {\\int_0^a {f(x)dx = \\int_0^a {f(a - x)dx} } } \\right)\\)   \\(2I = \\int_0^{\\pi /2} {dx}  \\Rightarrow I = \\frac{\\pi }{4}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - 1}^1 {x{{\\tan }^{ - 1}}x\\,dx} \\) equals ','(a)\\(\\left( {\\frac{\\pi }{2} - 1} \\right)\\)','(b)\\(\\left( {\\frac{\\pi }{2} + 1}\\right)\\)','(c) \\(    (\\pi  - 1) \\)','(d) 0 ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_{ - 1}^1 {x{{\\tan }^{ - 1}}x\\,dx = 2} \\int_0^1 {x{{\\tan }^{ -1}}x\\,dx} \\)\\(\\,\\,\\,x{\\tan ^{ - 1}}x\\)is an even function\\(I = [2\\frac{{{x^2}}}{2}{\\tan ^{ - 1}}x]_0^1 - 2\\int_0^1{\\frac{1}{2}\\frac{{{x^2}}}{{1 + {x^2}}}dx}\\)\\(I = [{x^2}{\\tan ^{ - 1}}x]_0^1 - \\int_0^1 {\\frac{{{x^2} + 1 - 1}}{{1 +{x^2}}}dx}\\)I =\\([{x^2}{\\tan ^{ - 1}}x]_0^1 - [x]_0^1 + [{\\tan ^{ - 1}}x]_0^1\\)\\(\\Rightarrow{}\\) \\(I = \\frac{\\pi }{4} - 1 + \\frac{\\pi }{4} = \\frac{\\pi }{2} -1\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - a}^a {\\sin x\\,f(\\cos x)\\,dx = } \\)','(a)\\(2\\int_0^a {\\sin x\\,f(\\cos x)\\,dx}\\)','(b) 0','(c)1','(d)None of these   ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_{ - a}^a {\\sin xf(\\cos x)\\,dx} \\)\\(f(x) = \\sin x\\,f(\\cos x) \\Rightarrow f( - x) =  - \\sin x\\,f(\\cos x)\\)\\(\\,\\,\\,\\,f(x)\\)is an odd function  \\(\\therefore \\,\\,\\,I = \\int_{}^{} {f(x)dx = 0} .\\)',CURRENT_TIMESTAMP);
