INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\, - 1}^{\\,2} {|x|\\,dx} \\)','(a)5/2','(b)1/2','(c)3/2','(d)7/2   ','1','DEFINITE INTEGRAL','2','Answer =  (a) \\(I = \\int_{ - 1}^2 {\\,|x|dx} \\)\\( = \\int_{ - 1}^0 { - x\\,dx}  + \\int_0^2{x\\,\\,dx} \\)\\( =  - \\left[ {\\frac{{{x^2}}}{2}} \\right]_{ - 1}^0 + \\left[{\\frac{{{x^2}}}{2}} \\right]_0^2\\)  \\( =  - \\left[ {0 - \\frac{1}{2}} \\right] + [2]\\)\\(= 2 + \\frac{1}{2} = \\frac{5}{2}.\\)',CURRENT_TIMESTAMP);
