INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{ - \\,\\pi /2}^{\\,\\pi /2} {\\,\\frac{{\\sin x}}{{1 + {{\\cos }^2}x}}{e^{ -{{\\cos }^2}x}}dx} \\) is equal to ','(a) \\(2{e^{ - 1}}\\)','(b) 1','(c)0','(d)None of these    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_{ - \\pi /2}^{\\pi /2} {\\frac{{\\sin x}}{{1 + {{\\cos }^2}x}}{e^{ -{{\\cos }^2}x}}dx} \\)\\(\\frac{{\\sin x}}{{1 + {{\\cos }^2}x}}{e^{ - {{\\cos }^2}x}}\\)is an \\( =\\frac{1}{{\\sqrt 3 }} - \\frac{1}{{3\\sqrt 3 }} + \\frac{{ - 1}}{{3\\sqrt 3 }} +\\frac{1}{{\\sqrt 3 }}\\)\\(= \\frac{4}{{3\\sqrt 3 }}.\\)odd function, \\(\\therefore{}\\)\\(I = 0\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(f(x) = f(2 - x),\\) then \\(\\int_{\\,0.5}^{\\,1.5} {\\,xf(x)\\,dx} \\) equals ','(a)\\(\\int_{\\,0}^{\\,1} {\\,f(x)\\,dx} \\)','(b)\\(\\int_{\\,0.5}^{\\,1.5} {\\,f(x)\\,dx} \\)','(c) \\( 2\\int_{\\,0.5}^{\\,1.5} {\\,f(x)\\,dx}\\)','(d) 0  ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(I = \\int_{0.5}^{1.5} {xf\\,(x)\\,dx = \\int_{0.5}^{1.5} {(2 - x)f(2 - x)\\,dx}} \\),\\(\\left[ {\\int_a^b {f(x)dx = \\int_a^b {f(a + b - x)dx} } } \\right]\\)\\( = \\int_{0.5}^{1.5} {(2 - x)f(x)\\,dx}  = 2\\int_{0.5}^{1.5} {f(x)\\,dx - I}\\)\\(\\Rightarrow{}\\)\\(I = \\int_{0.5}^{1.5} {f(x)\\,dx} \\).  ',CURRENT_TIMESTAMP);
