INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\, - 2}^{\\,2} {|x|\\,dx = } \\)','(a)0','(b)1','(c)2','(d)4    ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_{ - 2}^2 {|x|dx} \\)\\( =  - \\int_{ - 2}^0 {x\\,dx + \\int_0^2 {x\\,dx}} \\)\\( =  - \\left[ {\\frac{{{x^2}}}{2}} \\right]_{ - 2}^0 + \\left[{\\frac{{{x^2}}}{2}} \\right]_0^2\\)\\(\\Rightarrow I = (2 - 1) + (5 - 4) = 2.\\)\\(=  - ( - 2) + (2) = 4.\\)Answer = (d)Given \\(f( - x) =  - f(x)\\)We know that, \\(\\int_{ - a}^a {f(x)dx = 0 = \\int_{ - a}^0 {f(x)dx + } \\int_0^a{f(x)dx} } \\)\\(\\Rightarrow \\int_{ - 1}^0 {f(x)\\,dx + } \\int_0^1 {f(x)\\,dx = 0}  \\Rightarrow\\int_{ - 1}^0 {f(x)dx =  - 5}\\)\\(\\Rightarrow \\int_{ - 1}^0 {f(t)\\,dt =  - 5} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\({I_1} = \\int_a^{\\pi  - a} {xf(\\sin x)dx,\\,{I_2} = \\int_a^{\\pi  - a}{\\,\\,f(\\sin x)dx} } \\), then \\({I_2}\\) is equal to','(a)\\(\\frac{\\pi }{2}{I_1}\\)','(b)\\(\\pi \\,{I_1}\\)','(c)\\(\\frac{2}{\\pi }{I_1}\\)','(d)\\(2{I_1}\\)  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\({I_1} = \\int_a^{\\pi  - a} {xf(\\sin x)dx} \\)\\( = \\int_a^{\\pi  - a} {(\\pi  -x)\\,f\\,(\\sin (\\pi  - x))\\,dx} \\),\\([\\int_a^b {f(x)dx = \\int_a^b {f(a + b - x)\\,dx} } ]\\)\\( = \\int_a^{\\pi  - a} {(\\pi  - x)\\,f\\,(\\sin x)\\,dx} \\)\\(= \\int_a^{\\pi  - a} {\\pi \\,f\\,(\\sin x)\\,dx - {I_1}}\\)    \\(\\Rightarrow 2{I_1} = \\pi \\,{I_2}\\, \\Rightarrow {I_2} = \\frac{2}{\\pi }{I_1}.\\)',CURRENT_TIMESTAMP);
