INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\,{e^{ - 1}}}^{\\,{e^2}} {\\left| {\\frac{{{{\\log }_e}x}}{x}}\\right|\\,dx} \\) is ','(a)\\(\\frac{3}{2}\\)','(b)\\(\\frac{5}{2}\\)','(c)3','(d)5 ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_{{e^{ - 1}}}^{{e^2}} {\\left| {\\frac{{{{\\log }_e}x}}{x}} \\right|dx =\\int_{{e^{ - 1}}}^1 {\\left| {\\frac{{{{\\log }_e}x}}{x}} \\right|\\,dx +\\int_1^{{e^2}} {\\left| {\\frac{{{{\\log }_e}x}}{x}} \\right|\\,dx} } } \\)\\( = \\int_{{e^{ - 1}}}^1 { - \\frac{{\\log x}}{x}dx + \\int_1^{{e^2}} {\\frac{{\\logx}}{x}dx} } \\)\\(= \\int_{ - 1}^0 { - zdz + \\int_0^2 {zdz} } ,\\)(Putting \\({\\log _e}x = z\\) \\(\\Rightarrow{}\\) \\((1/x)\\,dx = dz)\\)   \\(= \\left[ { - \\frac{{{z^2}}}{2}} \\right]_{ - 1}^0 + \\left[ {\\frac{{{z^2}}}{2}}\\right]_0^2 = \\frac{1}{2} + 2 = \\frac{5}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ {\\begin{array}{*{20}{c}}{{e^{\\cos x}}\\sin x,}&{|x|\\, \\le 2}\\\\{2,}&{{\\rm{otherwise}}}\\end{array}} \\right.\\), then \\(\\int_{\\, - \\,2}^{\\,3} {f(x)\\,dx} \\) is equal to','(a)0','(b)1','(c)2','(d)3    ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(\\int_{ - 2}^3 {f(x)\\,dx = } \\int_{ - 2}^2 {f(x)\\,dx + \\int_2^3 {\\,f(x)\\,dx}} \\)\\({e^{\\cos x}}\\sin x\\)is an odd function  \\(\\therefore \\,\\int_{ - 2}^3 {f(x)\\,dx}  = \\int_{ - 2}^2 {{e^{\\cos x}}\\sin x\\,dx +\\int_2^3 {2\\,dx = 0 + 2\\,(3 - 2) = 2} } .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f:R \\to R\\) and \\(g:R \\to R\\) are one to one, real valued functions, thenthe value of the integral \\(\\int_{\\, - \\pi }^{\\,\\pi } {','(a)0','(b)\\(\\frac{8}{3}\\)','(c)1','(d)None of these ','1','DEFINITE INTEGRAL','2','Answer =  (a)Let \\(\\varphi (x) = [f(x) + f( - x)][g(x) - g( - x)]\\)then, \\(\\varphi ( - x) = [f( - x) + f(x)]\\,[g( - x) - g(x)]\\)\\(\\therefore \\int_{ - \\pi }^\\pi  {\\varphi (x)dx = 0} \\)\\(\\Rightarrow{}\\)\\(\\int_{- \\pi }^\\pi  {[f(x) + f( - x)][g(x) - g( - x)]dx = 0} \\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,\\pi /6}^{\\,\\pi /3} {\\,\\frac{{dx}}{{1 + \\sqrt {\\cot x} }}} \\) is ','(a)\\(\\pi /3\\)','(b)\\(\\pi /6\\)','(c)\\(\\pi /12\\)','(d)\\(\\pi /2\\) ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_{\\pi /6}^{\\pi /3} {\\frac{{dx}}{{1 + \\sqrt {\\cot x} }} = }\\int_{\\pi /6}^{\\pi /3} {\\frac{{\\sqrt {\\sin x} }}{{\\sqrt {\\sin x}  + \\sqrt {\\cosx} }}\\,} dx\\)   \\(\\ldots{} \\)..(i)\\(I = \\int_{\\pi /6}^{\\pi /3} {\\frac{{\\sqrt {\\cos x} }}{{\\sqrt {\\cos x}  + \\sqrt{\\sin x} }}\\,} dx\\)                 .....(ii)Adding (i) and (ii), \\(2I = \\int_{\\,\\pi /6}^{\\,\\pi /3} {dx} \\); \\(I =\\frac{1}{2}\\left( {\\frac{\\pi }{3} - \\frac{\\pi }{6}} \\right) = \\frac{\\pi}{{12}}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\,0}^{\\,\\pi /2} {\\frac{{{{\\sin }^{2/3}}x}}{{{{\\sin}^{2/3}}x + {{\\cos }^{2/3}}x}}dx} \\) is ','(a)\\(\\pi /4\\)','(b)\\(\\pi /2\\)','(c)\\(3\\pi /4\\)','(d)\\(\\pi \\)   ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_0^{\\pi /2} {\\frac{{{{\\sin }^{2/3}}x}}{{{{\\sin }^{2/3}}x + {{\\cos}^{2/3}}x}}dx} \\)or \\(I = \\int_0^{\\pi /2} {\\frac{{{{\\sin }^{2/3}}\\left( {\\frac{\\pi }{2} - x}\\right)}}{{{{\\sin }^{2/3}}\\left( {\\frac{\\pi }{2} - x} \\right) + {{\\cos}^{2/3}}\\left( {\\frac{\\pi }{2} - x} \\right)}}dx} \\)or \\(I = \\int_0^{\\pi /2} {\\frac{{{{\\cos }^{2/3}}x}}{{{{\\cos }^{2/3}}x + {{\\sin}^{2/3}}x}}} dx\\)Therefore, \\(2I = \\int\\limits_0^{\\pi /2} {\\frac{{({{\\sin }^{2/3}}x + {{\\cos}^{2/3}}x)}}{{({{\\sin }^{2/3}}x + {{\\cos }^{2/3}}x)}}dx} \\)\\( \\Rightarrow 2I = \\int_{\\,0}^{\\,\\pi /2} {dx} \\)\\( \\Rightarrow I =\\frac{1}{2}[x]_0^{\\pi /2}\\)\\(= \\frac{\\pi }{4}.\\)Trick: \\(\\int_0^{\\pi /2} {\\frac{{{{\\sin }^n}x}}{{{{\\sin }^n}x + {{\\cos}^n}x}}\\,} dx = \\frac{\\pi }{4}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\, - \\,1}^{\\,1} {\\log (x + \\sqrt {{x^2} + 1} )\\,dx = } \\)','(a)0','(b)log 2','(c)\\(\\log \\frac{1}{2}\\)','(d)None of these  ','1','DEFINITE INTEGRAL','2','Answer =  (a)Let \\(f(x) = \\log (x + \\sqrt {1 + {x^2}} )\\)Now,\\(f( - x) = \\log \\left( {\\sqrt {1 + {x^2}}  - x} \\right) = \\log (\\sqrt {1 +{x^2}}  - x).\\frac{{(\\sqrt {1 + {x^2}}  + x)}}{{(\\sqrt {1 + {x^2}}  + x)}}\\)\\( = \\log \\frac{{[(1 + {x^2}) - {x^2}]}}{{(\\sqrt {1 + {x^2}}  + x)}}\\)\\(= \\log 1 - \\log (\\sqrt {1 + {x^2}}  + x)\\)\\( =  - \\log (\\sqrt {1 + {x^2}}  + x)\\)\\(=  - f(x)\\)Hence, \\(\\int_{\\, - 1}^{\\,1} {\\log \\,(x + \\sqrt {1 + {x^2}} ) = 0} \\),  \\(\\left[ {\\int_{\\, - a}^{\\,a} {f(x) = 0,\\,} {\\rm{if }}f( - x) =  - f(x)} \\right].\\)',CURRENT_TIMESTAMP);
