INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,} {{e^{x - ','(a)\\({e^{1000}} - 1\\)','(b)\\(\\frac{{{e^{1000}} - 1}}{{e - 1}}\\)','(c)\\(1000(e - 1)\\)','(d)\\(\\frac{{e - 1}}{{1000}}\\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\( = \\int_{\\,0}^{\\,1} {\\,0\\,dx + } \\int_{\\,1}^{\\,\\sqrt 2 } {\\,dx} \\)\\(= [x]_1^{\\sqrt 2 } = \\sqrt 2  - 1.\\)\\({e^{x - [x]}}\\) is a periodic function with period 1.\\(\\therefore \\int_0^{Answer = } {{e^{x - [x]}}dx = Answer = \\int_0^1 {{e^{x - [x]}}dx} } ,\\)\\([[x] = 0,\\,\\,{\\rm{if}}\\,\\,0 < x < 1]\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the integral \\(\\int_{\\,\\frac{1}{n}}^{\\,\\frac{{an - 1}}{n}}{\\frac{{\\sqrt x }}{{\\sqrt {a - x}  + \\sqrt x }}dx} \\) is','(a)\\(\\frac{a}{2}\\)','(b)\\(\\frac{{na + 2}}{{2n}}\\)','(c)\\(\\frac{{na - 2}}{{2n}}\\)','(d)None of these  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_{1/n}^{\\frac{{an - 1}}{n}} {\\frac{{\\sqrt x }}{{\\sqrt {a - x}  +\\sqrt x }}dx = \\int_{1/n}^{a - \\frac{1}{n}} {\\frac{{\\sqrt x }}{{\\sqrt {a - x}  +\\sqrt x }}dx} } \\)  .....(i)\\( = Answer = {\\rm{ }}\\,[{e^x}]_0^1\\)\\(= Answer = \\,(e - 1).\\)\\( = \\int_{\\frac{1}{n}}^{a - \\frac{1}{n}} {\\frac{{\\sqrt {\\frac{1}{n} + a -\\frac{1}{n} - x} \\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,dx}}{{\\sqrt {a - \\left( {\\frac{1}{n} + a -\\frac{1}{n} - x} \\right) + } \\sqrt {\\frac{1}{n} + a - \\frac{1}{n} - x} }}} \\)\\(\\left[ {\\int_a^b {f(x)dx = \\int_a^b {f(a + b - x)\\,dx} } } \\right]\\)\\(I = \\int_{\\frac{1}{n}}^{a - \\frac{1}{n}} {\\frac{{\\sqrt {a - x} }}{{\\sqrt x  +\\sqrt {a - x} }}dx} \\).....(ii)Adding (i) and (ii), we get \\(2I = \\int_{\\,1/n}^{\\,a - (1/n)} {1\\,dx = \\left[{\\,x} \\right]_{\\,1/n}^{\\,a - \\frac{1}{n}}} \\) \\( \\Rightarrow 2I = a - \\frac{1}{n} - \\frac{1}{n} = \\frac{{na - 2}}{n}\\)(\\(\\Rightarrow I = \\frac{{na - 2}}{{2n}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,\\pi /2} {\\sin 2x\\log \\tan x\\,dx} \\) is equal to[Kerala (Engg.) ; AI CBSE ;Karnataka CET , 98]','(a)\\(\\pi \\)','(b)\\(\\pi /2\\)','(c)0','(d)\\(2\\pi \\)   ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^{\\pi /2} {\\,\\,\\,\\,\\,\\sin 2x\\log \\tan x\\,\\,dx} \\),\\(I = \\int_0^{\\pi /2} {\\sin 2\\,\\left( {\\frac{\\pi }{2} - x} \\right)\\log \\tan \\left({\\frac{\\pi }{2} - x} \\right)\\,\\,dx} ,\\)\\([\\int_0^a {f\\,(x)\\,dx = \\int_0^a {f\\,(a - x)\\,dx]} }\\)\\( = \\int_0^{\\pi /2} {\\,\\,\\,\\,\\,\\sin 2x\\log \\cot x\\,\\,dx} \\)\\(=  - \\int_0^{\\pi /2} {\\,\\,\\,\\,\\,\\sin 2x\\log \\tan x\\,\\,dx}\\)\\(\\therefore I =  - I\\,\\,\\)\\(\\Rightarrow{}\\)  2I = 0 \\( \\Rightarrow I = 0.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The integral \\(\\int_{\\, - 1/2}^{\\,1/2} {\\,\\left\\{ {','(a) \\(- \\frac{1}{2} \\)','(b) 0','(c)1','(d)\\(2\\log \\frac{1}{2}\\)   ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_{ - 1/2}^{1/2} {[x]dx + \\int_{ - 1/2}^{1/2} {\\log \\left( {\\frac{{1+ x}}{{1 - x}}} \\right)dx} } \\)If \\(f(x) = \\log \\left( {\\frac{{1 + x}}{{1 - x}}} \\right)\\), then\\(f( - x) = \\log \\left( {\\frac{{1 - x}}{{1 + x}}} \\right) =  - \\log \\left({\\frac{{1 + x}}{{1 - x}}} \\right) =  - f(x)\\)\\(\\therefore I = \\int_{ - 1/2}^{1/2} {{\\rm{     }}[x]} {\\rm{ }}dx + {\\rm{0}}\\)(being integral of odd function)\\(= \\int_{ - 1/2}^0 { - 1dx + \\int_0^{1/2} {\\,\\,\\,\\,0\\,dx} }  =  - (x)_{ - 1/2}^0= \\frac{{ - 1}}{2}.\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,2\\pi } {(\\sin x + |\\sin x|)\\,dx = } \\)','(a)0','(b)4','(c)8','(d)1    ','2','DEFINITE INTEGRAL','2','Answer =  (b)\\(\\int_0^\\pi  {2\\sin x\\,dx + \\int_\\pi ^{2\\pi } {0.\\,dx} } \\)\\( = 2\\,[ - \\cosx]_0^\\pi  + 0\\)  \\( =  - 2\\,(\\cos \\pi  - \\cos 0)\\)\\(=  - 2\\,( - 1 - 1) = 4.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{ - \\pi /2}^{\\,\\pi /2} {(3\\sin x + {{\\sin }^3}x)\\,dx} \\) is ','(a)3','(b)2','(c)0','(d)\\(\\frac{{10}}{3}\\)  ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_{ - \\pi /2}^{\\pi /2} {(3\\sin x + {{\\sin }^3}x} )\\,dx = 0\\),(( Function \\((3\\sin x + {\\sin ^3}x)\\)is an odd function).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(I = \\int_{\\,0}^{\\,1} {\\,x\\,\\left| {x - \\frac{1}{2}} \\right|\\,dx}\\) is ','(a)1/3','(b)1/4','(c)1/8','(d)None of these ','3','DEFINITE INTEGRAL','2','Answer =  (c)\\(I = \\int_0^1 {x\\,\\left| {\\,x - \\frac{1}{2}\\,} \\right|\\,dx} \\)\\( =  -\\int_0^{1/2} {x\\left( {x - \\frac{1}{2}} \\right) + \\int_{1/2}^1 {x\\left( {x -\\frac{1}{2}} \\right)} \\,dx} \\)\\(= \\int_0^{1/2} {\\left( {\\frac{1}{2}x - {x^2}} \\right)\\,dx + \\int_{1/2}^1{\\left( {{x^2} - \\frac{1}{2}x} \\right)\\,dx} }\\)\\(= \\left( {\\frac{{{x^2}}}{4} - \\frac{{{x^3}}}{3}} \\right)_0^{1/2} + \\left({\\frac{{{x^3}}}{3} - \\frac{{{x^2}}}{4}} \\right)_{1/2}^1\\)\\(= \\left( {\\frac{1}{{16}} - \\frac{1}{{24}}} \\right) + \\left( {\\frac{1}{3} -\\frac{1}{4} + \\frac{1}{{16}} - \\frac{1}{{24}}} \\right) = \\frac{1}{8}.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\int_{\\,0}^{\\,8} {\\,|x - 5|\\,dx} \\) is ','(a)17','(b)12','(c)9','(d)18   ','1','DEFINITE INTEGRAL','2','Answer =  (a)\\(I = \\int_0^8 {|x - 5|dx = \\int_0^5 { - (x - 5)dx + \\int_5^8 {(x - 5)\\,dx =17} } } \\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,2} {\\,|x - 1|\\,dx = } \\)   ','(a)0','(b)2','(c)1/2','(d)1   ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(I = \\int_0^2 {|x - 1|dx = \\int_0^1 {( - x + 1)dx + \\int_1^2 {(x - 1)\\,dx} }} \\)\\(= \\left( {\\frac{{ - {x^2}}}{2} + x} \\right)_0^1 + \\left( {\\frac{{{x^2}}}{2} -x} \\right)_1^2 = 1.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\, - \\,2}^{\\,2} {\\,\\left| {\\,','(a)1','(b)2','(c)3','(d)4  ','4','DEFINITE INTEGRAL','2','Answer =  (d) \\(\\int_{ - 2}^2 {|[x]|} \\,dx = \\int_{\\, - 2}^{\\, - 1} {\\,|[x]|dx + \\int_{ -1}^0 {|[x]|dx + \\int_0^1 {|[x]|dx| + \\int_1^2 {|[x]|dx} } } } \\)\\(= \\int_{ - 2}^{ - 1} {2dx\\,\\,}  + \\int_{ - 1}^0 {1dx + \\int_0^1 {0\\,dx + } }\\int_1^2 {1dx}\\)\\(= 2[x]_{ - 2}^{ - 1} + [x]_{ - 1}^0 + 0 + [x]_1^2\\)\\(= 2( - 1 + 2) + (0 + 1) + (2 - 1) = 2 + 1 + 1 = 4.\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\int_{\\,0}^{\\,1} {\\,{{\\tan }^{ - 1}}\\left( {\\frac{1}{{{x^2} - x + 1}}}\\right)\\,dx} \\) is ','(a)ln 2','(b)\\( - \\ln 2\\)','(c)\\(\\frac{\\pi }{2} + \\ln 2\\)','(d)\\(\\frac{\\pi }{2} - \\ln 2\\)    ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_0^1 {{{\\tan }^{ - 1}}\\left( {\\frac{1}{{{x^2} - x + 1}}} \\right)\\,dx} = \\int_0^1 {{{\\tan }^{ - 1}}x\\,dx - } \\int_0^1 {{{\\tan }^{ - 1}}(x - 1)} \\,dx\\)\\(= 2\\int_{\\,0}^{\\,1} {{{\\tan }^{ - 1}}x\\,dx}  = 2\\,[{\\tan ^{ - 1}}x -\\frac{1}{2}\\log (1 + {x^2})]_0^1 = \\frac{\\pi }{2} - \\log 2.\\)  ',CURRENT_TIMESTAMP);
