INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The correct evaluation of \\(\\int_0^\\pi  {\\left| {\\,{{\\sin }^4}x\\,} \\right|\\,dx}\\) is ','(a)\\(\\frac{{8\\pi }}{3}\\)','(b)\\(\\frac{{2\\pi }}{3}\\)','(c)\\(\\frac{{4\\pi }}{3}\\)','(d)\\(\\frac{{3\\pi }}{8}\\)    ','4','DEFINITE INTEGRAL','2','Answer =  (d)\\(\\int_0^\\pi  {{\\rm{ }}\\left| {{\\rm{ }}{{\\sin }^4}x{\\rm{ }}} \\right|{\\rm{}}dx = 2\\int_0^{\\pi /2} {{{\\sin }^4}x\\,dx} } \\)Applying gamma function, \\(2\\int_0^{\\pi /2} {{{\\sin }^4}x\\,dx}  = 2\\frac{{\\Gamma (5/2).\\Gamma(1/2)}}{{2.\\Gamma (6/2)}} = \\frac{{3\\pi }}{8}.\\)',CURRENT_TIMESTAMP);
