INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(3 + \\frac{5}{{1\\,!}} + \\frac{7}{{2\\,!}} + \\frac{9}{{3\\,!}} + .....\\infty  =\\)','(a)\\(3\\,e\\)','(b)\\(5\\,e\\)','(c)\\(5\\,e - 1\\)','(d)None of these ','2','EXPONENTIAL AND LOGARITHMIC SERIES','2','Answer =  (b)\\(3 + \\frac{5}{{1\\;!}} + \\frac{7}{{2\\;!}} + \\frac{9}{{3\\;!}} + ......\\infty\\)\\(= {e^{(a - b)/a}} = {e^{1 - (b/a)}} = {e^1}.{e^{ - b/a}} =\\frac{e}{{{e^{b/a}}}}.\\)\\({T_n} = \\frac{{2n + 1}}{{(n - 1)\\;!}} = \\frac{{2(n - 1)}}{{(n - 1)\\;!}} +\\frac{3}{{(n - 1)\\;!}}\\)\\(= \\frac{2}{{(n - 2)\\;!}} + \\frac{3}{{(n - 1)\\;!}}\\)Now, sum \\({S_n} = 2\\sum\\limits_{n = 1}^\\infty  {\\frac{1}{{(n - 2)\\;!}} +3\\sum\\limits_{n = 1}^\\infty  {\\frac{1}{{(n - 1)\\;!}}} } \\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In  the expansion of  \\(\\frac{{{e^{4x}} - 1}}{{{e^{2x}}}}\\), the coefficient of\\({x^2}\\) is','(a) \\(\\frac{1}{2}\\)','(b) 1','(c)0','(d)None of these    ','3','EXPONENTIAL AND LOGARITHMIC SERIES','2','Answer =  (c)\\(\\frac{{{e^{4x}} - 1}}{{{e^{2x}}}} = {e^{2x}} - {e^{ - 2x}} = 2\\left\\{ {2x +\\frac{{{{(2x)}^3}}}{{3\\;!}} + \\frac{{{{(2x)}^5}}}{{5\\;!}} + .....} \\right\\}\\)The coefficien\\(= 2e + 3e = 5e.\\)t of \\({x^2} = 0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(1 + \\frac{{a - bx}}{{1\\,!}} + \\frac{{{{(a - bx)}^2}}}{{2\\,!}} + \\frac{{{{(a -bx)}^3}}}{{3\\,!}} + ....\\infty  =\\)','(a)\\({e^{a - bx}}\\)','(b)\\({e^{a - bx}} - 1\\)','(c)\\(1 + a{\\log _e}(a - bx)\\)','(d)\\({e^{ - bx}}\\)  ','1','EXPONENTIAL AND LOGARITHMIC SERIES','2','Answer =  (a)\\(1 + \\frac{{(a - bx)}}{{1\\;!}} + \\frac{{{{(a - bx)}^2}}}{{2\\;!}} +\\frac{{{{(a - bx)}^3}}}{{3\\;!}} + ...... = {e^{a - bx}}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If  \\(y =  - \\left( {{x^3} + \\frac{{{x^6}}}{2} + \\frac{{{x^9}}}{3} + .....}\\right)\\), then \\(x = \\) ','(a)\\(\\frac{{1 + {e^y}}}{3}\\)','(b)\\(\\frac{{1 - {e^y}}}{3}\\)','(c)\\({(1 - {e^y})^{1/3}}\\)','(d)\\({(1 - {e^y})^3}\\) ','3','EXPONENTIAL AND LOGARITHMIC SERIES','2','Answer =  (c)\\(y =  - \\left\\{ {{x^3} + \\frac{{{{({x^3})}^2}}}{2} +\\frac{{{{({x^3})}^3}}}{3} + ......} \\right\\} = \\log (1 - {x^3})\\)    \\(\\Rightarrow {e^y} = 1 - {x^3} \\Rightarrow x = {(1 - {e^y})^{1/3}}.\\)',CURRENT_TIMESTAMP);
