INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{1}{2} + \\frac{1}{4} + \\frac{1}{{8(2)\\,!}} + \\frac{1}{{16\\,(3)\\,!}} +\\frac{1}{{32(4)\\,!}} + ......\\infty  =\\)','(a)\\(e\\)','(b)\\(\\sqrt e \\)','(c)\\(\\frac{{\\sqrt e }}{2}\\)','(d)None of these    ','3','EXPONENTIAL AND LOGARITHMIC SERIES','2','Answer =  (c)\\(S = \\frac{1}{2}\\left\\{ {1 + \\frac{{\\left( {\\frac{1}{2}} \\right)}}{{1!}} +\\frac{{{{\\left( {\\frac{1}{2}} \\right)}^n}}}{{2!}} + ....} \\right\\} =\\frac{1}{2}{e^{1/2}} = \\frac{{\\sqrt e }}{2}\\).    ',CURRENT_TIMESTAMP);
