INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If  A  and B  be symmetric matrices of the same order, then \\(AB - BA\\) will bea','(a)Symmetric matrix','(b)Skew symmetric matrix','(c)Null matrix','(d)None of these    ','2','DETERMINANTS AND MATRICES','2','Answer =  (b)Since \\(A,B\\) are symmetric \\( \\Rightarrow \\) \\(A = A\\) and \\(B = B\\)\\(\\therefore \\)\\((AB - BA) = (AB) - (BA) = BA - AB\\)\\(=  - (AB - BA) =  - (AB - BA)\\)\\( \\Rightarrow \\) \\((AB - BA)\\) is skew-symmetric.   ',CURRENT_TIMESTAMP);
