INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' An orthogonal matrix is','(a)\\(\\left[ {\\begin{array}{*{20}{c}}{\\cos \\alpha }&{2\\sin \\alpha }\\\\{ - 2\\sin \\alpha }&{\\cos \\alpha }\\end{array}} \\right]\\)','(b)\\(\\left[ {\\begin{array}{*{20}{c}}{\\cos \\alpha }&{\\sin \\alpha }\\\\{ - \\sin \\alpha }&{\\cos \\alpha }\\end{array}} \\right]\\)','(c)\\(\\left[ {\\begin{array}{*{20}{c}}{\\cos \\alpha }&{\\sin \\alpha }\\\\{\\sin \\alpha }&{\\cos \\alpha }\\end{array}} \\right]\\)','(d)\\(\\left[ {\\begin{array}{*{20}{c}}1&1\\\\1&1\\end{array}} \\right]\\)    ','2','DETERMINANTS AND MATRICES','2','Answer =  (b)A square matrix is to be orthogonal matrix if \\(AA = I = AA\\)\\( \\Rightarrow \\)\\(A = \\left[ {\\begin{array}{*{20}{c}}{\\cos \\alpha }&{\\sin \\alpha }\\\\{ - \\sin \\alpha }&{\\cos \\alpha }\\end{array}} \\right]\\)\\( \\Rightarrow \\)\\(AA = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\0&1\\end{array}} \\right],\\,AA = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\0&1\\end{array}} \\right]\\) \\(A = \\left[ {\\begin{array}{*{20}{c}}{\\cos \\alpha }&{ - \\sin \\alpha }\\\\{\\sin \\alpha }&{\\cos \\alpha }\\end{array}} \\right],\\) (\\(\\therefore \\)\\(AA = AA = I.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(A = \\left','(a)\\(\\frac{1}{{ab - cd}}\\left[ {\\begin{array}{*{20}{c}}b&{ - c}\\\\{ - d}&a\\end{array}} \\right]\\)','(b)\\(\\frac{1}{{ad - bc}}\\left[ {\\begin{array}{*{20}{c}}b&{ - c}\\\\{ - d}&a\\end{array}} \\right]\\)','(c)\\(\\frac{1}{{ab - cd}}\\left[ {\\begin{array}{*{20}{c}}b&d\\\\c&a\\end{array}} \\right]\\)','(d)None of these ','1','DETERMINANTS AND MATRICES','2','Answer =  (a)\\({A^{ - 1}} = \\frac{{adj\\,A}}{{|A|}}\\)But \\(|A| = \\left| {\\,\\begin{array}{*{20}{c}}a&c\\\\d&b\\end{array}\\,} \\right| = ab - cd\\) and \\(adj\\,A = \\left[{\\begin{array}{*{20}{c}}b&{ - c}\\\\{ - d}&a\\end{array}} \\right]\\)therefore \\({A^{ - 1}} = \\frac{1}{{ab - cd}}\\left[ {\\begin{array}{*{20}{c}}b&{ - c}\\\\{ - d}&a\\end{array}} \\right]\\).    ',CURRENT_TIMESTAMP);
