INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If a matrix A is such that \\(3{A^3} + 2{A^2} + 5A + I = 0,\\) then its inverse is','(a)\\( - (3{A^2} + 2A + 5I)\\)','(b)\\(3{A^2} + 2A + 5I\\)','(c)\\(3{A^2} - 2A - 5I\\)','(d)None of these ','1','DETERMINANTS AND MATRICES','2','Answer =  (a)\\(3{A^3} + 2{A^2} + 5A + I = 0 \\Rightarrow I =  - 3{A^3} - 2{A^2} - 5A\\)\\( \\Rightarrow \\)\\(I{A^{ - 1}} =  - 3{A^2} - 2A - 5I\\) \\( \\Rightarrow \\)\\({A^{ - 1}} =  - (3{A^2} + 2A + 5I)\\) ',CURRENT_TIMESTAMP);
