INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For non zero, \\(a,b,c\\) if \\(\\Delta  = \\left| {\\,\\begin{array}{*{20}{c}}{1 + a}&1&1\\\\1&{1 + b}&1\\\\1&1&{1 + c}\\end{array}} \\right| = 0\\), then the value of \\(\\frac{1}{a} + \\frac{1}{b} +\\frac{1}{c} = \\) ','(a)abc','(b)\\(\\frac{1}{{abc}}\\)','(c)\\( - (a + b + c)\\)','(d)None of these ','4','DETERMINANTS AND MATRICES','2','Answer =  (d)\\(\\Delta  = \\left| {\\,\\begin{array}{*{20}{c}}{1 + a}&1&1\\\\1&{1 + b}&1\\\\1&1&{1 + c}\\end{array}\\,} \\right|\\)= \\(abc\\,\\left| {\\,\\begin{array}{*{20}{c}}{\\frac{1}{a} + 1}&{\\frac{1}{b}}&{\\frac{1}{c}}\\\\{\\frac{1}{a}}&{\\frac{1}{b} + 1}&{\\frac{1}{c}}\\\\{\\frac{1}{a}}&{\\frac{1}{b}}&{\\frac{1}{c} + 1}\\end{array}\\,} \\right|\\), by \\(\\begin{array}{l}{C_1} \\to \\frac{1}{a}{C_1}\\\\{C_2} \\to \\frac{1}{b}{C_2}\\\\{C_3} \\to \\frac{1}{c}{C_3}\\end{array}\\)=\\(abc\\,\\left| {\\,\\begin{array}{*{20}{c}}{\\left( {1 + \\sum \\frac{1}{a}} \\right)}&{\\frac{1}{b}}&{\\frac{1}{c}}\\\\{\\left( {1 + \\sum \\frac{1}{a}} \\right)}&{\\frac{1}{b} + 1}&{\\frac{1}{c}}\\\\{\\left( {1 + \\sum \\frac{1}{a}} \\right)}&{\\frac{1}{b}}&{\\frac{1}{c} + 1}\\end{array}\\,} \\right|\\)by \\({C_1} \\to {C_1} + {C_2} + {C_3}\\)=\\(abc\\,\\left( {1 + \\sum \\frac{1}{a}} \\right)\\,\\left| {\\,\\begin{array}{*{20}{c}}1&{\\frac{1}{b}}&{\\frac{1}{c}}\\\\1&{\\frac{1}{b} + 1}&{\\frac{1}{c}}\\\\1&{\\frac{1}{b}}&{\\frac{1}{c} + 1}\\end{array}\\,} \\right|\\)\\([{\\rm{By taking }}\\left( {\\sum \\frac{1}{a} + 1} \\right)\\,{\\rm{as common]}}\\)\\(\\Delta  = abc\\,\\left( {1 + \\sum \\frac{1}{a}} \\right)\\,\\left|{\\,\\begin{array}{*{20}{c}}1&{\\frac{1}{b}}&{\\frac{1}{c}}\\\\0&1&0\\\\0&0&1\\end{array}\\,} \\right|\\), by \\(\\begin{array}{l}{R_2} \\to {R_2} - {R_1}\\\\{R_3} \\to {R_3} - {R_1}\\end{array}\\)= \\(abc\\,\\left( {1 + \\frac{1}{a} + \\frac{1}{b} + \\frac{1}{c}} \\right)\\,.\\,1,\\,\\)(by expansion along \\({C_1}\\))Therefore,\\(\\Delta  = 0\\)\\( \\Rightarrow {\\rm{Either}}\\,{\\rm{  }}abc = 0\\)or \\(1+ \\frac{1}{a} + \\frac{1}{b} + \\frac{1}{c} = 0\\)But \\(a,b,c\\) are non-zero and hence the product \\(abc\\) cannot be zero. So theonly alternative is that \\(\\frac{1}{a} + \\frac{1}{b} + \\frac{1}{c} =  - 1\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\left| {\\,\\begin{array}{*{20}{c}}a&b&c\\\\b&c&a\\\\c&a&b\\end{array}\\,} \\right| = k(a + b + c)({a^2} + {b^2} + {c^2}\\) \\( - bc - ca -ab)\\), then k =','(a)1','(b)2','(c)- 1','(d)- 2 ','3','DETERMINANTS AND MATRICES','2','Answer =  (c)\\(\\left| {\\,\\begin{array}{*{20}{c}}a&b&c\\\\b&c&a\\\\c&a&b\\end{array}\\,} \\right| = a(bc - {a^2}) - b({b^2} - ca) + c(ab - {c^2})\\)= \\( - {a^3} - {b^3} - {c^3} + 3abc\\) = \\( - 1\\,[{a^3} + {b^3} + {c^3} - 3abc]\\)= \\( - [(a + b + c)\\,({a^2} + {b^2} + {c^2} - ab - bc - ca)]\\)\\(\\Rightarrow{}\\)\\(k =  - 1\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(a \\ne p,b \\ne q,c \\ne r\\) and \\(\\left| {\\,\\begin{array}{*{20}{c}}p&b&c\\\\{p + a}&{q + b}&{2c}\\\\a&b&r\\end{array}\\,} \\right|\\) = 0, then \\(\\frac{p}{{p - a}} + \\frac{q}{{q - b}} + \\frac{r}{{r - c}} = \\)','(a)3','(b)2','(c)1','(d)0  ','2','DETERMINANTS AND MATRICES','2','Answer =  (b)\\(\\Delta  = \\left| {\\,\\begin{array}{*{20}{c}}p&b&c\\\\{p + a}&{q + b}&{2c}\\\\a&b&r\\end{array}\\,} \\right|\\, = 0\\)    Applying\\({R_2} \\to {R_2} - {R_1}\\)= \\(\\left| {\\,\\begin{array}{*{20}{c}}{p\\,\\,\\,}&{b\\,\\,}&c\\\\{a\\,\\,\\,}&{q\\,\\,}&c\\\\{a\\,\\,\\,}&{b\\,\\,}&r\\end{array}\\,} \\right|\\, = 0\\)Applying \\({R_2} \\to {R_2} - {R_1}\\) and \\({R_3} \\to {R_3} - {R_1}\\)\\(\\left| {\\,\\begin{array}{*{20}{c}}p&b&c\\\\{a - p}&{q - b}&0\\\\{a - p}&0&{r - c}\\end{array}\\,} \\right|\\, = 0\\)On expansion we get,\\(p\\,(q - b)(r - c) - b(a - p)(r - c) - c(q - b)(a - p) = 0\\)\\(\\Rightarrow{}\\) \\((p - a)(q - b)(r - c)\\)\\(\\left[ {\\frac{p}{{(p - a)}} +\\frac{b}{{(q - b)}} + \\frac{c}{{(r - c)}}} \\right] = 0\\)\\(\\Rightarrow{}\\) \\((p - a)(q - b)(r - c)\\)\\(\\left[ {\\frac{p}{{(p - a)}} +\\frac{q}{{(q - b)}} - 1 + \\frac{r}{{(r - c)}} - 1} \\right] = 0\\)\\(p \\ne a,\\,q \\ne b,\\,r \\ne c\\)  \\(\\therefore \\)\\(\\frac{p}{{p - a}} + \\frac{q}{{q - b}} + \\frac{r}{{r - c}} = 2.\\)',CURRENT_TIMESTAMP);
