INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(A = \\left','(a)\\({A^3} + 3{A^2} + A - 9{I_3} = O\\)','(b)\\({A^3} - 3{A^2} + A + 9{I_3} = O\\)','(c)\\({A^3} + 3{A^2} - A + 9{I_3} = O\\)','(d)\\({A^3} - 3{A^2} - A + 9{I_3} = O\\)   ','4','DETERMINANTS AND MATRICES','2','Answer =  (d)\\(A = \\left[ {\\begin{array}{*{20}{c}}1&2&1\\\\0&1&{ - 1}\\\\3&{ - 1}&1\\end{array}} \\right]\\)\\({A^2} = A\\,.\\,A = \\left[ {\\begin{array}{*{20}{c}}1&2&1\\\\0&1&{ - 1}\\\\3&{ - 1}&1\\end{array}} \\right]\\,\\left[ {\\begin{array}{*{20}{c}}1&2&1\\\\0&1&{ - 1}\\\\3&{ - 1}&1\\end{array}} \\right]\\,\\)\\(= \\left[ {\\begin{array}{*{20}{c}}4&3&0\\\\{ - 3}&2&{ - 2}\\\\6&4&5\\end{array}} \\right]\\)\\(A\\,.\\,{A^2} = \\left[ {\\begin{array}{*{20}{c}}1&2&1\\\\0&1&{ - 1}\\\\3&{ - 1}&1\\end{array}} \\right]\\,\\left[ {\\begin{array}{*{20}{c}}4&3&0\\\\{ - 3}&2&{ - 2}\\\\6&4&5\\end{array}} \\right]\\, = \\left[ {\\begin{array}{*{20}{c}}4&{11}&1\\\\{ - 9}&{ - 2}&{ - 7}\\\\{21}&{11}&7\\end{array}} \\right]\\)\\(\\Rightarrow{}\\) \\({A^3} - 3{A^2} - A + 9{I_3} = 0\\).  ',CURRENT_TIMESTAMP);
