INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\left| {\\,\\begin{array}{*{20}{c}}{1/a}&1&{bc}\\\\{1/b}&1&{ca}\\\\{1/c}&1&{ab}\\end{array}\\,} \\right| = \\)','(a)0','(b)abc','(c)1/abc','(d)None of these   ','1','DETERMINANTS AND MATRICES','2','Answer =  (a)\\(\\Delta  = \\frac{1}{a}[ab - ca] + 1\\left[ {ca.\\frac{1}{c} - \\frac{1}{b}.ab}\\right] + bc\\left[ {\\frac{1}{b} - \\frac{1}{c}} \\right]\\)\\(\\Rightarrow{}\\)\\(\\Delta  = (b - c) + 1(a - a) + (c - b)\\)\\(\\Rightarrow{}\\)\\(\\Delta  = 0\\).    ',CURRENT_TIMESTAMP);
