INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Solution of the equation \\(\\left| {\\,\\begin{array}{*{20}{c}}1&1&x\\\\{p + 1}&{p + 1}&{p + x}\\\\3&{x + 1}&{x + 2}\\end{array}\\,} \\right| = 0\\)are','(a)\\(x = 1,\\,2\\)','(b)\\(x = 2,\\,3\\)','(c)\\(x = 1,\\,p,\\,2\\)','(d)\\(x = 1,\\,2,\\, - p\\)  ','1','DETERMINANTS AND MATRICES','2','Answer =  (a)\\(A = \\left| {\\begin{array}{*{20}{c}}1&1&x\\\\{p + 1}&{p + 1}&{p + x}\\\\3&{x + 1}&{x + 2}\\end{array}\\,} \\right|\\)\\(|A|\\, = 0\\,{\\rm{for}}\\,x = 1\\,{\\rm{ and }}2\\). So option (a) is correct. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The values of the determinant \\(\\left| {\\,\\begin{array}{*{20}{c}}1&{\\cos (\\alpha  - \\beta )}&{\\cos \\alpha }\\\\{\\cos (\\alpha  - \\beta )}&1&{\\cos \\beta }\\\\{\\cos \\alpha }&{\\cos \\beta }&1\\end{array}\\,} \\right|\\) is ','(a)\\({\\alpha ^2} + {\\beta ^2}\\)','(b)\\({\\alpha ^2} - {\\beta ^2}\\)','(c)1','(d)0 ','4','DETERMINANTS AND MATRICES','2','Answer =  (d)On solving the determinant,\\(1\\,(1 - {\\cos ^2}\\beta ) - \\cos (\\alpha  - \\beta )\\)\\([\\cos (\\alpha  - \\beta ) - \\cos \\alpha \\cos \\beta ]\\)\\(+ \\cos \\alpha [\\cos \\beta \\cos (\\alpha  - \\beta ) - \\cos \\alpha ]\\)\\( = 1 - {\\cos ^2}\\beta  - {\\cos ^2}\\alpha  - {\\cos ^2}(\\alpha  - \\beta )\\)\\(+ 2\\cos \\alpha \\cos \\beta \\cos (\\alpha  - \\beta )\\)= \\(1 - {\\cos ^2}\\beta  - {\\cos ^2}\\alpha  + \\cos (\\alpha  - \\beta )\\)\\((2\\cos \\alpha \\cos \\beta  - \\cos (\\alpha  - \\beta ))\\)= \\(1 - {\\cos ^2}\\beta  - {\\cos ^2}\\alpha  + \\cos (\\alpha  - \\beta )\\)\\([\\cos (\\alpha  + \\beta ) + \\cos (\\alpha  - \\beta ) - \\cos (\\alpha  - \\beta )]\\)= \\(1 - {\\cos ^2}\\beta  - {\\cos ^2}\\alpha  + \\cos (\\alpha  - \\beta )\\cos (\\alpha + \\beta )\\)= \\(1 - {\\cos ^2}\\beta  - {\\cos ^2}\\alpha  + {\\cos ^2}\\alpha {\\cos ^2}\\beta  -{\\sin ^2}\\alpha {\\sin ^2}\\beta \\)= \\(1 - {\\cos ^2}\\beta  - {\\cos ^2}\\alpha (1 - {\\cos ^2}\\beta ) - {\\sin^2}\\alpha {\\sin ^2}\\beta \\)= \\(1 - {\\cos ^2}\\beta  - {\\cos ^2}\\alpha {\\sin ^2}\\beta  - {\\sin ^2}\\alpha{\\sin ^2}\\beta \\)= \\(1 - {\\cos ^2}\\beta  - {\\sin ^2}\\beta \\) = 0. ',CURRENT_TIMESTAMP);
