INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The values of x in the following determinant equation, \\(\\left|{\\,\\begin{array}{*{20}{c}}{a + x}&{a - x}&{a - x}\\\\{a - x}&{a + x}&{a - x}\\\\{a - x}&{a - x}&{a + x}\\end{array}\\,} \\right| = 0\\) are ','(a)\\(x = 0,x = 4a\\)','(b)\\(x = 0,x = a\\)','(c)\\(x = 0,x = 2a\\)','(d)\\(x = 0,x = 3a\\) ','4','DETERMINANTS AND MATRICES','2','Answer =  (d)Trick: Putting \\(x = 0\\) and \\(x = 3a,\\) the value of determinant becomeszero.  ',CURRENT_TIMESTAMP);
