INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(M = \\left','(a)- 2','(b)2','(c)- 4','(d)4   ','4','DETERMINANTS AND MATRICES','2','Answer =  (d)\\({M^2} - \\lambda M - {I_2} = 0\\)\\(\\Rightarrow \\,\\,\\left[ {\\begin{array}{*{20}{c}}1&2\\\\2&3\\end{array}} \\right]\\left[ {\\begin{array}{*{20}{c}}1&2\\\\2&3\\end{array}} \\right] - \\left[ {\\begin{array}{*{20}{c}}\\lambda &{2\\lambda }\\\\{2\\lambda }&{3\\lambda }\\end{array}} \\right] - \\left[ {\\begin{array}{*{20}{c}}1&0\\\\0&1\\end{array}} \\right] = O\\)\\(\\Rightarrow \\,\\,\\left[ {\\begin{array}{*{20}{c}}5&8\\\\8&{13}\\end{array}} \\right] - \\left[ {\\begin{array}{*{20}{c}}\\lambda &{2\\lambda }\\\\{2\\lambda }&{3\\lambda }\\end{array}} \\right] - \\left[ {\\begin{array}{*{20}{c}}1&0\\\\0&1\\end{array}} \\right] = O\\)\\(\\Rightarrow \\,\\,\\left[ {\\begin{array}{*{20}{c}}{5 - \\lambda }&{8 - 2\\lambda }\\\\{8 - 2\\lambda }&{13 - 3\\lambda }\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\0&1\\end{array}} \\right]\\)\\(\\Rightarrow{}\\) \\(5 - \\lambda  = 1,\\,\\,8 - 2\\lambda  = 0,\\,\\,13 - 3\\lambda  =1\\)\\(\\Rightarrow{}\\) \\(\\lambda  = 4\\), which satisfies all the three equations.    ',CURRENT_TIMESTAMP);
