INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For the matrix \\(A = \\left','(a)\\({A^3} + 3{A^2} - I = O\\)','(b)\\({A^3} - 3{A^2} - I = O\\)','(c)\\({A^3} + 2{A^2} - I = O\\)','(d)\\({A^3} - {A^2} + I = O\\)    ','2','DETERMINANTS AND MATRICES','2','Answer =  (b)\\({A^2} = AA = \\left[ {\\,\\begin{array}{*{20}{c}}1&1&0\\\\1&2&1\\\\2&1&0\\end{array}\\,} \\right]\\,\\left[ {\\,\\begin{array}{*{20}{c}}1&1&0\\\\1&2&1\\\\2&1&0\\end{array}\\,} \\right]\\)= \\(\\left[ {\\,\\begin{array}{*{20}{c}}2&3&1\\\\5&6&2\\\\3&4&1\\end{array}\\,} \\right]\\)\\(\\Rightarrow{}\\)\\({A^3} = {A^2}A = \\left[ {\\,\\begin{array}{*{20}{c}}2&3&1\\\\5&6&2\\\\3&4&1\\end{array}\\,} \\right]\\,\\,\\left[ {\\,\\begin{array}{*{20}{c}}1&1&0\\\\1&2&1\\\\2&1&0\\end{array}\\,} \\right]\\)=\\(\\left[ {\\,\\begin{array}{*{20}{c}}7&9&3\\\\{15}&{19}&6\\\\9&{12}&4\\end{array}\\,} \\right]\\)Here,\\({A^3} - 3{A^2} = \\left[ {\\,\\begin{array}{*{20}{c}}1&0&0\\\\0&1&0\\\\0&0&1\\end{array}\\,} \\right]\\, = I\\)\\(\\Rightarrow{}\\) \\({A^3} - 3{A^2} - I = 0\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The matrix \\(\\left','(a)\\(\\lambda  \\ne  - 2\\)','(b)\\(\\lambda  \\ne 2\\)','(c)\\(\\lambda  \\ne 3\\)','(d)\\(\\lambda  \\ne  - 3\\) ','1','DETERMINANTS AND MATRICES','2','Answer =  (a)The given matrix \\(A = \\left[ {\\begin{array}{*{20}{c}}2&\\lambda &{ - 4}\\\\{ - 1}&3&4\\\\1&{ - 2}&{ - 3}\\end{array}} \\right]\\) is non singular, if \\(|A|\\, \\ne 0\\)\\(|A|\\,\\, = \\left| {\\,\\begin{array}{*{20}{c}}2&\\lambda &{ - 4}\\\\{ - 1}&3&4\\\\1&{ - 2}&{ - 3}\\end{array}\\,} \\right|\\,\\)=\\(\\left| {\\,\\begin{array}{*{20}{c}}1&{\\lambda  + 3}&0\\\\{ - 1}&3&4\\\\1&{ - 2}&{ - 3}\\end{array}\\,} \\right|\\,\\),\\([{R_1} \\to {R_2} + {R_1}]\\)= \\(\\left| {\\,\\begin{array}{*{20}{c}}1&{\\lambda  + 3}&0\\\\0&1&1\\\\0&{ - \\lambda  - 5}&{ - 3}\\end{array}\\,} \\right|\\)\\(\\left[ {\\begin{array}{*{20}{c}}{{R_2} \\to {R_2} + {R_3}}\\\\{{R_3} \\to {R_3} - {R_1}}\\end{array}} \\right]\\)= \\(1\\,( - 3 + \\lambda  + 5) \\ne 0\\)\\( \\Rightarrow \\lambda  + 2 \\ne 0\\)\\(\\Rightarrow \\lambda \\,\\, \\ne  - 2.\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(A = \\left','(a)\\(a = 4,b = 1\\)','(b)\\(a = 1,b = 4\\)','(c)\\(a = 0,b = 4\\)','(d)\\(a = 2,b = 4\\)   ','2','DETERMINANTS AND MATRICES','2','Answer =  (b)Given, \\(A = \\left[ {\\begin{array}{*{20}{c}}1&{ - 1}\\\\2&{ - 1}\\end{array}} \\right],\\,\\,B = \\left[ {\\begin{array}{*{20}{c}}a&1\\\\b&{ - 1}\\end{array}} \\right]\\)\\(\\Rightarrow{}\\) \\(A + B = \\left[ {\\begin{array}{*{20}{c}}{1 + a}&0\\\\{2 + b}&{ - 2}\\end{array}} \\right]\\)\\({A^2} = \\left[ {\\begin{array}{*{20}{c}}1&{ - 1}\\\\2&{ - 1}\\end{array}} \\right]\\,\\,\\left[ {\\begin{array}{*{20}{c}}1&{ - 1}\\\\2&{ - 1}\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}{ - 1}&0\\\\0&{ - 1}\\end{array}} \\right]\\)\\({B^2} = \\left[ {\\begin{array}{*{20}{c}}a&1\\\\b&{ - 1}\\end{array}} \\right]\\,\\,\\,\\left[ {\\begin{array}{*{20}{c}}a&1\\\\b&{ - 1}\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}{{a^2} + b}&{a - 1}\\\\{ab - b}&{b + 1}\\end{array}} \\right]\\)\\({A^2} + {B^2} = \\left[ {\\begin{array}{*{20}{c}}{{a^2} + b - 1}&{a - 1}\\\\{ab - b}&b\\end{array}} \\right]\\)Also, \\({(A + B)^2} = \\left[ {\\begin{array}{*{20}{c}}{1 + a}&0\\\\{2 + b}&{ - 2}\\end{array}} \\right]\\,\\left[ {\\begin{array}{*{20}{c}}{1 + a}&0\\\\{2 + b}&{ - 2}\\end{array}} \\right]\\)\\({(A + B)^2} = \\left[ {\\begin{array}{*{20}{c}}{{{(1 + a)}^2}}&{\\,\\,\\,\\,0}\\\\{(2 + b)(1 + a) - 2(2 + b)}&{\\,\\,\\,\\,4}\\end{array}} \\right]\\)\\(\\therefore{}\\)     \\({(A + B)^2} = {A^2} + {B^2}\\)\\(\\Rightarrow{}\\) \\(\\left[ {\\begin{array}{*{20}{c}}{{{(1 + a)}^2}}&0\\\\{\\,(2 + b)(a - 1)}&4\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}{{a^2} + b - 1}&{a - 1}\\\\{ab - b}&b\\end{array}} \\right]\\)By equating, \\(a - 1 = 0 \\Rightarrow a = 1\\) and \\(b = 4\\).  ',CURRENT_TIMESTAMP);
