INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' What must be the matrix X if \\(2X + \\left','(a)\\(\\left[ {\\begin{array}{*{20}{c}}1&3\\\\2&{ - 1}\\end{array}} \\right]\\)','(b)\\(\\left[ {\\begin{array}{*{20}{c}}1&{ - 3}\\\\2&{ - 1}\\end{array}} \\right]\\)','(c)\\(\\left[ {\\begin{array}{*{20}{c}}2&6\\\\4&{ - 2}\\end{array}} \\right]\\)','(d)\\(\\left[ {\\begin{array}{*{20}{c}}2&{ - 6}\\\\4&{ - 2}\\end{array}} \\right]\\)  ','1','DETERMINANTS AND MATRICES','2','Answer =  (a)\\(2X\\, = \\left[ {\\begin{array}{*{20}{c}}3&8\\\\7&2\\end{array}} \\right] - \\left[ {\\begin{array}{*{20}{c}}1&2\\\\3&4\\end{array}} \\right]\\) \\(2X = \\left[ {\\begin{array}{*{20}{c}}2&6\\\\4&{ - 2}\\end{array}} \\right] \\Rightarrow X = \\left[ {\\begin{array}{*{20}{c}}1&3\\\\2&{ - 1}\\end{array}} \\right].\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(A + B = \\left','(a)\\(\\left[ {\\begin{array}{*{20}{c}}1&1\\\\2&1\\end{array}} \\right]\\)','(b)\\(\\left[ {\\begin{array}{*{20}{c}}{2/3}&{1/3}\\\\{1/3}&{2/3}\\end{array}} \\right]\\)','(c)\\(\\left[ {\\begin{array}{*{20}{c}}{1/3}&{1/3}\\\\{2/3}&{1/3}\\end{array}} \\right]\\)','(d)None of these ','3','DETERMINANTS AND MATRICES','2','Answer =  (c) \\(2A + 2B = \\left[ {\\begin{array}{*{20}{c}}2&0\\\\2&2\\end{array}} \\right]\\), \\(A - 2B = \\left[ {\\begin{array}{*{20}{c}}{ - 1}&1\\\\0&{ - 1}\\end{array}} \\right]\\)On adding, we get\\(3A = \\left[ {\\begin{array}{*{20}{c}}1&1\\\\2&1\\end{array}} \\right]\\) \\(\\Rightarrow \\,A = \\left[ {\\begin{array}{*{20}{c}}{1/3}&{1/3}\\\\{2/3}&{1/3}\\end{array}} \\right].\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The identity element in the group \\(M = \\left\\{ {\\left. {\\left({\\begin{array}{*{20}{c}}x&x\\\\x&x\\end{array}} \\right)} \\right|x \\in R;\\,x \\ne 0\\,} \\right\\}\\) with respect tomatrix multiplication is ','(a)\\(\\left( {\\begin{array}{*{20}{c}}1&1\\\\1&1\\end{array}} \\right)\\)','(b)\\(\\frac{1}{2}\\left( {\\begin{array}{*{20}{c}}1&1\\\\1&1\\end{array}} \\right)\\)','(c)\\(\\left( {\\begin{array}{*{20}{c}}1&0\\\\0&1\\end{array}} \\right)\\)','(d)\\(\\left( {\\begin{array}{*{20}{c}}0&1\\\\1&0\\end{array}} \\right)\\) ','2','DETERMINANTS AND MATRICES','2','Answer =  (b) Let \\(\\left[ {\\begin{array}{*{20}{c}}a&a\\\\a&a\\end{array}} \\right]\\)be the identity element then\\(\\left[ {\\begin{array}{*{20}{c}}x&x\\\\x&x\\end{array}} \\right]\\left[ {\\begin{array}{*{20}{c}}a&a\\\\a&a\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}x&x\\\\x&x\\end{array}} \\right]\\)i.e., \\(2ax = x \\Rightarrow a = \\frac{1}{2}\\), \\((x \\ne 0)\\)Identity element = \\(\\frac{1}{2}\\left[ {\\begin{array}{*{20}{c}}1&1\\\\1&1\\end{array}} \\right]\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(A = \\left','(a)\\({A^n} = nA + (n - 1)I\\)','(b)\\({A^n} = {2^{n - 1}}A + (n - 1)I\\)','(c)\\({A^n} = nA - (n - 1)I\\)','(d)\\({A^n} = {2^{n - 1}}A - (n - 1)I\\)   ','3','DETERMINANTS AND MATRICES','2','Answer =  (c)\\({A^2} = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\1&1\\end{array}} \\right]\\,\\left[ {\\begin{array}{*{20}{c}}1&0\\\\1&1\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\2&1\\end{array}} \\right]\\)\\({A^3} = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\2&1\\end{array}} \\right]\\,\\left[ {\\begin{array}{*{20}{c}}1&0\\\\1&1\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\3&1\\end{array}} \\right]\\)\\(\\therefore{}\\) \\({A^n} = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\n&1\\end{array}} \\right]\\)\\(nA = \\left[ {\\begin{array}{*{20}{c}}n&0\\\\n&n\\end{array}} \\right],(n - 1)I = \\left[ {\\begin{array}{*{20}{c}}{n - 1}&0\\\\0&{n - 1}\\end{array}} \\right]\\)  \\(nA - (n - 1)I = \\left[ {\\begin{array}{*{20}{c}}1&0\\\\n&1\\end{array}} \\right] = {A^n}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Inverse of the matrix \\(\\left','(a)\\(\\left[ {\\begin{array}{*{20}{c}}1&2&3\\\\3&3&7\\\\{ - 2}&{ - 4}&{ - 5}\\end{array}} \\right]\\)','(b)\\(\\left[ {\\begin{array}{*{20}{c}}1&{ - 3}&5\\\\7&4&6\\\\4&2&7\\end{array}} \\right]\\)','(c)\\(\\left[ {\\begin{array}{*{20}{c}}1&2&3\\\\2&5&7\\\\{ - 2}&{ - 4}&{ - 5}\\end{array}} \\right]\\)','(d)\\(\\left[ {\\begin{array}{*{20}{c}}1&{ - 3}&5\\\\7&4&6\\\\4&2&{ - 7}\\end{array}} \\right]\\)   ','3','DETERMINANTS AND MATRICES','2','Answer =  (c)Let \\(A = \\left[ {\\begin{array}{*{20}{c}}3&{ - 2}&{ - 1}\\\\{ - 4}&1&{ - 1}\\\\2&0&1\\end{array}} \\right]\\),then \\(|A| = \\left| {\\,\\begin{array}{*{20}{c}}3&{ - 2}&{ - 1}\\\\{ - 4}&1&{ - 1}\\\\2&0&1\\end{array}\\,} \\right| = 1\\)The matrix of cofactors of A= \\(\\left[ {\\begin{array}{*{20}{c}}{{c_{11}}}&{{c_{12}}}&{{c_{13}}}\\\\{{c_{21}}}&{{c_{22}}}&{{c_{23}}}\\\\{{c_{31}}}&{{c_{32}}}&{{c_{33}}}\\end{array}} \\right] = \\left[ {\\begin{array}{*{20}{c}}1&2&{ - 2}\\\\2&5&{ - 4}\\\\3&7&{ - 5}\\end{array}} \\right]\\)Therefore, \\(adj(A) = \\left[ {\\begin{array}{*{20}{c}}1&2&3\\\\2&5&7\\\\{ - 2}&{ - 4}&{ - 5}\\end{array}} \\right]\\)\\(\\therefore \\) \\({A^{ - 1}} = \\frac{1}{{|A|}}\\,.\\,adjA = \\left[{\\begin{array}{*{20}{c}}1&2&3\\\\2&5&7\\\\{ - 2}&{ - 4}&{ - 5}\\end{array}} \\right]\\) ,    (\\(\\,|A| = 1\\)).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If A  and B  are non-singular matrices, then','(a)\\({(AB)^{ - 1}} = {A^{ - 1}}{B^{ - 1}}\\)','(b)\\(AB = BA\\)','(c)\\((AB) = AB\\)','(d)\\({(AB)^{ - 1}} = {B^{ - 1}}{A^{ - 1}}\\)   ','4','DETERMINANTS AND MATRICES','2','Answer =  (d)\\({(AB)^{ - 1}} = {B^{ - 1}}{A^{ - 1}}\\). ',CURRENT_TIMESTAMP);
