INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the covariance between x and y is 10and the variance of x and y are 16 and 9 respectively, then the coefficient ofcorrelation between x and y is','(a)0.61','(b)0.79','(c)0.83','(d)0.93    ','3','CORRELATION','2','Answer =  (c)\\(Cov\\,(x,\\,y) = 10\\), \\(Var\\,(x) = 16\\), \\(Var\\,(y) = 9\\)\\(\\therefore \\)\\(r = \\frac{{Cov\\,(x,\\,y)}}{{\\sqrt {Var(x)\\,.\\,Var(y)} }} = \\frac{{10}}{{\\sqrt {16\\times 9} }} = \\frac{{10}}{{12}} = 0.83.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the variances of two variables x and y are respectively 9 and 16 and theircovariance is 8, then their coefficient of correlation is','(a)\\(\\frac{2}{3}\\)','(b)  \\(\\frac{8}{{3\\sqrt 2 }}\\)','(c)\\(\\frac{9}{{8\\sqrt 2 }}\\)','(d)  \\(\\frac{2}{9}\\)   ','1','CORRELATION','2','Answer =  (a)Coefficient of correlation\\(r = \\frac{{Cov(x,\\,y)}}{{\\sqrt {Var\\,(x)} \\,.\\,\\sqrt {Var(y)} }}\\) =\\(\\frac{8}{{\\sqrt 9 \\,\\sqrt {16} }} \\Rightarrow r = \\frac{2}{3}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\({x_1},{x_2},{x_3},.....,{x_n}\\)be the ranks of n individuals according tocharacter A and \\({y_1},{y_2},.....,{y_n}\\)the ranks of the same individualsaccording to other character B such that \\({x_i} + {y_i} = n + 1\\) for \\(i =1,2,3,.....,n.\\) Then the coefficient of rank correlation between the charactersA and B is','(a)1','(b)0','(c)-1','(d)None of these ','3','CORRELATION','2','Answer =  (c)\\({x_i} + {y_i} = n + 1\\) for all \\(i = 1,2,3.....n.\\)Let \\({x_i} - {y_i} = {d_i}\\),then \\(2{x_i} = n + 1 + {d_i}\\)\\(\\Rightarrow{}\\) \\({d_i} = 2{x_i} - (n + 1)\\)\\(\\therefore \\)\\(\\sum\\limits_{i = 1}^n {d_i^2 = } \\sum\\limits_{i = 1}^n {[2{x_i}}  - (n + 1){]^2}\\)\\(= \\sum\\limits_{i = 1}^n {[4x_i^2 + {{(n + 1)}^2} - 4(n + 1){x_i}} ]\\)\\(= 4\\sum\\limits_{i = 1}^n {x_i^2 + {{(n + 1)}^2}(n) - 4(n + 1)\\Sigma {x_i}}\\)\\(= 4\\frac{{n(n + 1)(2n + 1)}}{6} + {(n + 1)^2}n - 4(n + 1)\\frac{{n(n + 1)}}{2}\\)\\(= \\frac{{n({n^2} - 1)}}{3}\\)    \\(\\therefore \\)\\(r = 1 - \\frac{{6\\sum {d_i^2} }}{{n({n^2} - 1)}} = 1 - 2 =  - 1.\\)',CURRENT_TIMESTAMP);
