INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the sum of the coefficients in the expansion of \\({(1 - 3x + 10{x^2})^n}\\) isa and if the sum of the coefficients in the expansion of  \\({(1 + {x^2})^n}\\) isb, then ','(a)\\(a = 3b\\)','(b)\\(a = {b^3}\\)','(c)\\(b = {a^3}\\)','(d)None of these    ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)We have a = sum of the coefficient in the expansion of \\({(1 - 3x +10{x^2})^n} = {(1 - 3 + 10)^n} = {(8)^n}\\)\\(\\Rightarrow{}\\) \\({(1 - 3x + 10{x^2})^n} = {(2)^{3n}}\\),  [Putting x =1] .Now, b = sum of the coefficients in the expansion of \\({(1 + {x^2})^n} = {(1 +1)^n} = {2^n}\\). Clearly, \\(a = {b^3}\\)   ',CURRENT_TIMESTAMP);
