INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the sum of the coefficients in the expansion of \\({(\\alpha {x^2} - 2x +1)^{35}}\\) is equal to the sum of the coefficientsin the expansion of \\({(x - \\alpha y)^{35}}\\), then \\(\\alpha \\)=','(a)0','(b)1','(c)May be any real number','(d)No such value exist   ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)Accordingly,  \\({(\\alpha  - 2 + 1)^{35}} = {(1 - \\alpha )^{35}}\\)\\(\\Rightarrow{}\\) \\({(\\alpha  - 1)^{35}} = {(1 - \\alpha )^{35}} \\Rightarrow\\alpha  = 1\\)  ',CURRENT_TIMESTAMP);
