INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The least remainder when \\({17^{30}}\\) is divided by 5 is','(a)1','(b)2','(c)3','(d)4    ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d)17 = 2 (mod 5)\\({(17)^5} = {(2)^5}\\,({\\rm{mod 5)}}\\) = 2 (mod 5)\\(\\Rightarrow{}\\)  \\({({17^5})^6} = {(2)^6}\\)(mod 5) \\(\\Rightarrow{}\\) \\({(17)^{30}} = 4\\) (mod 5)Hence required remainder = 4.   ',CURRENT_TIMESTAMP);
