INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\({(\\sqrt 2  + 1)^6} - {(\\sqrt 2  - 1)^6} = \\)','(a)101','(b)\\(70\\sqrt 2 \\)','(c)\\(140\\sqrt 2 \\)','(d)\\(120\\sqrt 2 \\) ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({(x + a)^n} - {(x - a)^n}\\)\\(= 2\\,{[^n}{C_1}{x^{n - 1}}a + {\\,^n}{C_3}{x^{n - 3}}{a^3} + {\\,^n}{C_5}{x^{n -5}}{a^5} + ......]\\)\\(\\therefore{}\\) \\({(\\sqrt 2  + 1)^6} - {(\\sqrt 2  - 1)^6}\\)\\(= 2\\,{[^6}{C_1}{(\\sqrt 2 )^5}{(1)^1} + {\\,^6}{C_3}{(\\sqrt 2 )^3}{(1)^3} +{\\,^6}{C_5}{(\\sqrt 2 )^1}{(1)^5}]\\)\\(\\,\\,\\,{(\\sqrt 2  + 1)^6} - {(\\sqrt 2  - 1)^6} = 2[6 \\times 4\\sqrt 2  + 20 \\times2\\sqrt 2  + 6\\sqrt 2 ]\\)=\\(2[24\\sqrt 2  + 40\\sqrt 2  + 6\\sqrt 2 ] = Answer = sqrt 2 \\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\({x^5} + 10{x^4}a + 40{x^3}{a^2} + 80{x^2}{a^3}\\)\\(+ 80x{a^4} + 32{a^5} =\\)','(a)\\({(x + a)^5}\\)','(b)\\({(3x + a)^5}\\)','(c)\\({(x + 2a)^5}\\)','(d)\\({(x + 2a)^3}\\)    ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)Conversely,\\({(x + a)^n} = {\\,^n}{C_0} + {\\,^n}{C_1}{x^{n - 1}}a + {\\,^n}{C_2}{x^{n -2}}{a^2} + .....\\)So, \\({(x + 2a)^5} = {x^5} + 10{x^4}a + 40{x^3}{a^2} + 80{x^2}{a^3} + 80x{a^4} +32{a^5}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The formulae\\({(a + b)^m} = {a^m} + m{a^{m - 1}}b\\)\\( + \\frac{{m(m - 1)}}{{1.2}}{a^{m -2}}{b^2} + ....\\) holds when','(a)\\(b < a\\)','(b)\\(a < b\\)','(c)\\(|a|\\, < \\,|b|\\)','(d)\\(|b|\\, < \\,|a|\\)   ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d)The expression can be written as \\({a^m}\\left\\{ {{{\\left( {1 + \\frac{b}{a}}\\right)}^m}} \\right\\}\\)Hence it is valid only when \\(\\left| {\\frac{b}{a}} \\right| < 1 \\Rightarrow |b|\\,< \\,|a|\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The total number of terms in the expansion of \\({(x + a)^{} + {(x -a)^{}\\) after simplification will be','(a)202','(b)51','(c)50','(d)None of these  ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)We know  \\(\\frac{1}{2}\\left\\{ {{{(1 + a)}^n} + {{(1 - a)}^n}} \\right\\} ={\\,^n}{C_0} + {\\,^n}{C_2}{a^2} + {\\,^n}{C_4}{a^4} + .....\\)Therefore, number of terms in expansion of  \\(\\{ {(x + a)^{Answer = } + {(x -a)^{Answer = }\\} \\) is 51.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{1}{{\\sqrt {5 + 4x} }}\\)can be expanded by binomial theorem, if','(a)\\(x < 1\\)','(b)\\(|x| < 1\\)','(c)\\(|x|\\,\\, < \\frac{5}{4}\\)','(d)\\(|x| < \\frac{4}{5}\\)   ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)The given expression can be written as \\({5^{ - 1/2}}{\\left( {1 +\\frac{4}{5}x} \\right)^{ - 1/2}}\\)and it is valid only when\\(\\left| {\\frac{4}{5}x} \\right| < 1 \\Rightarrow |x| < \\frac{5}{4}\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\({(\\sqrt 5  + 1)^5} - {(\\sqrt 5  - 1)^5}\\)is','(a)252','(b)352','(c)452','(d)532 ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\({(\\sqrt 5  + 1)^5} - {(\\sqrt 5  - 1)^5}\\)\\(= 2\\left\\{ {^5{C_1}{{(\\sqrt 5 )}^4}{ + ^5}{C_3}{{(\\sqrt 5 )}^2}{ + ^5}{C_5}.1}\\right\\} = Answer = ) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the expansion of the following expression\\(1 + (1 + x) + \\)\\({(1 + x)^2} + ..... + {(1 + x)^n}\\)the coefficient of \\({x^k}(0 \\le k \\le n)\\) is','(a)\\(^{n + 1}{C_{k + 1}}\\)','(b)\\(^n{C_k}\\)','(c)\\(^n{C_{n - k - 1}}\\)','(d)None of these    ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)The expression being in G. P.\\(E = 1 + (1 + x) + {(1 + x)^2} + .... + {(1 + x)^n}\\)\\(\\frac{{{{(1 + x)}^{n + 1}} - 1}}{{(1 + x) - 1}} = {x^{ - 1}}\\{ {(1 + x)^{n + 1}}- 1\\}\\)\\(\\therefore \\,\\,\\,\\)The coefficient of x \\(^{k}\\) in E=  The coefficient of \\({x^{k + 1}}\\)in \\(\\{ {(1 + x)^{n + 1}} - 1\\} \\) \\( ={\\,^{n + 1}}{C_{k + 1}}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The larger of \\({99^{50}} + {{50}}\\) and \\({{50}}\\) is','(a)\\({99^{50}} + {100^{50}}\\)','(b)Both are equal','(c)\\({101^{50}}\\)','(d)None of these  ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)We have\\({Answer = {50}} = {(Answer = + 1)^{50}} = {Answer = {50}} + {50.Answer = {49}} +\\frac{{50.49}}{{2.1}}{Answer = {48}} + ....\\)\\(\\ldots{} \\)..(i)and\\({99^{50}} = {(Answer = - 1)^{50}} = {Answer = {50}} - {50.Answer = {49}} +\\frac{{50.\\,49}}{{2.1}}{Answer = {48}} - ..\\)\\(\\ldots{} \\)..(ii)Subtracting (ii) from (i), we get\\({Answer = {50}} - {99^{50}} = {Answer = {50}} + 2\\frac{{50.49.48}}{{1.2.3}}{Answer = {47}} >{Answer = {50}}\\)Hence  Answer = (^{50 }\\)\\(>\\) Answer = (^{50}\\)+99\\(^{50}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\({(1 + x)^n} - nx - 1\\) divisible (where \\(n \\in N\\))','(a)by \\(2x\\)','(b)by \\({x^2}\\)','(c)by \\(2{x^3}\\)','(d)All of these    ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\({(1 + x)^n} = 1 + nx + \\frac{{n(n - 1)}}{{1.2}}{x^2} + \\frac{{n(n - 1)(n -2)}}{{1.2.3}}{x^3} + ....\\)\\(\\therefore{}\\) \\({(1 + x)^n} - nx - 1 = {x^2}\\left[ {\\frac{{n(n - 1)}}{{1.2}}+ \\frac{{n(n - 1)(n - 2)}}{{1.2.3}}x + ....} \\right]\\)From above it is clear that \\({(1 + x)^n} - nx - 1\\)is divisible by x\\(^{2}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\({T_0},{T_1},{T_2},....{T_n}\\) represent the terms in the expansion of \\({(x+ a)^n}\\), then \\({({T_0} - {T_2} + {T_4} - ....)^2}\\) \\( + {({T_1} - {T_3} +{T_5} - ....)^2} = \\)','(a)\\(({x^2} + {a^2})\\)','(b)\\({({x^2} + {a^2})^n}\\)','(c)\\({({x^2} + {a^2})^{1/n}}\\)','(d)  \\({({x^2} + {a^2})^{ - 1/n}}\\)    ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)From the given condition, replacing a by ai and - ai respectively, we get\\({(x + ai)^n} = ({T_0} - {T_2} + {T_4} - ....) + i({T_1} - {T_3} + {T_5} -....)\\)  ....(i)and \\({(x - ai)^n} = ({T_0} - {T_2} + {T_4} - ....) - i({T_1} - {T_3} + {T_5} -....)\\)......(ii)Multiplying (ii) and (i) we get required resulti.e.  \\({({x^2} + {a^2})^n} = {({T_0} - {T_2} + {T_4} - ....)^2} + {({T_1} -{T_3} + {T_5} - ....)^2}\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The number of non-zero terms in the expansion of \\({(1 + 3\\sqrt 2 x)^9} + {(1 -3\\sqrt 2 x)^9}\\) is ','(a)9','(b)0','(c)5','(d)10   ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)Given expression\\(= 2\\,[1 + {\\,^9}{C_2}{(3\\sqrt 2 x)^2} + {\\,^9}{C_4}{(3\\sqrt 2 x)^4}\\)\\(+ {\\,^9}{C_6}{(3\\sqrt 2 x)^6} + {\\,^9}{C_8}{(3\\sqrt 2 x)^8}]\\)\\(\\therefore{}\\)  The number of non-zero terms is 5. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The greatest integer which divides the number \\({{} - 1\\), is','(a)100','(b)1000','(c)10000','(d)100000  ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({(1 + Answer = ^{Answer = } = 1 + Answer = Answer = + \\frac{{Answer = 99}}{{1.2}}.{(Answer = ^2}\\)\\(+ \\frac{{Answer = 99.98}}{{1.2.3}}{(Answer = ^3} + ....\\)\\({(Answer = ^{Answer = } - 1 = Answer = Answer = left[ {1 + \\frac{{Answer = 99}}{{1.2}} +\\frac{{Answer = 99.98}}{{1.2.3}}.Answer = + ....} \\right]\\)From above it is clear that,\\({(Answer = ^{Answer = } - 1\\)is divisible by (Answer = \\(^{2}\\) = Answer = 0   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The positive integer just greater than  (1 +  0.)\\(^{0}\\) is ','(a)4','(b)5','(c)2','(d)3    ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d)We know that \\(e = \\mathop {\\lim }\\limits_{n \\to \\infty } \\,{\\left( {1 +\\frac{1}{n}} \\right)^n}\\) and \\(2 < e < 3\\).\\(\\therefore{}\\) \\({(1 + 0.Answer = )^{Answer = 0}} < 3\\)   (By putting n = Answer = 0)Also \\({(1 + 0.Answer = )^{Answer = 0}} = 1 + Answer = 0 \\times {10^{ - 4}}\\)\\( + \\frac{{Answer = 0 \\times Answer = }}{{2!}} \\times {10^{ - 8}} + ......\\)uptoAnswer = 1terms\\( \\Rightarrow {(1 + 0.Answer = )^{Answer = 0}} > 2\\).  Hence 3 is the positive integerjust greater than \\({(1 + 0.Answer = )^{Answer = 0}} > 2\\).Hence (d) is the correct option.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The last digit in \\({7^{}\\) is ','(a)7','(b)9','(c)1','(d)3  ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)We have \\({7^2} = 49 = 50 - 1\\)Now, \\({7^{Answer = } = {({7^2})^{Answer = } = {(50 - 1)^{Answer = }\\)= \\(^{Answer = {C_0}{(50)^{Answer = }{( - 1)^0} +{\\,^{Answer = }{C_1}{(50)^{Answer = }{( - 1)^1} + ......\\)\\(+ {\\,^{Answer = }{C_{Answer = }{(50)^0}{( - 1)^{Answer = }\\)Thus the last digits of \\({7^{Answer = }\\) are \\(^{Answer = {C_{Answer = }.1.1\\) i.e., 1.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\({6^{th}}\\)term in expansion of \\({\\left( {2{x^2} - \\frac{1}{{3{x^2}}}}\\right)^{10}}\\) is','(a)\\(\\frac{{4580}}{{17}}\\)','(b)\\( - \\frac{{896}}{{27}}\\)','(c)\\(\\frac{{5580}}{{17}}\\) ','(d)None of these    ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)Applying \\({T_{r + 1}} = {\\,^n}{C_r}{x^{n - r}}{a^r}\\)for \\({(x + a)^n}\\)Hence \\({T_6} = {\\,^{10}}{C_5}{(2{x^2})^5}{\\left( { - \\frac{1}{{3{x^2}}}}\\right)^5}\\)\\(=  - \\frac{{10\\,!}}{{5\\,!\\,5\\,!}}32 \\times \\frac{1}{{Answer = } =  -\\frac{{Answer = }{{27}}\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the ratio of the coefficient of third and fourth term in the expansion of\\({\\left( {x - \\frac{1}{{2x}}} \\right)^n}\\) is 1 : 2, then the value of  n  willbe','(a)18','(b)16','(c)12','(d)- 10  ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d) \\({T_3} = {\\,^n}{C_2}{(x)^{n - 2}}{\\left( { - \\frac{1}{{2x}}} \\right)^2}\\)and \\({T_4} = {\\,^n}{C_3}{(x)^{n - 3}}{\\left( { - \\frac{1}{{2x}}} \\right)^3}\\)But according to the condition,\\(\\frac{{ - \\,n(n - 1) \\times 3 \\times 2 \\times 1 \\times 8}}{{n(n - 1)(n - 2)\\times 2 \\times 1 \\times 4}} = \\frac{1}{2} \\Rightarrow n =  - 10\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the coefficients of  \\({r^{th}}\\)term and \\({(r + 4)^{th}}\\)term are equal inthe expansion of \\({(1 + x)^{20}}\\), then the value of r will be','(a)7','(b)8','(c)9','(d)10    ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\(^{20}{C_{r - 1}}{ = ^{20}}{C_{r + 3}}\\)\\( \\Rightarrow \\) \\(20 - r + 1 = r +3 \\Rightarrow r = 9\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\({r^{th}}\\)term in the expansion of  \\({(a + 2x)^n}\\)is','(a)\\(\\frac{{n(n + 1)....(n - r + 1)}}{{r!}}{a^{n - r + 1}}{(2x)^r}\\)','(b)\\(\\frac{{n(n - 1)....(n - r + 2)}}{{(r - 1)\\,!}}{a^{n - r + 1}}{(2x)^{r -1}}\\)','(c)\\(\\frac{{n(n + 1)....(n - r)}}{{(r + 1)!}}{a^{n - r}}{(x)^r}\\)','(d)None of these   ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\({r^{th}}\\) term of \\({(a + 2x)^n}\\)is \\(^n{C_{r - 1}}{(a)^{n - r +1}}{(2x)^{r - 1}}\\)\\(= \\frac{{n!}}{{(n - r + 1)!(r - 1)!}}{a^{n - r + 1}}{(2x)^{r - 1}}\\)\\(= \\frac{{n(n - 1).....(n - r + 2)}}{{(r - 1)!}}{a^{n - r + 1}}{(2x)^{r - 1}}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\({16^{th}}\\) term in the expansion of \\({(\\sqrt x  - \\sqrt y )^{17}}\\) is','(a)\\(136x{y^7}\\)','(b)\\(136xy\\)','(c)\\( - 136x{y^{15/2}}\\)','(d)\\( - 136x{y^2}\\)    ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({T_{16}} = {\\,^{17}}{C_{15}}{(\\sqrt x )^2}{( - \\sqrt y )^{15}}\\)\\(=  - \\frac{{17 \\times 16}}{{2 \\times 1}} \\times x{y^{15/2}} =  - Answer = {y^{15/2}}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In \\({\\left( {\\sqrt[3]{2} + \\frac{1}{{\\sqrt[3]{3}}}} \\right)^n}\\)  if the ratioof \\({7^{th}}\\) term from the beginning to the \\({7^{th}}\\) term from the end is\\(\\frac{1}{6}\\), then  \\(n = \\)','(a)7','(b)8','(c)9','(d)None of these ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\(\\frac{1}{6} = \\frac{{^n{C_6}{{({2^{1/3}})}^{n - 6}}{{({3^{ -1/3}})}^6}}}{{^n{C_{n - 6}}{{({2^{1/3}})}^6}{{({3^{ - 1/3}})}^{n - 6}}}}\\)or \\({6^{ - 1}} = {6^{ - 4}}{.6^{n/3}} = {6^{n/3 - 4}}\\)\\(\\therefore{}\\)\\(\\frac{n}{3} - 4 =  - 1\\) \\( \\Rightarrow \\) n = 9.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If coefficient of \\({(2r + 3)^{th}}\\) and \\({(r - 1)^{th}}\\) terms in theexpansion of \\({(1 + x)^{15}}\\) are equal, then value of r is','(a)5','(b)6','(c)4','(d)3   ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)\\(^{15}{C_{2r + 2}}{ = ^{15}}{C_{r - 2}}\\)But \\(^{15}{C_{2r + 2}} = {\\,^{15}}{C_{15 - (2r + 2)}} = {\\,^{15}}{C_{13 -2r}}\\)\\(\\Rightarrow{}\\) \\(^{15}{C_{13 - 2r}} = \\,{\\,^{15}}{C_{r - 2}} \\Rightarrow r =5\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\({x^4}\\) occurs in the \\({r^{th}}\\) term in the expansion of  \\({\\left({{x^4} + \\frac{1}{{{x^3}}}} \\right)^{15}}\\), then \\(r = \\)         ','(a)7','(b)8','(c)9','(d)10 ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c) \\({T_r} = {\\,^{15}}{C_{r - 1}}{({x^4})^{16 - r}}{\\left( {\\frac{1}{{{x^3}}}}\\right)^{r - 1}} = {\\,^{15}}{C_{r - 1}}{x^{67 - 7r}}\\)\\(\\Rightarrow{}\\) \\(67 - 7r = 4 \\Rightarrow r = 9\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the \\({(r + 1)^{th}}\\) term in the expansion of \\({\\left({\\sqrt[3]{{\\frac{a}{{\\sqrt b }}}} + \\sqrt {\\frac{b}{{\\sqrt[3]{a}}}} }\\right)^{21}}\\) has the same power of a and b, then the value of r is','(a)9','(b)10','(c)8','(d)6    ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)We have \\({T_{r + 1}} = {\\,^{21}}{C_r}{\\left( {\\sqrt[3]{{\\frac{a}{{\\sqrt b}}}}} \\right)^{21 - r}}{\\left( {\\sqrt {\\frac{b}{{\\sqrt[3]{a}}}} } \\right)^r}\\)\\(= {\\,^{21}}{C_r}{a^{7 - (r/2)}}{b^{(2/3)r - (7/2)}}\\)Since the powers of a and b are the same, therefore\\(7 - \\frac{r}{2} = \\frac{2}{3}r - \\frac{7}{2} \\Rightarrow r = 9\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the third term in the binomial expansion of \\({(1 + x)^m}\\) is \\( -\\frac{1}{8}{x^2}\\),  then the rational value of m is','(a)2','(b)\\(1/2\\)','(c)3','(d)4    ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b) We have  \\({(1 + x)^m} = 1 + mx + \\frac{{m(m - 1)}}{{2!}}{x^2} + ...\\)By hypothesis, \\(\\frac{{m(m - 1)}}{2}{x^2} =  - \\frac{1}{8}{x^2}\\)\\(\\Rightarrow{}\\) \\(4{m^2} - 4m =  - 1\\) \\(\\Rightarrow{}\\) \\({(2m - 1)^2} = 0\\Rightarrow m = \\frac{1}{2}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The first 3 terms in the expansion of \\({(1 + ax)^n}\\) \\((n \\ne 0)\\) are 1, 6xand 16x\\(^{2}\\). Then the value of a and n are respectively','(a)2 and 9','(b)3 and 2','(c)2/3 and 9','(d)3/2 and \\(6\\) ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({T_1} = {}^n{C_0} = 1\\)                \\(\\ldots{} \\)..(i)\\({T_2} = {}^n{C_1}ax = 6x\\)         \\(\\ldots{} \\)..(ii)\\({T_3} = {}^n{C_2}{(ax)^2} = 16{x^2}\\)               \\(\\ldots{} \\)..(iii)From (ii), \\(\\frac{{n!}}{{\\left( {n - 1} \\right)\\,!}}a = 6\\)  \\(\\Rightarrow{}\\)\\(n\\,a\\, = \\,6\\)    \\(\\ldots{} \\)..(iv)From (iii), \\(\\frac{{n(n - 1)}}{2}\\,{a^2} = 16\\)     \\(\\ldots{} \\)..(v)Only (c) is satisfying equation (iv) and (v).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the coefficients of \\({T_r},\\,{T_{r + 1}},\\,{T_{r + 2}}\\) terms of \\({(1 +x)^{14}}\\) are in A.P., then r = ','(a)6','(b)7','(c)8','(d)9   ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d)\\({T_{r\\,}} = {\\,^{14}}{C_{r - 1}}\\,{x^{r - 1}}\\,;\\,\\,{T_{r + 1}}\\, =\\,{\\,^{14}}{C_r}\\,{x^r}\\); \\({T_{r + 2}} = {\\,^{14}}{C_{r + 1}}\\,{x^{r + 1}}\\)By the given condition  \\(2\\,{.^{14}}{C_r} = {\\,^{14}}{C_{r - 1}} +{\\,^{14}}{C_{r + 1}}\\)   \\(\\ldots{} \\)..(i)\\(\\Rightarrow{}\\)  \\(2\\,.\\frac{{14!}}{{r!\\,\\,(14 - r)!}}\\, = \\,\\frac{{14!}}{{(r- 1)!\\,\\,(15 - r)!}} + \\frac{{14!}}{{(r + 1)!\\,(13 - r)!}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{2}{{r.(r - 1)!.(14 - r).(13 - r)!}}\\)=\\(\\frac{1}{{(r - 1)!.(15 - r).(14 - r).(13 - r)!}}\\)+\\(\\frac{1}{{(r + 1)\\,r(r -1)\\,!\\,(13 - r)\\,!}}\\)\\(\\Rightarrow{}\\) \\(\\frac{2}{{r(14 - r)}} = \\frac{1}{{(15 - r)(14 - r)}} +\\frac{1}{{(r + 1)r}}\\)\\(\\Rightarrow{}\\) \\(\\frac{1}{{r(14 - r)}} - \\frac{1}{{(15 - r)(14 - r)}} =\\frac{1}{{(r + 1)r}} - \\frac{1}{{r(14 - r)}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{(15 - r) - r}}{{r(15 - r)(14 - r)}} = \\frac{{(14 - r)- (r + 1)}}{{(r + 1)r(14 - r)}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{15 - 2r}}{{15 - r}} = \\frac{{13 - 2r}}{{r + 1}}\\)\\(\\Rightarrow{}\\)  \\(15r + 15 - 2{r^2} - 2r = Answer = - 30r - 13r + 2{r^2}\\)\\(\\Rightarrow{}\\)  \\(4{r^2} - 56r + Answer = = 0\\,\\, \\Rightarrow \\,{r^2} - 14r + 45 =0\\)\\(\\Rightarrow{}\\)   \\((r - 5)(r - 9) = 0 \\Rightarrow r = 5,\\,9\\)But 5 is not given.  Hence r = 9.Trick : Put the value of r from options in equation (i),  only (d)satisfy it. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Coefficient of x in the expansion of \\({\\left( {{x^2} + \\frac{a}{x}}\\right)^5}\\) is','(a)\\(9{a^2}\\)','(b)\\(10{a^3}\\)','(c)\\(10{a^2}\\)','(d)\\(10a\\)    ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)In the expansion of \\({\\left( {{x^2} + \\frac{a}{x}} \\right)^5}\\) the generalterm is \\({T_{r + 1}} = {\\,^5}{C_r}{({x^2})^{5 - r}}{\\left( {\\frac{a}{x}}\\right)^r} = {\\,^5}{C_r}{a^r}{x^{10 - 3r}}\\)Here, exponent of x is \\(10 - 3r = 1 \\Rightarrow r = 3\\)\\(\\therefore \\)\\({T_{2 + 1}} = {\\,^5}{C_3}{a^3}x = 10{a^3}.x\\)Hence coefficient of x is \\(10{a^3}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the coefficients of \\({p^{th}}\\), \\({(p + 1)^{th}}\\) and \\({(p +2)^{th}}\\)terms in the expansion of  \\({(1 + x)^n}\\)are in A.P., then','(a)\\({n^2} - 2np + 4{p^2} = 0\\)','(b)\\({n^2} - n\\,(4p + 1) + 4{p^2} - 2 = 0\\)','(c)\\({n^2} - n\\,(4p + 1) + 4{p^2} = 0\\)','(d)None of these  ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)Coefficient of \\({p^{th}},{(p + 1)^{th}}\\)and \\({(p + 2)^{th}}\\) terms inexpansion of \\({(1 + x)^n}\\) are \\(^n{C_{p - 1}}{,^n}{C_p}{,^n}{C_{p + 1}}\\).Then  \\({2^n}{C_p} = {\\,^n}{C_{p - 1}} + {\\,^n}{C_{p + 1}}\\)\\(\\Rightarrow {n^2} - n(4p + 1) + 4{p^2} - 2 = 0\\)Trick : Let p  = 1, hence \\(^n{C_0},{\\,^n}{C_1}\\)and \\(^n{C_2}\\)are inA.P.\\(\\Rightarrow \\,\\,\\,{2.^n}{C_1} = {\\,^n}{C_0}{ + ^n}{C_2}\\,\\,\\,\\, \\Rightarrow 2n= 1 + \\frac{{n\\,(n - 1)}}{2}\\)\\(\\Rightarrow \\,\\,4n = 2 + {n^2} - n\\,\\,\\,\\, \\Rightarrow {n^2} - 5n + 2 = 0\\)which is given by (b). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the expansion of \\({\\left( {\\frac{a}{x} + bx} \\right)^{12}}\\),the coefficientof x\\(^{-10}\\) will be','(a)\\(12{a^{11}}\\)','(b)\\(12{b^{11}}a\\)','(c)\\(12{a^{11}}b\\)','(d)\\(12{a^{11}}{b^{11}}\\)    ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({\\left[ {\\exp \\frac{a}{x}} \\right]^{12 - r}} + {[\\exp bx]^r} =  - 10\\)\\(\\Rightarrow  - 12 + r + r =  - 10 \\Rightarrow r = 1\\)Then coefficient of \\({x^{ - 10}}\\)is \\(^{12}{C_1}{(a)^{11}}{(b)^1} =12{a^{11}}b\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The ratio of the coefficient of terms \\({x^{n - r}}{a^r}\\)and \\({x^r}{a^{n -r}}\\) in the binomial expansion of \\({(x + a)^n}\\)will be','(a)\\(x:a\\)','(b)\\(n:r\\)','(c)\\(x:n\\)','(d)None of these    ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d)Ratio of coefficient of \\({x^{n - r}}{a^r}\\)and \\({x^r}{a^{n - r}}\\)is= \\(\\frac{{^n{C_r}}}{{^n{C_{n - r}}}} = \\frac{{^n{C_r}}}{{^n{C_r}}} =\\frac{1}{1}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If A and B are the coefficients of \\({x^n}\\) in the expansions of \\({(1 +x)^{2n}}\\) and \\({(1 + x)^{2n - 1}}\\)respectively, then','(a)\\(A = B\\)','(b)\\(A = 2B\\)','(c)\\(2A = B\\)','(d)None of these ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\(\\frac{{{\\rm{Coefficient}}\\,{\\rm{of}}\\,{x^n}\\,{\\rm{in}}\\,{\\rm{expansion}}\\,\\,{\\rm{of}}{{(1+x)}^{2n}}}}{{{\\rm{Coefficient}}\\,{\\rm{of}}\\,{x^n}\\,{\\rm{in}}\\,{\\rm{expansion}}\\,\\,{\\rm{of}}\\,{{(1+ x)}^{2n - 1}}}}\\)\\(= \\frac{{^{2n}{C_n}}}{{^{(2n - 1)}{C_n}}} = \\frac{{(2n)!}}{{n!\\,n!}} \\times\\frac{{(n - 1)!\\,n!}}{{(2n - 1)!}}\\)\\(= \\frac{{(2n)(2n - 1)!(n - 1)!}}{{n(n - 1)!\\,\\,(2n - 1)!}} = \\frac{{2n}}{n} =2:1\\)\\(\\Rightarrow{}\\) \\(\\frac{A}{B} = \\frac{2}{1}\\) \\(\\Rightarrow{}\\) \\(A = 2B\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the expansion of \\({\\left( {{y^2} + \\frac{c}{y}} \\right)^5}\\), thecoefficient of y will be','(a)\\(20c\\)','(b)\\(10c\\)','(c)\\(10{c^3}\\)','(d)\\(20{c^2}\\) ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c) 2(5 - r) + (-1) r = 1 \\( \\Rightarrow \\) 10 -  2r - r = 1 \\( \\Rightarrow\\,r = 3\\)Thus coefficient of y is \\(^5{C_3}{c^3} = \\frac{{5 \\times 4}}{{2 \\times 1}}{c^3}= 10{c^3}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If p and q be positive, then the coefficients of \\({x^p}\\) and \\({x^q}\\) in theexpansion of \\({(1 + x)^{p + q}}\\)will be','(a)Equal','(b)Equal in magnitude but opposite in sign','(c)Reciprocal to each other','(d)None of these    ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)Coefficient of \\({x^p}\\) is \\(^{(p + q)}{C_p}\\) and coefficient of \\({x^q}\\)is \\(^{(p + q)}{C_q}\\).  But \\(^{(p + q)}{C_p} = {\\,^{(p + q)}}{C_q}\\),\\(({\\,^n}{C_r} = {\\,^n}{C_{n - r}})\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the expansion of \\({\\left( {x - \\frac{1}{x}} \\right)^6}\\),  the constant termis','(a)- 20','(b)20','(c)30','(d)- 30    ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)In the expansion of \\({\\left( {x - \\frac{1}{x}} \\right)^6}\\), the generalterm is \\(^6{C_r}{x^{6 - r}}{\\left( { - \\frac{1}{x}} \\right)^r}{ = ^6}{C_r}{( -1)^r}{x^{6 - 2r}}\\)For term independent of \\(x,\\,\\,6 - 2r = 0 \\Rightarrow r = 3\\)Thus the required coefficient \\( = {( - 1)^3}{.^6}{C_3} =  - 20\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the expansion of \\({\\left( {{x^2} - 2x} \\right)^{10}}\\), the coefficient of\\({x^{16}}\\) is','(a)-1680','(b)1680','(c)3360','(d)6720   ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)The coefficient of \\({x^{16}}\\)in the expansion of \\({({x^2} - 2x)^{10}}\\)= The coefficient of \\({x^{16}}\\)in \\({x^{10}}{(x - 2)^{10}}\\)= The coefficient of \\({x^6}\\)in \\({(x - 2)^{10}}\\)= \\(^{10}{C_4}{.2^4},\\,\\,\\,\\,\\left( {\\,\\,{T_{r + 1}} = {\\,^n}{C_r}{x^{n -r}}{a^r}} \\right)\\)  \\(= Answer = \\times 16 = 3',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the expansion of \\({\\left( {\\frac{x}{2} - \\frac{3}{{{x^2}}}} \\right)^{10}}\\),the coefficient of \\({x^4}\\)is','(a)\\(\\frac{{405}}{{256}}\\)','(b)\\(\\frac{{504}}{{259}}\\)','(c)\\(\\frac{{450}}{{263}}\\)','(d)None of these  ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)In the expansion of \\({\\left( {\\frac{x}{2} - \\frac{3}{{{x^2}}}}\\right)^{10}}\\), the general term is \\({T_{r + 1}} = {\\,^{10}}{C_r}{\\left({\\frac{x}{2}} \\right)^{10 - r}}.\\,\\,{\\left( { - \\frac{3}{{{x^2}}}} \\right)^r}\\)\\({ = ^{10}}{C_r}{( - 1)^r}.\\frac{{{3^r}}}{{{2^{10 - r}}}}{x^{10 - r - 2r}}\\)Here, the exponent of x  is \\(10 - 3r = 4 \\Rightarrow r = 2\\)\\(\\therefore \\,\\,\\,\\,{T_{2 + 1}}{ = ^{10}}{C_2}{\\left( {\\frac{x}{{\\rm{2}}}}\\right)^8}{\\left( { - \\frac{3}{{{x^2}}}} \\right)^2} =\\frac{{10.9}}{{1.2}}.\\frac{1}{{{2^8}}}{.3^2}.{x^4}\\)= \\(\\frac{{Answer = }{{Answer = }{x^4}\\)\\(\\therefore \\) The required coefficient \\( = \\frac{{Answer = }{{Answer = }\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the coefficients of \\({5^{th}}\\), \\({6^{th}}\\)and \\({7^{th}}\\) terms in theexpansion of \\({(1 + x)^n}\\)be in A.P., then n =','(a)7 only','(b)14 only','(c)7 or 14','(d)None of these    ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)Coefficient of \\({T_5} = {\\,^n}{C_4},{T_6} = {\\,^n}{C_5}\\)and \\({T_7} ={\\,^n}{C_6}\\)According to the condition, \\(2\\,{\\,^n}{C_5} = {\\,^n}{C_4} + {\\,^n}{C_6}\\)\\(\\Rightarrow \\,\\,2\\left[ {\\frac{{n!}}{{(n - 5)!5!}}} \\right] = \\left[{\\frac{{n!}}{{(n - 4)\\,!\\,4\\,!}} + \\frac{{n!}}{{(n - 6)\\,!\\,6\\,!}}} \\right]\\)\\(\\Rightarrow \\,\\,2\\left[ {\\frac{1}{{(n - 5)\\,5}}} \\right] = \\left[ {\\frac{1}{{(n- 4)(n - 5)}} + \\frac{1}{{6 \\times 5}}} \\right]\\)After solving, we get n=7 or 14.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^7}\\) in the expansion of \\({\\left( {\\frac{{{x^2}}}{2} -\\frac{2}{x}} \\right)^8}\\) is','(a)- 56','(b)56','(c)- 14','(d)14    ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c) \\((8 - r)(2) + r( - 1) = 7 \\Rightarrow 16 - 2r - r = 7 \\Rightarrow r = 3\\)Thus coefficient of x\\(^{7}\\) is   \\(= {\\,^8}{C_3}{\\left( {\\frac{1}{2}} \\right)^5}{( - 2)^3} =  - \\frac{{8 \\times 7\\times 6}}{{3 \\times 2 \\times 1}} \\times \\frac{1}{4} =  - 14.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^{ - 7}}\\) in the expansion of \\({\\left( {ax -\\frac{1}{{b{x^2}}}} \\right)^{11}}\\) will be                       ','(a)\\(\\frac{{462{a^6}}}{{{b^5}}}\\)','(b)\\(\\frac{{462{a^5}}}{{{b^6}}}\\)','(c)\\(\\frac{{ - 462{a^5}}}{{{b^6}}}\\)','(d)\\(\\frac{{ - 462{a^6}}}{{{b^5}}}\\)   ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)For number of term,\\((11 - r)(1) + r( - 2) =  - 7 \\Rightarrow 11 - r - 2r =  - 7\\)\\(\\Rightarrow r = 6\\)Thus coefficient of x\\(^{-7}\\) is  \\(^{11}{C_6}{(a)^5}{\\left( { - \\frac{1}{b}}\\right)^6} = \\frac{{Answer = }{{{b^6}}}{a^5}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^{53}}\\) in the following expansion  \\(\\sum\\limits_{m =0}^{ {{\\,^{}{C_m}{{(x - 3)}^{- m}}} {.2^m}\\)is','(a)\\(^{100}{C_{47}}\\)','(b)\\(^{100}{C_{53}}\\)','(c)\\({ - ^{100}}{C_{53}}\\)','(d)\\({ - ^{100}}{C_{100}}\\) ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)The given sigma is expansion of\\({[(x - 3) + 2]^{Answer = } = {(x - 1)^{Answer = } = {(1 - x)^{Answer = }.\\)\\(\\therefore{}\\) \\({x^{53}}\\)will occur in \\({T_{54}}\\).\\({T_{54}} = {\\,^{Answer = }{C_{53}}{( - x)^{53}}\\)\\(\\therefore{}\\) Coefficient is - \\(^{Answer = \\)C\\(_{53}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^{32}}\\)in the expansion of \\({\\left( {{x^4} -\\frac{1}{{{x^3}}}} \\right)^{15}}\\) is','(a)\\(^{15}{C_5}\\)','(b)\\(^{15}{C_6}\\)','(c)\\(^{15}{C_4}\\)','(d)\\(^{15}{C_7}\\)  ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)Let \\({T_{r + 1}}\\) term containing x\\(^{32}\\).Therefore \\(^{15}{C_r}{x^{4r}}{\\left( {\\frac{{ - 1}}{{{x^3}}}} \\right)^{15 -r}}\\)\\(\\Rightarrow{}\\) \\({x^{4r}}{x^{ - 45 + 3r}} = {x^{32}} \\Rightarrow 7r = 77\\Rightarrow r = 11\\).Hence coefficient of x\\(^{32}\\) is \\(^{15}{C_{11}}\\) or \\(^{15}{C_4}\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the coefficients of \\({x^7}\\) and \\({x^8}\\)in \\({\\left( {2 + \\frac{x}{3}}\\right)^n}\\)are equal, then n is                       ','(a)56','(b)55','(c)45','(d)15   ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b) \\({x^7}\\),\\({x^8}\\)will occur in \\({T_8}\\)and\\({T_9}\\).Coefficients of \\({T_8}\\) and \\({T_9}\\)are equal.   \\(\\therefore {\\,^n}{C_7}{2^{n - 7}}{\\left( {\\frac{1}{3}} \\right)^7} ={\\,^n}{C_8}{2^{n - 8}}{\\left( {\\frac{1}{3}} \\right)^8}\\,\\, \\Rightarrow n = 55.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^3}\\) in the expansion of \\({\\left( {x - \\frac{1}{x}}\\right)^7}\\) is','(a)14','(b)21','(c)28','(d)35 ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\(7 - 2r = 3 \\Rightarrow r = 2\\)\\(\\therefore \\) The coefficient is \\(^7{C_2} = 21\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If in the expansion of \\({(1 + x)^m}{(1 - x)^n}\\), the coefficient of  x and \\({x^2}\\)are 3 and - 6 respectively, then m is','(a)6','(b)9','(c)12','(d)24  ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({(1 + x)^m}{(1 - x)^n}\\)\\(= \\left( {1 + mx + \\frac{{m(m - 1){x^2}}}{{2!}} + ....} \\right)\\,\\left( {1 - nx+ \\frac{{n(n - 1)}}{{2!}}{x^2} - ....} \\right)\\)\\( = 1 + (m - n)x + \\left[ {\\frac{{{n^2} - n}}{2} - mn + \\frac{{({m^2} -m)}}{2}} \\right]{x^2}\\)+.........Given, m - n = 3  or n = m - 3Hence \\(\\frac{{{n^2} - n}}{2} - mn + \\frac{{{m^2} - m}}{2} =  - 6\\)\\(\\Rightarrow{}\\) \\(\\frac{{(m - 3)(m - 4)}}{2} - m(m - 3) + \\frac{{{m^2} -m}}{2} =  - 6\\)\\(\\Rightarrow{}\\) \\({m^2} - 7m + 12 - 2{m^2} + 6m + {m^2} - m + 12 = 0\\)\\(\\Rightarrow{}\\) \\( - 2m + 24 = 0\\,\\,\\, \\Rightarrow m = 12\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\({x^m}\\)occurs in the expansion of \\({\\left( {x + \\frac{1}{{{x^2}}}}\\right)^{2n}},\\)then the coefficient of \\({x^m}\\) is ','(a)\\(\\frac{{(2n)!}}{{(m)!\\,(2n - m)!}}\\)','(b)\\(\\frac{{(2n)!\\,3!\\,3!}}{{(2n -m)!}}\\)','(c)\\(\\frac{{(2n)!}}{{\\left( {\\frac{{2n - m}}{3}} \\right)\\,!\\,\\left( {\\frac{{4n +m}}{3}} \\right)\\,!}}\\)','(d)None of these ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({T_{r + 1}} = {}^{2n}{C_r}{x^{2n - r}}{\\left( {\\frac{1}{{{x^2}}}}\\right)^r}\\) = \\({}^{2n}{C_r}{x^{2n - 3r}},\\)This contains x\\(^{m}\\),  if 2n - 3r = mi.e. if \\(r = \\frac{{2n - m}}{3}\\)\\(\\therefore{}\\) Coefficient of x\\(^{m}\\) \\( = {}^{2n}{C_r},\\) \\(r = \\frac{{2n -m}}{3}\\)= \\(\\frac{{2n!}}{{(2n - r)!r!}} = \\frac{{2n!}}{{\\left( {2n - \\frac{{2n - m}}{3}}\\right)\\,\\,!\\left( {\\frac{{2n - m}}{3}} \\right)\\,\\,!}}\\)= \\(\\frac{{2n!}}{{\\left( {\\frac{{4n + m}}{3}} \\right)\\,\\,!\\,\\,\\left( {\\frac{{2n- m}}{3}} \\right)\\,\\,!}}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If coefficients of 2\\(^{nd}\\), 3\\(^{rd}\\) and 4\\(^{th}\\) terms in the binomialexpansion of \\({(1 + x)^n}\\) are in A.P., then \\({n^2} - 9n\\) is equal to','(a)- 7','(b)7','(c)14','(d)- 14    ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d)Coefficients of 2\\(^{nd}\\), 3\\(^{rd}\\) and 4\\(^{th}\\) terms are respectively\\(^n{C_1},{\\,^n}{C_2}\\) and \\(^n{C_3}\\) are in A.P.\\(\\Rightarrow{}\\) \\({2.^n}{C_2} = {\\,^n}{C_1} + {\\,^n}{C_3}\\)\\(\\Rightarrow{}\\) \\(\\frac{{2n!}}{{2\\,!\\left( {n - 2} \\right)\\,!}} =\\frac{{n!}}{{(n - 1)!}} + \\frac{{n!}}{{3!\\,\\left( {n - 3} \\right)\\,!}}\\)On solving,  \\({n^2} - 9n + 14 = 0\\,\\, \\Rightarrow \\,\\,{n^2} - 9n =  - 14\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the expansion of \\({(1 + x + {x^3} + {x^4})^{10}},\\) the coefficient of\\({x^4}\\) is ','(a)\\(^{40}{C_4}\\)','(b)\\(^{10}{C_4}\\)','(c)210','(d)310  ','4','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (d)\\({(1 + x + {x^3} + {x^4})^{10}} = {(1 + x)^{10}}\\,{(1 + {x^3})^{10}}\\)\\( = (1 + {}^{10}{C_1}\\,.\\,x + {}^{10}{C_2}\\,.\\,{x^2} + .....)\\)\\((1 + {}^{10}{C_1}\\,.\\,{x^3} + {}^{10}{C_2}\\,.\\,{x^6} + .....)\\)\\(\\therefore \\)Coefficient of \\({x^4}\\) \\( = \\,{}^{10}{C_1}.{}^{10}{C_1} +{}^{10}{C_4} = Answer = ).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If coefficients of \\({(2r + 1)^{th}}\\) term and \\({(r + 2)^{th}}\\) term areequal in the expansion of \\({(1 + x)^{43}},\\) then the value of  r  will be','(a)14','(b)15','(c)13','(d)16   ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)Coefficient of \\({(2r + 1)^{th}}\\) term in expansion of         (1 +x)\\(^{43}\\) = \\({}^{43}{C_{2r}}\\) and coefficient of \\({(r + 2)^{th}}\\) term =coefficient of \\({\\{ (r + 1) + 1\\} ^{th}}\\) term = \\({}^{43}{C_{r + 1}}\\)According to question \\({}^{43}{C_{2r}} = {}^{43}{C_{r + 1}}\\) \\( ={}^{43}{C_{43 - (r + 1)}}\\)then \\(2r = 43 - (r + 1)\\)or \\(3r = 42\\) or \\(r = 14\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the coefficient of 4\\(^{th}\\) term in the expansion of \\({(a + b)^n}\\) is 56,then  n is ','(a)12','(b)10','(c)8','(d)6  ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({T_4} = {T_{3 + 1}}\\)\\( = \\,{}^n{C_3}\\,\\,\\,{a^{n - 3}}{b^3}\\)\\(\\Rightarrow{}\\)  \\({}^n{C_3} = 56\\) \\(\\Rightarrow{}\\)\\(\\frac{{n!}}{{3\\,!\\,\\,\\,\\left( {n - 3} \\right)\\,!}} = 56\\)\\(\\Rightarrow{}\\)  \\(n(n - 1)(n - 2) = 56.6\\) \\(\\Rightarrow{}\\) \\(n(n - 1)(n -2) = 8\\,.\\,7\\,.\\,6\\)\\(\\Rightarrow{}\\)  \\(n = 8\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^{39}}\\) in the expansion of \\({\\left( {{x^4} -\\frac{1}{{{x^3}}}} \\right)^{15}}\\) is','(a)- 455','(b)- 105','(c)105','(d)455 ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)\\({T_{r + 1}} = \\,{}^{15}{C_r}{({x^4})^{15 - r}}{( - 1/{x^3})^r}\\)= \\({( -1)^r}\\,\\,{}^{15}{C_r}{(x)^{60 - 7r}}\\)For coefficient of \\({x^{39}}\\),  \\(60 - 7r = 39 \\Rightarrow r = 3\\)\\(\\therefore{}\\) \\({T_4} = {}^{15}{C_3}{({x^4})^{12}}{( - 1/{x^3})^3}\\)= \\( -Answer = ,{x^{39}}\\)Hence the required coefficient is - ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^5}\\) in the expansion of \\({(1 + x)^{21}} + {(1 +x)^{22}} + .......... + {(1 + x)^{30}}\\) is ','(a)\\(^{51}{C_5}\\)','(b)\\(^9{C_5}\\)','(c)\\(^{31}{C_6}{ - ^{21}}{C_6}\\)','(d)\\(^{30}{C_5}{ + ^{20}}{C_5}\\)   ','3','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (c)\\({(1 + x)^{21}} + {(1 + x)^{22}} + .... + {(1 + x)^{30}}\\)\\( = {(1 + x)^{21}}\\left[ {\\frac{{{{(1 + x)}^{10}} - 1}}{{(1 + x) - 1}}}\\right]\\)= \\(\\frac{1}{x}[{(1 + x)^{31}} - {(1 + x)^{21}}]\\)\\(\\therefore{}\\) Coefficient of x\\(^{5}\\) in the given expression= Coefficient of x\\(^{5}\\) in  \\(\\left\\{ {\\frac{1}{x}[{{(1 + x)}^{31}} - {{(1 +x)}^{21}}]} \\right\\}\\)= Coefficient of x\\(^{6}\\) in \\([{(1 + x)^{31}} - {(1 + x)^{21}}]\\)= \\({}^{31}{C_6} - {}^{21}{C_6}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the coefficients of second, third and fourth term in the expansion of \\({(1 +x)^{2n}}\\) are in A.P., then \\(2{n^2} - 9n + 7\\) is equal to    ','(a)- 1','(b)0','(c)1','(d)3/2   ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\({T_2} = {}^{2n}{C_1}\\,\\,x\\), \\({T_3} = {}^{2n}{C_2}\\,\\,{x^2}\\),  \\({T_4} ={}^{2n}{C_3}\\,\\,{x^3}\\)Coefficient of T\\(_{2}\\), T\\(_{3}\\), T\\(_{4 }\\)are in A.P.\\(\\Rightarrow{}\\) \\(2.{}^{2n}{C_2} = {}^{2n}{C_1} + {}^{2n}{C_3}\\)\\(\\Rightarrow{}\\) \\(2\\frac{{2n!}}{{2\\,!\\,(2n - 2)\\,!}} = \\frac{{2n!}}{{(2n -1)\\,!}} + \\frac{{2n!}}{{3\\,!\\,(2n - 3)\\,!}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{2\\,.\\,2n(2n - 1)}}{2} = 2n + \\frac{{\\,2n(2n - 1)(2n -2)}}{6}\\)\\(\\Rightarrow{}\\) \\(n(2n - 1) = n + \\frac{{(n)(2n - 1)(2n - 2)}}{6}\\)\\(\\Rightarrow{}\\) \\(6(2{n^2} - n) = 6n + 4{n^3} - 6{n^2} + 2n\\)\\(\\Rightarrow{}\\) \\(6n(2n - 1) = 2n(2{n^2} - 3n + 4)\\)\\(\\Rightarrow{}\\) \\(6n - 3 = 2{n^2} - 3n + 4\\)\\(\\Rightarrow{}\\) \\(0 = 2{n^2} - 9n + 7\\)\\(\\Rightarrow{}\\) \\(2{n^2} - 9n + 7 =0\\).  ',CURRENT_TIMESTAMP);
