INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The coefficient of \\({x^{32}}\\) in the expansion of \\({\\left( {{x^4} -\\frac{1}{{{x^3}}}} \\right)^{15}}\\) is','(a)\\(^{15}{C_4}\\)','(b)\\(^{15}{C_3}\\)','(c)\\(^{15}{C_2}\\)','(d)\\(^{15}{C_5}\\) ','1','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (a)\\({T_{r + 1}} = {}^{15}{C_r}{({x^4})^{15 - r}}{\\left( {\\frac{{ -1}}{{{x^3}}}} \\right)^r}\\)\\(\\therefore{}\\)  \\({T_{r + 1}} = {}^{15}{C_r}\\frac{{{{(x)}^{60 - 4r}}{{( -1)}^r}}}{{{{(x)}^{3r}}}}\\)\\( = {}^{15}{C_r}{( - 1)^r}{(x)^{60 - 7r}}\\)Now putting \\(60 - 7r = 32\\)\\(\\Rightarrow{}\\) \\(60 - 32 = 7r\\)  \\(\\Rightarrow{}\\)   \\(r = \\frac{{28}}{7} =4\\)\\(\\therefore \\) Coefficient of \\({r^{32}} = {}^{15}{C_4}{( - 1)^4} ={}^{15}{C_4}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If in the expansion of \\({(1 + x)^{21}}\\), the coefficients of \\({x^r}\\) and\\({x^{r + 1}}\\) be equal, then r is equal to ','(a)9','(b)10','(c)11','(d)12 ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\({T_{r + 1}} = {\\,^{21}}{C_r}{(1)^{21 - r}}{(x)^r} = {\\,^{21}}{C_r}\\)\\(\\therefore \\)Coefficient of \\({x^r} = {\\,^{21}}{C_r}\\) and coefficient of\\({x^{r + 1}} = {\\,^{21}}{C_{r + 1}}\\)So, we must have \\(^{21}{C_r} = {\\,^{21}}{C_{r + 1}} \\Rightarrow r = 10\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The term independent of x in the expansion of  \\({\\left( {\\sqrt {\\frac{x}{3}}  +\\frac{3}{{2{x^2}}}} \\right)^{10}}\\) will be','(a)3/2','(b)5/4','(c)5/2','(d)None of these   ','2','BINOMIAL THEOREM & MATHEMATICAL INDUCTION','2','Answer =  (b)\\((10 - r)\\left( {\\frac{1}{2}} \\right) + r( - 2) = 0 \\Rightarrow 5 -\\frac{r}{2} - 2r = 0 \\Rightarrow r = 2\\)So the term independent of x\\({ = ^{10}}{C_2} \\times {\\left( {\\frac{1}{3}} \\right)^4}{\\left( {\\frac{3}{2}}\\right)^2} = \\frac{{10 \\times 9}}{{2 \\times 1}} \\times \\frac{1}{{3 \\times 3\\times 2 \\times 2}} = \\frac{5}{4}\\)  ',CURRENT_TIMESTAMP);
