INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\sec ^{ - 1}}\\left( {\\frac{{\\sqrt x  + 1}}{{\\sqrt x  - 1}}} \\right) +{\\sin ^{ - 1}}\\left( {\\frac{{\\sqrt x  - 1}}{{\\sqrt x  + 1}}} \\right)\\), then\\(\\frac{{dy}}{{dx}} = \\)','(a)0','(b)\\(\\frac{1}{{\\sqrt x  + 1}}\\)','(c)1','(d)None of these    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {\\sec ^{ - 1}}\\left( {\\frac{{\\sqrt x  + 1}}{{\\sqrt x  - 1}}} \\right) +{\\sin ^{ - 1}}\\left( {\\frac{{\\sqrt x  - 1}}{{\\sqrt x  + 1}}} \\right)\\)\\(= {\\cos ^{ - 1}}\\left( {\\frac{{\\sqrt x  - 1}}{{\\sqrt x  + 1}}} \\right) + {\\sin^{ - 1}}\\left( {\\frac{{\\sqrt x  - 1}}{{\\sqrt x  + 1}}} \\right) = \\frac{\\pi }{2}\\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = 0\\) , \\(\\left\\{ {{{\\sin }^{ - 1}}x +{{\\cos }^{ - 1}}x = \\frac{\\pi }{2}} \\right\\}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}{\\sin ^{ - 1}}(3x - 4{x^3}) = \\)','(a)\\(\\frac{3}{{\\sqrt {1 - {x^2}} }}\\)','(b)\\(\\frac{{ - 3}}{{\\sqrt {1 - {x^2}} }}\\)','(c)\\(\\frac{1}{{\\sqrt {1 - {x^2}} }}\\)','(d)\\(\\frac{{ - 1}}{{\\sqrt {1 - {x^2}} }}\\) ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Put \\(x = \\sin \\theta ,\\) we get \\(\\frac{d}{{dx}}{\\sin ^{ - 1}}(3x -4{x^3})\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {t^{4/3}} - 3{t^{ - 2/3}}\\), then \\(dy/dt\\)=','(a)\\(\\frac{{2{t^2} + 3}}{{3{t^{5/3}}}}\\)','(b)\\(\\frac{{2{t^2} + 3}}{{{t^{5/3}}}}\\)','(c)\\(\\frac{{2(2{t^2} + 3)}}{{{t^{5/3}}}}\\)','(d)\\(\\frac{{2(2{t^2} +3)}}{{3{t^{5/3}}}}\\)   ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(y = {t^{4/3}} - 3{t^{ - 2/3}}\\)    \\(\\therefore \\frac{{dy}}{{dt}} = \\frac{4}{3}{t^{1/3}} + 3 \\times \\frac{2}{3}{t^{ -5/3}} = \\frac{{4{t^2} + 6}}{{3{t^{5/3}}}} = \\frac{{2(2{t^2} + 3)}}{{3{t^{5/3}}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {x^2}\\log x + \\frac{2}{{\\sqrt x }},\\) then \\(\\frac{{dy}}{{dx}} = \\)','(a)\\(x + 2x\\log x - \\frac{1}{{\\sqrt x }}\\)','(b)\\(x + 2x\\log x -\\frac{1}{{{x^{3/2}}}}\\)','(c)\\(x + 2x\\log x - \\frac{2}{{{x^{3/2}}}}\\)','(d)None of these   ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(= \\frac{d}{{dx}}{\\sin ^{ - 1}}(\\sin 3\\theta ) = \\frac{3}{{\\sqrt {1 - {x^2}} }}.\\)\\(y = {x^2}\\log x + \\frac{2}{{\\sqrt x }}\\) \\(\\frac{{dy}}{{dx}} = 2x\\log x + x - {x^{ - 3/2}} = x + 2x\\log x -\\frac{1}{{{x^{3/2}}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left( {\\frac{{{e^x}}}{{1 + {x^2}}}} \\right) =\\)','(a)\\(\\frac{{{e^x}(1 + x)}}{{{{(1 + {x^2})}^2}}}\\)','(b)\\(\\frac{{{e^x}{{(1 -x)}^2}}}{{{{(1 + {x^2})}^2}}}\\)','(c)\\(\\frac{{{e^x}{{(1 + x)}^2}}}{{(1 + {x^2})}}\\)','(d)\\(\\frac{{{e^x}{{(1 -x)}^2}}}{{(1 + {x^2})}}\\) ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}\\left( {\\frac{{{e^x}}}{{1 + {x^2}}}} \\right) = \\frac{{(1 +{x^2}){e^x} - {e^x}(2x)}}{{{{(1 + {x^2})}^2}}} = \\frac{{{e^x}{{(1 -x)}^2}}}{{{{(1 + {x^2})}^2}}}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{\\tan x + \\cot x}}{{\\tan x - \\cot x}},\\)then \\(\\frac{{dy}}{{dx}}= \\)','(a)\\(2\\tan 2x\\sec 2x\\)','(b)\\(\\tan 2x\\sec 2x\\)','(c)\\( - \\tan 2x\\sec 2x\\)','(d)\\( - 2\\tan 2x\\sec 2x\\)  ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d) \\(y = \\frac{{\\tan x + \\cot x}}{{\\tan x - \\cot x}} =  - \\frac{{1 + {{\\tan}^2}x}}{{1 - {{\\tan }^2}x}} =  - \\sec 2x\\) \\(\\Rightarrow \\frac{{dy}}{{dx}} =  - 2\\sec 2x\\tan 2x.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(A = \\frac{{{2^x}\\cot x}}{{\\sqrt x }},\\)then \\(\\frac{{dA}}{{dx}} = \\)','(a)\\(\\frac{{{2^{x - 1}}\\left\\{ { - 2x\\,{\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}x + \\cotx.\\log \\left( {\\frac{{{4^x}}}{e}} \\right)} \\right\\}}}{{{x^{3/2}}}}\\)','(b)\\(\\frac{{{2^{x - 1}}\\left\\{ { - 2x\\cos {\\rm{e}}{{\\rm{c}}^2}x + \\cot x.\\log\\left( {\\frac{{{4^x}}}{e}} \\right)} \\right\\}}}{x}\\)','(c)\\(\\frac{{2x\\left\\{ { - 2x{\\rm{cose}}{{\\rm{c}}^2}x + \\cot x.\\log \\left({\\frac{{{4^x}}}{e}} \\right)} \\right\\}}}{{{x^{{\\rm{3/2}}}}}}\\)','(d)None of these ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)  \\(\\frac{{dA}}{{dx}} = \\frac{{\\sqrt x \\{ {2^x}{{\\log }_e}2\\cot x -{2^x}{\\rm{cose}}{{\\rm{c}}^2}x\\}  - {2^x}\\cot x\\frac{1}{{2\\sqrt x }}}}{x}\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left( {\\frac{{\\log x}}{{\\sin x}}} \\right) =\\)','(a)\\(\\frac{{\\frac{{\\sin x}}{x} - \\log x.\\cos x}}{{\\sinx}}\\)','(b)\\(\\frac{{\\frac{{\\sin x}}{x} - \\log x.\\cos x}}{{{{\\sin }^2}x}}\\)','(c)\\(\\frac{{\\sin x - \\log x.\\cos x}}{{{{\\sin }^2}x}}\\)','(d)\\(\\frac{{\\frac{{\\sinx}}{x} - \\log x}}{{{{\\sin }^2}x}}\\)  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}\\left( {\\frac{{\\log x}}{{\\sin x}}} \\right) =\\frac{{\\frac{{\\sin x}}{x} - \\log x.\\cos x}}{{{{\\sin }^2}x}}\\).   \\(= \\frac{{{2^{x - 1}}\\left\\{ { - 2x\\,{\\rm{cose}}{{\\rm{c}}^2}x + \\cot x.\\log\\left( {\\frac{{{4^x}}}{e}} \\right)} \\right\\}}}{{{x^{3/2}}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{\\sqrt {{x^2} + 1}  + \\sqrt {{x^2} - 1} }}{{\\sqrt {{x^2} + 1}  -\\sqrt {{x^2} - 1} }}\\), then \\(\\frac{{dy}}{{dx}} = \\)','(a)\\(2x + \\frac{{2{x^3}}}{{\\sqrt {{x^4} - 1} }}\\)','(b)\\(2x + \\frac{{{x^3}}}{{\\sqrt{{x^4} - 1} }}\\)','(c)\\(x + \\frac{{2{x^3}}}{{\\sqrt {{x^4} - 1} }}\\)','(d)None of these ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Rationalising, \\(y = \\frac{{2{x^2} + 2\\sqrt {{x^4} - 1} }}{2} = {x^2} +{({x^4} - 1)^{1/2}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = 2x + \\frac{{2{x^3}}}{{\\sqrt {{x^4} - 1}}}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{\\sqrt {a + x}  - \\sqrt {a - x} }}{{\\sqrt {a + x}  + \\sqrt {a -x} }}\\), then  \\(\\frac{{dy}}{{dx}} = \\)','(a)\\(\\frac{{ay}}{{x\\sqrt {{a^2} - {x^2}} }}\\)','(b)\\(\\frac{{ay}}{{\\sqrt {{a^2} -{x^2}} }}\\)','(c)\\(\\frac{{ay}}{{x\\sqrt {{x^2} - {a^2}} }}\\)','(d)None of these   ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = \\frac{{\\sqrt {a + x}  - \\sqrt {a - x} }}{{\\sqrt {a + x}  + \\sqrt {a -x} }} \\Rightarrow y = \\frac{{{{(\\sqrt {a + x}  - \\sqrt {a - x} )}^2}}}{{(a + x) -(a - x)}}\\)\\(\\Rightarrow y = \\frac{{(a + x) + (a - x) - 2(\\sqrt {{a^2} - {x^2}} )}}{{2x}}\\)\\( = \\frac{{2a - 2\\sqrt {{a^2} - {x^2}} }}{{2x}}\\) or \\(y = \\frac{{a -\\sqrt {{a^2} - {x^2}} }}{x}\\)  \\(\\ldots{} \\)..(i)Differentiating w.r.t. x of y, we get\\(\\frac{{dy}}{{dx}} = \\frac{{x\\left[ { - \\frac{1}{{2\\sqrt {{a^2} - {x^2}} }}( -2x)} \\right] - (a - \\sqrt {{a^2} - {x^2}} )}}{{{x^2}}}\\)\\(= \\frac{{{x^2} - a\\sqrt {{a^2} - {x^2}}  + {a^2} - {x^2}}}{{{x^2}\\sqrt {{a^2} -{x^2}} }} = \\frac{{a(a - \\sqrt {{a^2} - {x^2}} )}}{{{x^2}\\sqrt {{a^2} - {x^2}} }}\\)\\( = \\frac{a}{{x\\sqrt {{a^2} - {x^2}} }}\\left[ {\\frac{{a - \\sqrt {{a^2} -{x^2}} }}{x}} \\right] = \\frac{{ay}}{{x\\sqrt {{a^2} - {x^2}} }}\\) [By (i)] ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {(x{\\cot ^3}x)^{3/2}},\\)then \\(dy/dx = \\)','(a)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{1/2}}[{\\cot ^3}x - 3x{\\cot ^2}x\\cos{\\rm{e}}{{\\rm{c}}^2}x]\\)','(b)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{1/2}}[{\\cot ^2}x - 3x{\\cot^2}x\\,{\\rm{cose}}{{\\rm{c}}^2}x]\\)','(c)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{1/3}}[{\\cot ^3}x -3x\\,{\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}x]\\)','(d)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{3/2}}[{\\cot ^3}x -3x\\,{\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}x]\\)  ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {(x{\\cot ^3}x)^{3/2}}\\)\\(\\therefore \\frac{{dy}}{{dx}} = \\frac{3}{2}{(x{\\cot ^3}x)^{1/2}}[{\\cot ^3}x +3x{\\cot ^2}x( - {\\rm{cose}}{{\\rm{c}}^2}x)]\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {(x{\\cot ^3}x)^{3/2}},\\)then \\(dy/dx = \\)','(a)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{1/2}}[{\\cot ^3}x - 3x{\\cot ^2}x\\cos{\\rm{e}}{{\\rm{c}}^2}x]\\)','(b)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{1/2}}[{\\cot ^2}x - 3x{\\cot^2}x\\,{\\rm{cose}}{{\\rm{c}}^2}x]\\)','(c)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{1/3}}[{\\cot ^3}x -3x\\,{\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}x]\\)','(d)\\(\\frac{3}{2}{(x{\\cot ^3}x)^{3/2}}[{\\cot ^3}x -3x\\,{\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}x]\\)  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b) \\(\\frac{{f(2) - f(0)}}{{2 - 0}} = f(x) \\Rightarrow \\frac{{f(2) - 0}}{2} =f(x)\\)\\(\\Rightarrow \\frac{{df(x)}}{{dx}} = \\frac{{f(2)}}{2} \\Rightarrow f(x) =\\frac{{f(2)}}{2}x + c\\)\\(\\therefore f(0) = 0 \\Rightarrow c = 0\\); \\(\\therefore f(x) =\\frac{{f(2)}}{2}x\\).....(i)Given \\(|f(x)| \\le \\frac{1}{2} \\Rightarrow \\left| {\\frac{{f(2)}}{2}} \\right|\\le \\frac{1}{2}\\)\\(\\ldots{} \\)..(ii)(i) \\(\\Rightarrow{}\\) \\(|f(x)| = \\left| {\\frac{{f(2)}}{2}x} \\right| = \\left|{\\frac{{f(2)}}{2}} \\right||x| \\le \\frac{1}{2}|x|\\)[from (ii)]In [0, 2], for maximum \\(x(x = 2)\\)   \\(|f(x)| \\le \\frac{1}{2}.\\,\\,\\,2 \\Rightarrow |f(x)| \\le 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\{ \\cos (\\sin {x^2})\\}  = \\)','(a)\\(\\sin (\\sin {x^2}).\\cos {x^2}.2x\\)','(b)\\( - \\sin (\\sin {x^2}).\\cos {x^2}.2x\\)','(c)\\( - \\sin (\\sin {x^2}).{\\cos ^2}x.2x\\)','(d)None of these ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}\\{ \\cos (\\sin {x^2})\\}  =  - \\sin (\\sin {x^2})\\cos{x^2}.2x\\).    \\(= \\frac{3}{2}{(x{\\cot ^3}x)^{1/2}}[{\\cot ^3}x - 3x{\\cot^2}x\\,{\\rm{cose}}{{\\rm{c}}^2}x].\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\sin (\\sqrt {\\sin x + \\cos x} )\\), then \\(\\frac{{dy}}{{dx}} = \\) ','(a)\\(\\frac{1}{2}\\frac{{\\cos \\sqrt {\\sin x + \\cos x} }}{{\\sqrt {\\sin x + \\cos x}}}\\)','(b)\\(\\frac{{\\cos \\sqrt {\\sin x + \\cos x} }}{{\\sqrt {\\sin x + \\cos x} }}\\)','(c)\\(\\frac{1}{2}\\frac{{\\cos \\sqrt {\\sin x + \\cos x} }}{{\\sqrt {\\sin x + \\cos x}}}.(\\cos x - \\sin x)\\)','(d)None of these ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(y = \\sin (\\sqrt {\\sin x + \\cos x} )\\)  \\(\\frac{{dy}}{{dx}} = \\frac{1}{2}\\frac{{\\cos (\\sqrt {\\sin x + \\cos x} )}}{{\\sqrt{\\sin x + \\cos x} }}(\\cos x - \\sin x).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\sin \\left( {\\frac{{1 + {x^2}}}{{1 - {x^2}}}} \\right)\\), then\\(\\frac{{dy}}{{dx}} = \\)','(a)\\(\\frac{{4x}}{{1 - {x^2}}}.\\cos \\left( {\\frac{{1 + {x^2}}}{{1 - {x^2}}}}\\right)\\)','(b)\\(\\frac{x}{{{{(1 - {x^2})}^2}}}.\\cos \\left( {\\frac{{1 + {x^2}}}{{1 -{x^2}}}} \\right)\\)','(c)\\(\\frac{x}{{(1 - {x^2})}}.\\cos \\left( {\\frac{{1 + {x^2}}}{{1 - {x^2}}}}\\right)\\)','(d)\\(\\frac{{4x}}{{{{(1 - {x^2})}^2}}}.\\cos \\left( {\\frac{{1 + {x^2}}}{{1- {x^2}}}} \\right)\\)    ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(\\frac{{dy}}{{dx}} = \\cos \\left( {\\frac{{1 + {x^2}}}{{1 - {x^2}}}}\\right){\\rm{ }}\\left[ {\\frac{{(1 - {x^2})2x + (1 + {x^2})2x}}{{{{(1 -{x^2})}^2}}}} \\right]\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\sqrt {\\frac{{1 + \\tan x}}{{1 - \\tan x}}} \\), then \\(\\frac{{dy}}{{dx}}= \\) }[AISSE , 83, 84, 85; DSSE ; AI CBSE , 83]','(a)\\(\\frac{1}{2}\\sqrt {\\frac{{1 - \\tan x}}{{1 + \\tan x}}} .{\\sec ^2}\\left({\\frac{\\pi }{4} + x} \\right)\\)','(b)\\(\\sqrt {\\frac{{1 - \\tan x}}{{1 + \\tan x}}}.{\\sec ^2}\\left( {\\frac{\\pi }{4} + x} \\right)\\)','(c)\\(\\frac{1}{2}\\sqrt {\\frac{{1 - \\tan x}}{{1 + \\tan x}}} .\\sec \\left({\\frac{\\pi }{4} + x} \\right)\\)','(d)None of these    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = \\sqrt {\\left( {\\frac{{1 + \\tan x}}{{1 - \\tan x}}} \\right)} \\) o\\(= \\frac{{4x}}{{{{(1 - {x^2})}^2}}}\\cos \\left( {\\frac{{1 + {x^2}}}{{1 - {x^2}}}}\\right).\\)r \\(y = \\sqrt {\\tan \\left( {\\frac{\\pi }{4} + x} \\right)} \\)\\(\\frac{{dy}}{{dx}} = \\frac{1}{{2\\sqrt {\\tan \\left( {\\frac{\\pi }{4} + x} \\right)}}}{\\sec ^2}\\left( {\\frac{\\pi }{4} + x} \\right)\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}{({x^2} + \\cos x)^4} = \\)','(a)\\(4({x^2} + \\cos x)(2x - \\sin x)\\)','(b) \\(4{({x^2} - \\cos x)^3}(2x - \\sin x)\\)','(c)\\(4{({x^2} + \\cos x)^3}(2x - \\sin x)\\)','(d)\\(4{({x^2} + \\cos x)^3}(2x + \\sin x)\\) ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(= \\frac{1}{2}\\sqrt {\\left[ {\\frac{{1 - \\tan x}}{{1 + \\tan x}}} \\right]} {\\sec^2}\\left( {\\frac{\\pi }{4} + x} \\right).\\)\\(\\frac{d}{{dx}}{[{x^2} + \\cos x]^4} = 4{[{x^2} + \\cos x]^3}[2x - \\sin x]\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\sqrt {x\\sin x}  = \\)','(a)\\(\\frac{{\\sin x + x\\cos x}}{{2\\sqrt {x\\sin x} }}\\)','(b)\\(\\frac{{\\sin x + x\\cosx}}{{\\sqrt {x\\sin x} }}\\)','(c)\\(\\frac{{x\\sin x + \\cos x}}{{\\sqrt {2\\sin x} }}\\)','(d)\\(\\frac{{\\sin x + x\\cosx}}{{2\\sqrt {x\\sin x} }}\\) ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Let \\({y^2} = x\\sin x \\Rightarrow 2y\\frac{{dy}}{{dx}} = \\sin x + x\\cos x\\)  \\(\\therefore \\frac{{dy}}{{dx}} = \\frac{{[\\sin x + x\\cos x]}}{{2\\sqrt {x\\sin x} }}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\sqrt {{{\\sec }^2}x + {\\rm{cose}}{{\\rm{c}}^2}x}  = \\)','(a)\\(4\\cos {\\rm{ec 2}}x.\\cot 2x\\)','(b)\\( - 4\\cos {\\rm{ec 2}}x.\\cot 2x\\)','(c)\\( - 4\\cos {\\rm{ec }}x.\\cot 2x\\)','(d)None of these    ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}[\\sqrt {{{\\sec }^2}x + {\\rm{cose}}{{\\rm{c}}^2}x} ] =\\frac{d}{{dx}}\\left[ {\\sqrt {\\left( {\\frac{1}{{{{\\cos }^2}x}} + \\frac{1}{{{{\\sin}^2}x}}} \\right)} } \\right]\\) \\(= \\frac{d}{{dx}}[2\\,{\\rm{cosec}}2x] =  - 4\\,{\\rm{cosec}}2x\\cot 2x.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left( {\\frac{{\\sec x + \\tan x}}{{\\sec x - \\tan x}}} \\right) =\\)             ','(a)\\(\\frac{{2\\cos x}}{{{{(1 - \\sin x)}^2}}}\\)','(b)\\(\\frac{{\\cos x}}{{{{(1 - \\sinx)}^2}}}\\)','(c)\\(\\frac{{2\\cos x}}{{1 - \\sin x}}\\)','(d)None of these    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\frac{d}{{dx}}\\left( {\\frac{{\\sec x + \\tan x}}{{\\sec x - \\tan x}}} \\right)= \\frac{d}{{dx}}\\left( {\\frac{{1 + \\sin x}}{{1 - \\sin x}}} \\right)\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left( {{x^3}{{\\tan }^2}\\frac{x}{2}} \\right)\\)=','(a)\\({x^3}\\tan \\frac{x}{2}.{\\sec ^2}\\frac{x}{2} + 3x{\\tan ^2}\\frac{x}{2}\\)','(b)\\({x^3}\\tan \\frac{x}{2}.{\\sec ^2}\\frac{x}{2} + 3{x^2}{\\tan ^2}\\frac{x}{2}\\)','(c)\\({x^3}{\\tan ^2}\\frac{x}{2}.{\\sec ^2}\\frac{x}{2} + 3{x^2}{\\tan^2}\\frac{x}{2}\\)','(d)None of these    ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}\\left( {{x^3}{{\\tan }^2}\\frac{x}{2}} \\right) = 3{x^2}{\\tan^2}\\frac{x}{2} + 2{x^3}\\tan \\frac{x}{2}{\\sec ^2}\\frac{x}{2}.\\frac{1}{2}\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\tan ^{ - 1}}\\left( {\\frac{{{x^{1/3}} + {a^{1/3}}}}{{1 -{x^{1/3}}{a^{1/3}}}}} \\right)\\), then \\(\\frac{{dy}}{{dx}} = \\) ','(a)\\(\\frac{1}{{3{x^{2/3}}(1 + {x^{2/3}})}}\\)','(b)\\(\\frac{1}{{3{x^{2/3}}(1 +{x^{2/3}})}}\\)','(c)\\( - \\frac{1}{{3{x^{2/3}}(1 + {x^{2/3}})}}\\)','(d)\\( - \\frac{a}{{3{x^{2/3}}(1 +{x^{2/3}})}}\\) ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a) \\(y = {\\tan ^{ - 1}}\\left( {\\frac{{{x^{1/3}} + {a^{1/3}}}}{{1 -{x^{1/3}}.{a^{1/3}}}}} \\right) = {\\tan ^{ - 1}}({x^{1/3}}) + {\\tan ^{ -1}}{a^{1/3}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = \\frac{1}{{3{x^{2/3}}(1 + {x^{2/3}})}}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\cot ^{ - 1}}\\left( {\\frac{{1 + x}}{{1 - x}}} \\right)\\), then\\(\\frac{{dy}}{{dx}} = \\)','(a)\\(\\frac{1}{{1 + {x^2}}}\\)','(b)\\( - \\frac{1}{{1 + {x^2}}}\\)','(c)\\(\\frac{2}{{1 + {x^2}}}\\)','(d)\\( - \\frac{2}{{1 + {x^2}}}\\) ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(= {x^3}\\tan \\frac{x}{2}{\\sec ^2}\\frac{x}{2} + 3{x^2}{\\tan ^2}\\frac{x}{2}.\\)\\(y = {\\cot ^{ - 1}}\\left( {\\frac{{1 + x}}{{1 - x}}} \\right)\\)\\(\\frac{{dy}}{{dx}} =  - \\frac{1}{{1 + {{\\left( {\\frac{{1 + x}}{{1 - x}}}\\right)}^2}}}\\left[ {\\frac{{(1 - x) + (1 + x)}}{{{{(1 - x)}^2}}}} \\right]\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The differential coefficient of the given function \\({\\log _e}\\left( {\\sqrt{\\frac{{1 + \\sin x}}{{1 - \\sin x}}} } \\right)\\) with respect to x is','(a)\\(\\cos {\\rm{ec}}\\,x\\)','(b)\\(\\tan x\\)','(c)\\(\\cos x\\)','(d)\\(\\sec x\\)  ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(\\frac{{dy}}{{dx}} = \\frac{1}{{\\left( {\\frac{{1 + \\sin x}}{{\\cos x}}}\\right)}}\\left[ {\\frac{{\\cos x\\cos x + (1 + \\sin x)\\sin x}}{{{{(\\cos x)}^2}}}}\\right]\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left','(a)\\(\\sec x\\)','(b)\\(\\cos {\\rm{ec}}\\,x\\)','(c)\\(\\cos {\\rm{ec}}\\frac{x}{2}\\)','(d)\\(\\sec \\frac{x}{2}\\)  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}\\left[ {\\log \\sqrt {\\frac{{1 - \\cos x}}{{1 + \\cos x}}} }\\right] = \\frac{d}{{dx}}\\left[ {\\log \\left( {\\tan \\frac{x}{2}} \\right)} \\right] ={\\rm{cosec}}\\,x\\).\\(=  - \\frac{{2{{(1 - x)}^2}}}{{2(1 + {x^2})(1 - {x^2})}} =  - \\frac{1}{{1 +{x^2}}}.\\)\\(= \\left( {\\frac{{\\cos x}}{{1 + \\sin x}}} \\right)\\frac{{{{\\cos }^2}x + {{\\sin}^2}x + \\sin x}}{{{{(\\cos x)}^2}}} = \\sec x.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left','(a) \\(- \\frac{1}{2}\\)','(b) 0','(c) \\(\\frac{1}{2}\\)','(d) 1   ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{d}{{dx}}\\left[ {{{\\tan }^{ - 1}}\\sqrt {\\frac{{1 - \\cos x}}{{1 + \\cosx}}} } \\right] = \\frac{d}{{dx}}\\left[ {{{\\tan }^{ - 1}}\\tan \\frac{x}{2}} \\right]= \\frac{1}{2}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = {\\tan ^{ - 1}}\\left( {\\frac{{\\sin x}}{{1 + \\cos x}}} \\right)\\),then\\(f\\left( {\\frac{\\pi }{3}} \\right) = \\)','(a)\\(\\frac{1}{{2(1 + \\cos x)}}\\)','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{4}\\)','(d)None of these ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(f(x) = {\\tan ^{ - 1}}\\left( {\\frac{{\\sin x}}{{1 + \\cos x}}} \\right) = {\\tan^{ - 1}}\\left[ {\\tan \\frac{x}{2}} \\right] = \\frac{x}{2}\\)\\(\\Rightarrow{}\\) \\(f(x) = \\frac{1}{2}.\\) Hence \\(f\\left( {\\frac{\\pi }{3}}\\right) = \\frac{1}{2}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}{e^{x\\sin x}} = \\)','(a)\\({e^{x\\sin x}}(x\\cos x + \\sin x)\\)','(b)\\({e^{x\\sin x}}(\\cos x + x\\sin x)\\)','(c)\\({e^{x\\sin x}}(\\cos x + \\sin x)\\)','(d)None of these ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Let\\(y = {e^{x\\sin x}}\\)\\(\\Rightarrow{}\\) \\(\\log y = x\\sin x\\)\\(\\therefore \\frac{1}{y}\\frac{{dy}}{{dx}} = \\sin x + x\\cos x\\)or\\(\\frac{{dy}}{{dx}} = {e^{x\\sin x}}(\\sin x + x\\cos x)\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\{ \\log (\\sec x + \\tan x)\\}  = \\)','(a)\\(\\cos x\\)','(b)\\(\\sec x\\)','(c)\\(\\tan x\\)','(d)\\(\\cot x\\)  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}\\{ \\log (\\sec x + \\tan x)\\}  = \\frac{{\\sec x\\tan x + {{\\sec}^2}x}}{{\\sec x + \\tan x}} = \\sec x\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}(x{e^{{x^2}}}) = \\)','(a)\\(2{x^2}{e^x}^2 + {e^x}^2\\)','(b)\\({x^2}{e^x}^2 + {e^x}^2\\)','(c)\\({e^x}.2{x^2} + {e^x}^2\\)','(d)None of these   ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\frac{d}{{dx}}\\left( {x{e^{{x^2}}}} \\right) = {e^{{x^2}}} + x{e^{{x^2}}}2x= {e^{{x^2}}}(1 + 2{x^2})\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left','(a)\\(\\frac{{{e^{ax}}[a\\sin (bx + c) + b\\cos (bx + c)]}{{{{\\sin }^2}(bx + c)}}\\)','(b)\\(\\frac{{{e^{ax}}[a\\sin (bx + c) - b\\cos (bx + c)]}{{\\sin (bx + c)}}\\)','(c)\\(\\frac{{{e^{ax}}[a\\sin (bx + c) - b\\cos (bx + c)]}{{{{\\sin }^2}(bx + c)}}\\)','(d)None of these  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{d}{{dx}}\\left( {\\frac{{{e^{ax}}}}{{\\sin (bx + c)}}} \\right)\\)\\( =\\frac{{a{e^{ax}}\\sin (bx + c) - b{e^{ax}}\\cos (bx + c)}}{{{{\\{ \\sin (bx + c)\\}}^2}}}\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{{e^x}\\log x}}{{{x^2}}}\\), then \\(\\frac{{dy}}{{dx}} = \\) ','(a)\\(\\frac{{{e^x}[1 + (x + 2)\\log x]}{{{x^3}}}\\)','(b)\\(\\frac{{{e^x}[1 - (x -2)\\log x]}{{{x^4}}}\\)','(c)\\(\\frac{{{e^x}[1 - (x - 2)\\log x]}{{{x^3}}}\\)','(d)\\(\\frac{{{e^x}[1 + (x -2)\\log x]}{{{x^3}}}\\)  ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(\\frac{{dy}}{{dx}} =  - 2{x^{ - 3}}{e^x}\\log x + {x^{ - 2}}\\left( {{e^x}\\logx + \\frac{{{e^x}}}{x}} \\right)\\)\\(= {e^x}\\left[ {\\frac{{1 + (x - 2)\\log x}}{{{x^3}}}} \\right]\\)\\(= \\frac{{{e^{ax}}[a\\sin (bx + c) - b\\cos (bx + c)]}}{{{{\\sin }^2}(bx + c)}}.\\)Aliter: Taking \\(\\log \\), \\(\\log y = x + \\log \\log x - 2\\log x\\)\\(\\Rightarrow{}\\)   \\(\\frac{1}{y}\\frac{{dy}}{{dx}} = 1 + \\frac{1}{{x\\log x}} -\\frac{2}{x}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{dy}}{{dx}} = \\frac{{{e^x}\\log x}}{{{x^2}}}\\),     \\(\\left[ {\\frac{{x\\log x + 1 - 2\\log x}}{{x\\log x}}} \\right]\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{{e^{2x}}\\cos x}}{{x\\sin x}},\\)then \\(\\frac{{dy}}{{dx}} = \\)','(a)\\(\\frac{{{e^{2x}}[(2x - 1)\\cot x - x\\,{\\rm{cose}}{{\\rm{c}}^2}x]}{{{x^2}}}\\)','(b)\\(\\frac{{{e^{2x}}[(2x + 1)\\cot x - x\\,{\\rm{cose}}{{\\rm{c}}^2}x]}{{{x^2}}}\\)','(c)\\(\\frac{{{e^{2x}}[(2x - 1)\\cot x + x\\,{\\rm{cose}}{{\\rm{c}}^2}x]}{{{x^2}}}\\)','(d)None of these ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = \\frac{{{e^{2x}}\\cos x}}{{x\\sin x}}\\)\\(\\Rightarrow{}\\) \\(\\log y = 2x +\\log \\cos x - \\log x - \\log \\sin x\\)\\(\\frac{1}{y}\\frac{{dy}}{{dx}} = 2 + \\left( {\\frac{{ - \\sin x}}{{\\cos x}}} \\right)- \\frac{1}{x} - \\frac{{\\cos x}}{{\\sin x}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{dy}}{{dx}} = {e^{2x}}\\left[ {\\frac{2}{x}\\cot x -\\frac{1}{x} - \\frac{1}{{{x^2}}}\\cot x - \\frac{{{{\\cot }^2}x}}{x}} \\right]\\)    \\(= \\frac{{{e^x}[(x - 2)\\log x + 1]}}{{{x^3}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\{ {e^{ - a{x^2}}}\\log (\\sin x)\\}  = \\)','(a)\\({e^{ - a{x^2}}}(\\cot x + 2ax\\log \\sin x)\\)','(b)\\({e^{ - a{x^2}}}(\\cot x + ax\\log \\sin x)\\)','(c)\\({e^{ - a{x^2}}}(\\cot x - 2ax\\log \\sin x)\\)','(d)None of these  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{d}{{dx}}\\{ {e^{ - a{x^2}}}\\log (\\sin x)\\} \\)\\(= {e^{ - a{x^2}}}( - 2ax).\\log (\\sin x) + {e^{ - a{x^2}}}\\cot x\\)    \\(= {e^{ - ax}}^2[\\cot x - 2ax\\log (\\sin x)].\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\log x.{e^{(\\tan x + {x^2})}},\\)then \\(\\frac{{dy}}{{dx}} = \\)','(a)\\({e^{(\\tan x + {x^2})}}\\left[ {\\frac{1}{x} + ({{\\sec }^2}x + x)\\log x}\\right]\\)','(b)\\({e^{(\\tan x + {x^2})}}\\left[ {\\frac{1}{x} + ({{\\sec }^2}x - x)\\log x}\\right]\\)','(c)\\({e^{(\\tan x + {x^2})}}\\left[ {\\frac{1}{x} + ({{\\sec }^2}x + 2x)\\log x}\\right]\\)','(d)\\({e^{(\\tan x + {x^2})}}\\left[ {\\frac{1}{x} + ({{\\sec }^2}x - 2x)\\log x}\\right]\\)    ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(= \\frac{{{e^{2x}}}}{{{x^2}}}[(2x - 1)\\cot x - x\\,{\\rm{cose}}{{\\rm{c}}^2}x].\\)\\(y = \\log x.{e^{(\\tan x + {x^{2)}}}}\\)\\(\\therefore \\frac{{dy}}{{dx}} = {e^{(\\tan x + {x^2})}}.\\frac{1}{x} + \\logx.{e^{(\\tan x + {x^2})}}({\\sec ^2}x + 2x)\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\sqrt {\\frac{{1 + {e^x}}}{{1 - {e^x}}}} \\), then \\(\\frac{{dy}}{{dx}} =\\)','(a)\\(\\frac{{{e^x}}}{{(1 - {e^x})\\sqrt {1 - {e^{2x}}} }}\\)','(b)\\(\\frac{{{e^x}}}{{(1- {e^x})\\sqrt {1 - {e^x}} }}\\)','(c)\\(\\frac{{{e^x}}}{{(1 - {e^x})\\sqrt {1 + {e^{2x}}} }}\\)','(d)\\(\\frac{{{e^x}}}{{(1- {e^x})\\sqrt {1 - {e^{2x}}} }}\\)    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = \\sqrt {\\frac{{1 + {e^x}}}{{1 - {e^x}}}} \\)or \\({y^2} = \\frac{{1 +{e^x}}}{{1 - {e^x}}}\\)\\(= {e^{(\\tan x + {x^2})}}\\left[ {\\frac{1}{x} + ({{\\sec }^2}x + 2x)\\log x}\\right].\\)\\(2y\\frac{{dy}}{{dx}} = \\frac{{(1 - {e^x}){e^x} + (1 + {e^x}){e^x}}}{{{{(1 -{e^x})}^2}}} = \\frac{{2{e^x}}}{{{{(1 - {e^x})}^2}}}\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left\\{ {{e^x}\\log (1 + {x^2})} \\right\\} = \\)','(a)\\({e^x}\\left[ {\\log (1 + {x^2}) + \\frac{{2x}}{{1 + {x^2}}}} \\right]\\)','(b)\\({e^x}\\left[ {\\log (1 + {x^2}) - \\frac{{2x}}{{1 + {x^2}}}} \\right]\\)','(c)\\({e^x}\\left[ {\\log (1 + {x^2}) + \\frac{x}{{1 + {x^2}}}} \\right]\\)','(d)\\({e^x}\\left[ {\\log (1 + {x^2}) - \\frac{x}{{1 + {x^2}}}} \\right]\\)    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\frac{d}{{dx}}\\{ {e^x}\\log (1 + {x^2})\\}  = {e^x}\\log (1 + {x^2}) +{e^x}\\frac{1}{{(1 + {x^2})}}2x\\)   \\(\\therefore \\frac{{dy}}{{dx}} = \\frac{{{e^x}}}{{{{(1 - {e^x})}^2}}}\\sqrt{\\left[ {\\frac{{1 - {e^x}}}{{1 + {e^x}}}} \\right]\\left[ {\\frac{{1 - {e^x}}}{{1 -{e^x}}}} \\right]} \\)\\(= \\frac{{{e^x}}}{{(1 - {e^x})\\sqrt {1 - {e^{2x}}} }}.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{{e^{2x}} + {e^{ - 2x}}}}{{{e^{2x}} - {e^{ - 2x}}}}\\), then\\(\\frac{{dy}}{{dx}} = \\)','(a)\\(\\frac{{ - 8}}{{{{({e^{2x}} - {e^{ - 2x}})}^2}}}\\)','(b)\\(\\frac{8}{{{{({e^{2x}}- {e^{ - 2x}})}^2}}}\\)','(c)\\(\\frac{{ - 4}}{{{{({e^{2x}} - {e^{ - 2x}})}^2}}}\\)','(d)\\(\\frac{4}{{{{({e^{2x}}- {e^{ - 2x}})}^2}}}\\)   ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = \\frac{{{e^{2x}} + {e^{ - 2x}}}}{{{e^{2x}} - {e^{ - 2x}}}}\\)\\(= {e^x}\\left[ {\\log (1 + {x^2}) + \\frac{{2x}}{{1 + {x^2}}}} \\right].\\)\\(\\therefore \\frac{{dy}}{{dx}} = \\frac{{({e^{2x}} - {e^{ - 2x}})2({e^{2x}} - {e^{- 2x}}) - ({e^{2x}} + {e^{ - 2x}})2({e^{2x}} + {e^{ - 2x}})}}{{{{({e^{2x}} - {e^{- 2x}})}^2}}}\\)\\(= \\frac{{ - 8}}{{{{({e^{2x}} - {e^{ - 2x}})}^2}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{2{{(x - \\sin x)}^{3/2}}}}{{\\sqrt x }}\\), then\\(\\frac{{dy}}{{dx}} = \\)','(a)\\(\\frac{{2{{(x - \\sin x)}^{3/2}}}}{{\\sqrt x }}\\left[ {\\frac{3}{2}.\\frac{{1 -\\cos x}}{{1 - \\sin x}} - \\frac{1}{{2x}}} \\right]\\)','(b)\\(\\frac{{2{{(x - \\sin x)}^{3/2}}}}{{\\sqrt x }}\\left[ {\\frac{3}{2}.\\frac{{1 -\\cos x}}{{x - \\sin x}} - \\frac{1}{{2x}}} \\right]\\)','(c)\\(\\frac{{2{{(x - \\sin x)}^{1/2}}}}{{\\sqrt x }}\\left[ {\\frac{3}{2}.\\frac{{1 -\\cos x}}{{x - \\sin x}} - \\frac{1}{{2x}}} \\right]\\)','(d)None of these   ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\log y = \\log 2 + \\frac{3}{2}\\log (x - \\sin x) - \\frac{1}{2}\\log x\\)    \\(\\Rightarrow \\frac{{dy}}{{dx}} = y\\left[ {\\frac{3}{2}.\\frac{{1 - \\cos x}}{{x -\\sin x}} - \\frac{1}{{2x}}} \\right].\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left( {{{\\cos }^{ - 1}}\\sqrt {\\frac{{1 + \\cos x}}{2}} } \\right)= \\)','(a)1','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{3}\\)','(d)None of these    ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}\\left( {{{\\cos }^{ - 1}}\\sqrt {\\frac{{1 + \\cos x}}{2}} }\\right) = \\frac{d}{{dx}}\\left[ {{{\\cos }^{ - 1}}\\left( {\\cos \\frac{x}{2}}\\right)} \\right] = \\frac{1}{2}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\tan ^{ - 1}}\\left( {\\frac{{\\sqrt a  - \\sqrt x }}{{1 + \\sqrt {ax} }}}\\right)\\), then \\(\\frac{{dy}}{{dx}} = \\) ','(a)\\(\\frac{1}{{2(1 + x)\\sqrt x }}\\)','(b)\\(\\frac{1}{{(1 + x)\\sqrt x }}\\)','(c)\\( - \\frac{1}{{2(1 + x)\\sqrt x }}\\)','(d)None of these    ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c) \\(y = {\\tan ^{ - 1}}\\sqrt a  - {\\tan ^{ - 1}}\\sqrt x \\)Differentiating w.r.t. x, we get, \\(\\frac{{dy}}{{dx}} =  - \\frac{1}{{(1 +x)}}.\\frac{1}{{2\\sqrt x }}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\sec ^{ - 1}}\\left( {\\frac{{x + 1}}{{x - 1}}} \\right) + {\\sin ^{ -1}}\\left( {\\frac{{x - 1}}{{x + 1}}} \\right)\\), then \\(\\frac{{dy}}{{dx}} = \\)','(a)0','(b)1','(c)2','(d)3  ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {\\sec ^{ - 1}}\\left( {\\frac{{x + 1}}{{x - 1}}} \\right) + {\\sin ^{ -1}}\\left( {\\frac{{x - 1}}{{x + 1}}} \\right)\\)or \\(y = {\\cos ^{ - 1}}\\frac{{x - 1}}{{x + 1}} + {\\sin ^{ - 1}}\\left( {\\frac{{x- 1}}{{x + 1}}} \\right)\\)   \\(\\therefore y = \\frac{\\pi }{2} \\Rightarrow \\frac{{dy}}{{dx}} = 0\\)\\(\\left( {{{\\sin }^{ - 1}}x + {{\\cos }^{ - 1}}x = \\frac{\\pi }{2}} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}({\\log _e}x)({\\log _a}x)] =\\)','(a)\\(\\frac{{{{\\log }_a}x}}{x}\\)','(b) \\(\\frac{{{{\\log }_x}x}}{x}\\)','(c)\\(\\frac{{2\\log x}}{x}\\)','(d)\\(\\frac{{2{{\\log }_a}x}}{x}\\)    ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(\\frac{d}{{dx}}\\left( {{{\\log }_e}x.{{\\log }_a}x} \\right) =\\frac{d}{{dx}}\\left[ {\\log x.\\frac{{\\log x}}{{\\log a}}} \\right]\\)    ',CURRENT_TIMESTAMP);
