INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left[ {\\log \\sqrt {\\sin \\sqrt {{e^x}} } } \\right]\\)=','(a)\\(\\frac{1}{4}{e^{x/2}}\\cot ({e^{x/2}})\\)','(b)\\({e^{x/2}}\\cot ({e^{x/2}})\\)','(c)\\(\\frac{1}{4}{e^x}\\cot \\,({e^x})\\)','(d)\\(\\frac{1}{2}{e^{x/2}}\\cot\\,({e^{x/2}})\\)    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\frac{d}{{dx}}[\\log \\sqrt {\\sin \\sqrt {{e^x}} } ] = \\frac{d}{{dx}}\\left[{\\frac{1}{2}\\log (\\sin \\sqrt {{e^x}} )} \\right]\\)\\(= \\frac{1}{2}\\cot \\sqrt {{e^x}} \\frac{1}{{2\\sqrt {{e^x}} }}{e^x} =\\frac{1}{4}{e^{x/2}}\\cot ({e^{x/2}})\\)\\(= \\frac{{1 + {e^x}}}{{{e^x}}} \\times \\frac{{{e^x}}}{{{{(1 + {e^x})}^2}}} =\\frac{1}{{1 + {e^x}}}.\\)',CURRENT_TIMESTAMP);
