INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' At \\(x = \\sqrt {\\frac{\\pi }{2}} ,\\frac{d}{{dx}}\\cos (\\sin {x^2})\\)=','(a)-1','(b)1','(c)0','(d)None of these   ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{d}{{dx}}[\\cos (\\sin {x^2})] =  - \\sin (\\sin {x^2})\\cos {x^2}.2x\\)Putting \\(x = \\sqrt {\\frac{\\pi }{2}} ,\\) we have\\( =  - 2\\sqrt {\\frac{\\pi }{2}} \\sin \\left( {\\sin \\frac{\\pi }{2}} \\right)\\cos\\frac{\\pi }{2} = 0\\), \\(\\left[ {\\,\\cos \\frac{\\pi }{2} = 0} \\right]\\). ',CURRENT_TIMESTAMP);
