INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\sin ^{ - 1}}\\left( {\\frac{{19}}{{20}}x} \\right) + {\\cos ^{ -1}}\\left( {\\frac{{19}}{{20}}x} \\right)\\), then \\(\\frac{{dy}}{{dx}} = \\)','(a)0','(b)1','(c)- 1','(d)None of these  ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {\\sin ^{ - 1}}\\left( {\\frac{{19}}{{20}}x} \\right) + {\\cos ^{ -1}}\\left( {\\frac{{19}}{{20}}x} \\right) = \\frac{\\pi }{2}\\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = 0\\),  \\(\\left( {{{\\sin }^{ - 1}}x +{{\\cos }^{ - 1}}x = \\frac{\\pi }{2}} \\right)\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = (1 + {x^{1/4}})(1 + {x^{1/2}})(1 - {x^{1/4}})\\), then\\(\\frac{{dy}}{{dx}}\\)=','(a)1','(b)- 1','(c)x','(d)\\(\\sqrt x \\)  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(y = (1 + {x^{1/4}})(1 + {x^{1/2}})(1 - {x^{1/4}})\\)\\(\\Rightarrow{}\\) \\(y = (1 + {x^{1/4}})(1 - {x^{1/4}})(1 + {x^{1/2}})\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{{a^{{{\\cos }^{ - 1}}x}}}}{{1 + {a^{{{\\cos }^{ - 1}}x}}}}\\)and\\(z = {a^{{{\\cos }^{ - 1}}x}}\\),  then \\(\\frac{{dy}}{{dx}}\\)=','(a)\\(\\frac{1}{{1 + {a^{{{\\cos }^{ - 1}}x}}}}\\)','(b)\\( - \\frac{1}{{1 + {a^{{{\\cos}^{ - 1}}x}}}}\\)','(c)\\(\\frac{1}{{{{(1 + {a^{{{\\cos }^{ - 1}}x}})}^2}}}\\)','(d)None of these  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(y = \\frac{{{a^{{{\\cos }^{ - 1}}x}}}}{{1 + {a^{{{\\cos }^{ - 1}}x}}}},\\,\\,\\,z= {a^{{{\\cos }^{ - 1}}x}}\\) \\( \\Rightarrow y = \\frac{z}{{1 + z}}\\)   \\( \\Rightarrow \\frac{{dy}}{{dz}} = \\frac{{(1 + z)1 - z(1)}}{{{{(1 + z)}^2}}} =\\frac{1}{{{{(1 + z)}^2}}}\\)\\(= \\frac{1}{{{{(1 + {a^{{{\\cos }^{ - 1}}x}})}^2}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = (x - {x_0})g(x)\\), where \\(g(x)\\) is continuous at \\({x_0}\\), then\\(f({x_0})\\) is equal to','(a)0','(b)\\({x_0}\\)','(c)\\(g({x_0})\\)','(d)None of these  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(f({x_0}) = \\mathop {\\lim }\\limits_{x \\to {x_0}} \\frac{{(x - {x_0})g(x) -0}}{{x - {x_0}}} = \\mathop {\\lim }\\limits_{x \\to {x_0}} g(x) = g({x_0})\\)\\(= (1 - {x^{1/2}})(1 + {x^{1/2}}) = 1 - x \\Rightarrow \\frac{{dy}}{{dx}} =  - 1.\\)Since g is continuous. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If\\(y = {\\log _{\\sin x}}(\\tan x),\\) then \\({\\left( {\\frac{{dy}}{{dx}}}\\right)_{\\pi /4}} = \\)','(a)\\(\\frac{4}{{\\log 2}}\\)','(b)\\( - 4\\log 2\\)','(c)\\(\\frac{{ - 4}}{{\\log 2}}\\)','(d)None of these ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(y = \\frac{{\\log \\tan x}}{{\\log \\sin x}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{dy}}{{dx}} = \\frac{{(\\log \\sin x)\\left({\\frac{{{{\\sec }^2}x}}{{\\tan x}}} \\right) - (\\log \\tan x)(\\cot x)}}{{{{(\\log \\sinx)}^2}}}\\)\\(\\Rightarrow{}\\)  \\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{\\pi /4}} = \\frac{{ -4}}{{\\log 2}}\\)       (On simplification). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\log _2}[{\\log _2}(x)]\\), then \\(\\frac{{dy}}{{dx}}\\)is equal to','(a)\\(\\frac{{{{\\log }_2}e}}{{x{{\\log }_e}x}}\\) ','(b)\\(\\frac{1}{{{{\\log }_e}x{{\\log}_e}2}}\\)','(c)\\(\\frac{1}{{{{\\log }_e}{{(2x)}^x}}}\\)','(d)None of these ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {\\log _2}[{\\log _2}(x)] = {\\log _e}({\\log _e}x.{\\log _2}e).{\\log _2}e\\)\\(= [{\\log _e}{\\log _e}x + {\\log _e}({\\log _2}e)]{\\log _2}e\\)  \\(\\therefore \\frac{{dy}}{{dx}} = {\\log _2}e.\\frac{1}{{x{{\\log }_e}x}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}({e^{{x^3}}})\\) is equal to     ','(a)\\(3x{e^{{x^3}}}\\)','(b)  \\(3{x^2}{e^{{x^3}}}\\)','(c)\\(3x{\\left( {{e^{{x^3}}}} \\right)^2}\\)','(d) \\(2{x^2}{e^{{x^3}}}\\)  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(\\frac{d}{{dx}}({e^x}^3) = {e^x}^3.\\frac{d}{{dx}}({x^3}) =3{x^2}.\\,{e^x}^3\\).    ',CURRENT_TIMESTAMP);
