INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\tan ^{ - 1}}\\sqrt {\\frac{{1 + \\cos x}}{{1 - \\cos x}}} \\), then\\(\\frac{{dy}}{{dx}}\\) is equal to','(a)0','(b)\\( - \\frac{1}{2}\\)','(c)1/2','(d)1 ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(y = {\\tan ^{ - 1}}\\sqrt {\\frac{{1 + \\cos x}}{{1 - \\cos x}}}  = {\\tan ^{ -1}}\\sqrt {\\frac{{2{{\\cos }^2}\\frac{x}{2}}}{{2{{\\sin }^2}\\frac{x}{2}}}} \\)\\(= {\\tan ^{ - 1}}\\cot \\frac{x}{2} = {\\tan ^{ - 1}}\\tan \\left( {\\frac{\\pi }{2} -\\frac{x}{2}} \\right) = \\frac{\\pi }{2} - \\frac{x}{2}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{dy}}{{dx}} =  - \\frac{1}{2}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{{{\\sin }^{ - 1}}x}}{{\\sqrt {1 - {x^2}} }}\\), then \\((1 -{x^2})\\frac{{dy}}{{dx}}\\) is equal to','(a)\\(x + y\\)','(b)\\(1 + xy\\)','(c)1- xy','(d)\\(xy - 2\\)  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(y = \\frac{{{{\\sin }^{ - 1}}x}}{{\\sqrt {1 - {x^2}} }}\\)\\(\\frac{{dy}}{{dx}} = \\frac{{\\sqrt {1 - {x^2}} \\frac{1}{{\\sqrt {1 - {x^2}} }} -({{\\sin }^{ - 1}}x)\\frac{1}{2}\\frac{{( - 2x)}}{{\\sqrt {1 - {x^2}} }}}}{{1 -{x^2}}}\\)\\(\\Rightarrow (1 - {x^2})\\frac{{dy}}{{dx}} = 1 + x\\left( {\\frac{{{{\\sin }^{ -1}}x}}{{\\sqrt {1 - {x^2}} }}} \\right) = 1 + xy.\\)',CURRENT_TIMESTAMP);
