INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(2) = 4\\), \\(f(2) = 1\\)then \\(\\mathop {\\lim }\\limits_{x \\to 2}\\frac{{xf(2) - 2f(x)}}{{x - 2}} = \\)','(a)1','(b)2','(c)3','(d) -2  ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)Given \\(f(2) = 4,\\;\\;f(2) = 1\\)\\(\\therefore \\mathop {\\lim }\\limits_{x \\to 2} \\frac{{xf(2) - 2f(x)}}{{x - 2}} =\\mathop {\\lim }\\limits_{x \\to 2} \\frac{{xf(2) - 2f(2) + 2f(2) - 2f(x)}}{{x - 2}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 2} \\frac{{(x - 2)f(2)}}{{x - 2}} - \\mathop{\\lim }\\limits_{x \\to 2} \\frac{{2f(x) - 2f(2)}}{{x - 2}}\\)\\(= f(2) - 2\\mathop {\\lim }\\limits_{x \\to 2} \\frac{{f(x) - f(2)}}{{x - 2}} = f(2)- 2f(2) = 4 - 2(1) = 4 - 2 = 2\\)Aliter: Applying L-Hospital rule, we get  \\(\\mathop {\\lim }\\limits_{x \\to 2}\\frac{{f(2) - 2f(2)}}{1} = 2\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left','(a)\\(\\left( {x + \\frac{1}{x}} \\right)\\)','(b)\\(\\frac{{\\left( {1 +\\frac{1}{{{x^2}}}} \\right)}}{{\\left( {1 + \\frac{1}{x}} \\right)}}\\)','(c)\\(\\frac{{\\left( {1 - \\frac{1}{{{x^2}}}} \\right)}}{{\\left( {x + \\frac{1}{x}}\\right)}}\\)','(d)\\(\\left( {1 + \\frac{1}{x}} \\right)\\)   ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{d}{{dx}}\\left\\{ {\\log \\left( {x + \\frac{1}{x}} \\right)} \\right\\} =\\frac{1}{{x + \\frac{1}{x}}} \\times \\frac{d}{{dx}}\\left( {x + \\frac{1}{x}} \\right)= \\frac{1}{{\\left( {x + \\frac{1}{x}} \\right)}}\\left( {1 - \\frac{1}{{{x^2}}}}\\right)\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\sin ^{ - 1}}\\sqrt x \\),  then \\(\\frac{{dy}}{{dx}} = \\) ','(a)\\(\\frac{2}{{\\sqrt x \\sqrt {1 - x} }}\\)','(b)\\(\\frac{{ - 2}}{{\\sqrt x \\sqrt {1 -x} }}\\)','(c)\\(\\frac{1}{{2\\sqrt x \\sqrt {1 - x} }}\\)','(d)\\(\\frac{1}{{\\sqrt {1 - x} }}\\)  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c) \\(\\frac{{dy}}{{dx}} = \\frac{1}{{\\sqrt {1 - x} }}.\\frac{d}{{dx}}(\\sqrt x ) =\\frac{1}{{2\\sqrt x .\\sqrt {1 - x} }}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\sin ^{ - 1}}\\sqrt {(1 - x)}  + {\\cos ^{ - 1}}\\sqrt x \\), then\\(\\frac{{dy}}{{dx}} = \\)','(a)\\(\\frac{1}{{\\sqrt {x(1 - x)} }}\\)','(b)\\(\\frac{{ - 1}}{{\\sqrt {x(1 - x)} }}\\)','(c)\\(\\frac{1}{{\\sqrt {x(1 + x)} }}\\)','(d)None of these ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\({\\sin ^{ - 1}}\\sqrt {1 - x}  = {\\sin ^{ - 1}}\\sqrt {1 - {{(\\sqrt x )}^2}} = {\\cos ^{ - 1}}\\sqrt x \\)\\(\\therefore y = 2{\\cos ^{ - 1}}\\sqrt x \\)or\\(\\frac{{dy}}{{dx}} = 2.\\frac{{ -1}}{{\\sqrt {1 - x} }}.\\frac{1}{{2\\sqrt x }}\\)etc.Aliter: \\(y = {\\sin ^{ - 1}}\\sqrt {1 - x}  + {\\cos ^{ - 1}}\\sqrt x \\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = \\frac{1}{{\\sqrt {1 - 1 + x} }}.\\frac{{ -1}}{{2\\sqrt {1 - x} }} - \\frac{1}{{\\sqrt {1 - x} }}.\\frac{1}{{2\\sqrt x }}\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {x^n}\\log x + x{(\\log x)^n}\\), then \\(\\frac{{dy}}{{dx}} = \\)','(a)\\({x^{n - 1}}(1 + n\\log x) + {(\\log x)^{n - 1}}[n + \\log x]\\)','(b)\\({x^{n - 2}}(1 + n\\log x) + {(\\log x)^{n - 1}}[n + \\log x]\\)','(c)\\({x^{n - 1}}(1 + n\\log x) + {(\\log x)^{n - 1}}[n - \\log x]\\)','(d)None of these    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {x^n}\\log x + x{(\\log x)^n}\\)\\(\\frac{{dy}}{{dx}} = n{x^{n - 1}}\\log x + {x^n}.\\left( {\\frac{1}{x}} \\right) +xn{(\\log x)^{n - 1}}.\\left( {\\frac{1}{x}} \\right) + 1.{(\\log x)^n}\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y\\sqrt {{x^2} + 1}  = \\log \\left\\{ {\\sqrt {{x^2} + 1}  - x} \\right\\}\\),then\\(({x^2} + 1)\\frac{{dy}}{{dx}} + xy + 1 = \\)','(a)0','(b)1','(c)2','(d)None of these   ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(= {x^{n - 1}}(1 + n\\log x) + {(\\log x)^{n - 1}}[n + \\log x].\\)\\(= \\frac{{ - 2}}{{2\\sqrt x \\sqrt {1 - x} }} = \\frac{{ - 1}}{{\\sqrt x \\sqrt {1 -x} }}.\\)\\(y\\sqrt {{x^2} + 1}  = \\log \\left\\{ {\\sqrt {{x^2} + 1}  - x} \\right\\}\\)Differentiating both sides w.r.t. x, we get\\(\\frac{{dy}}{{dx}}\\sqrt {{x^2} + 1}  + y.\\frac{1}{{2\\sqrt {{x^2} + 1} }}.2x =\\frac{1}{{\\sqrt {{x^2} + 1}  - x}} \\times \\left\\{ {\\frac{1}{2}\\frac{{2x}}{{\\sqrt{{x^2} + 1} }} - 1} \\right\\}\\)\\(\\Rightarrow{}\\)  \\(({x^2} + 1)\\frac{{dy}}{{dx}} + xy = \\sqrt {{x^2} + 1}.\\frac{{ - 1}}{{\\sqrt {{x^2} + 1} }}\\)\\(\\Rightarrow{}\\)  \\(({x^2} + 1)\\frac{{dy}}{{dx}} + xy + 1 = 0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The derivative of tanx - x with respect to x is  ','(a)\\(1 - {\\tan ^2}x\\)','(b)tan x','(c)\\( - {\\tan ^2}x\\)','(d)\\({\\tan ^2}x\\)   ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(f(x) = {\\sec ^2}x - 1 = {\\tan ^2}x\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = ({\\log _{\\cot x}}\\tan x){({\\log _{\\tan x}}\\cot x)^{ - 1}},\\)then\\(f(2) = \\)','(a)2','(b)0','(c)\\( \\frac{1}{2}\\)','(d) - 2 ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)Obviously, \\(f(x) = {({\\log _{\\cot x}}\\tan x)^2}\\)        ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = 3{e^{{x^2}}}\\),then \\(f(x) - 2xf(x) + \\frac{1}{3}f(0) - f(0) = \\)','(a)0','(b)1','(c)\\(\\frac{7}{3}{e^{{x^2}}}\\)','(d)None of these ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)We have \\(f(x) = 3{e^{{x^2}}}.\\)Di\\(= {({\\log _{\\cot x}}{(\\cot x)^{ - 1}})^2} \\Rightarrow f(x) = 0.\\)fferentiating w.r.t. x, we get \\(f(x) = 6x{e^{{x^2}}}\\); \\(\\therefore f(0) =3\\)and\\(f(0) = 0\\)\\(\\Rightarrow{}\\)\\(f(x) - 2xf(x) + \\frac{1}{3}f(0) - f(0)\\)    \\(= 6x{e^{{x^2}}} - 6x{e^{{x^2}}} + \\frac{1}{3}(3) - 0 = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\log _{\\cos x}}\\sin x\\), then \\(\\frac{{dy}}{{dx}}\\)is equal to','(a)\\(\\frac{{\\cot x\\log \\cos x + \\tan x\\log \\sin x}}{{{{(\\log \\cos x)}^2}}}\\)','(b)\\(\\frac{{\\tan x\\log \\cos x + \\cot x\\log \\sin x}}{{{{(\\log \\cos x)}^2}}}\\)','(c)\\(\\frac{{\\cot x\\log \\cos x + \\tan x\\log \\sin x}}{{{{(\\log \\sin x)}^2}}}\\)','(d)None of these  ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)We have \\(y = {\\log _{\\cos x}}\\sin x = \\frac{{\\log \\sin x}}{{\\log \\cos x}}\\) \\(\\therefore \\frac{{dy}}{{dx}} = \\frac{{\\cot x.\\log \\cos x + (\\log \\sin x)\\tanx}}{{{{(\\log \\cos x)}^2}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\left[ {\\log \\left\\{ {{e^x}{{\\left( {\\frac{{x + 2}}{{x - 2}}}\\right)}^{3/4}}} \\right\\}} \\right]\\) equals }','(a) \\(\\frac{{{x^2} - 7}}{{{x^2} - 4}}\\)','(b) 1','(c)\\(\\frac{{{x^2} + 1}}{{{x^2} - 4}}\\)','(d)\\({e^x}\\frac{{{x^2} - 1}}{{{x^2} -4}}\\)   ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = \\log {e^x} + \\frac{3}{4}\\log \\frac{{x + 2}}{{x - 2}} = x +\\frac{3}{4}\\log \\frac{{x + 2}}{{x - 2}}\\)\\(\\Rightarrow{}\\)  \\(y = x + \\frac{3}{4}\\left[ {\\log (x + 2) - \\log (x - 2)}\\right]\\)\\(\\frac{{dy}}{{dx}} = 1 + \\frac{3}{4}\\left[ {\\frac{1}{{x + 2}} - \\frac{1}{{x -2}}} \\right] = 1 - \\frac{3}{{{x^2} - 4}}\\)\\(\\frac{{dy}}{{dx}} = \\frac{{{x^2} - 7}}{{{x^2} - 4}}.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For the curve \\(\\sqrt x  + \\sqrt y  = 1,\\frac{{dy}}{{dx}}\\)at \\(\\left({\\frac{1}{4},\\frac{1}{4}} \\right)\\)is','(a)1/2','(b)1','(c)-1','(d)2  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\sqrt x  + \\sqrt y  = 1\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} =  -\\frac{{\\sqrt y }}{{\\sqrt x }}\\) \\(\\Rightarrow{}\\)  \\({\\left( {\\frac{{dy}}{{dx}}}\\right)_{\\left( {\\frac{1}{4},\\,\\frac{1}{4}} \\right)}} =  - 1\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Differential coefficient of \\(\\sqrt {\\sec \\sqrt x } \\)is','(a) \\(\\frac{1}{{4\\sqrt x }}{(\\sec \\sqrt x )^{3/2}}\\sin \\sqrt x \\)','(b)\\(\\frac{1}{{4\\sqrt x }}\\sec \\sqrt x \\sin \\sqrt x \\)','(c) \\(\\frac{1}{2}\\sqrt x {(\\sec \\sqrt x )^{3/2}}\\sin \\sqrt x \\) ','(d) \\(\\frac{1}{2}\\sqrt x \\sec \\sqrt x \\sin \\sqrt x \\)  ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\frac{d}{{dx}}\\left( {\\sqrt {\\sec \\sqrt x } } \\right) = \\frac{1}{{2\\sqrt{\\sec \\sqrt x } }}.\\frac{d}{{dx}}(\\sec \\sqrt x )\\)\\end{itemize}\\(= \\frac{1}{{2{{(\\sec \\sqrt x )}^{1/2}}}}.\\sec \\sqrt x .\\tan \\sqrt x.\\frac{1}{{2\\sqrt x }}\\)\\({x^2} = \\cos 2\\theta .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {e^{(1 + {{\\log }_e}x)}}\\), then the value of \\(\\frac{{dy}}{{dx}} = \\)','(a)e','(b)1','(c)0','(d)\\({\\log _e}x\\,\\,{e^{{{\\log }_e}ex}}\\)    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {e^{1 + {{\\log }_e}x}} = {e^1}.{e^{{{\\log }_e}x}} = e.x \\Rightarrow\\frac{{dy}}{{dx}} = e\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For the function \\(f(x) = {x^2} - 6x + 8,2 \\le x \\le 4\\), the value of x forwhich \\(f(x)\\) vanishes, is ','(a)\\(\\frac{9}{4}\\)','(b)\\(\\frac{5}{2}\\)','(c)3','(d)\\(\\frac{7}{2}\\)  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(f(x) = 2x - 6 = 0 \\Rightarrow x = 3\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = {e^x}g(x),g(0) = 2,g(0) = 1\\), then \\(f(0)\\)is','(a)1','(b)3','(c)2','(d)0 ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(f(x) = {e^x}g(x) \\Rightarrow f(x) = {e^x}g(x) + {e^x}g(x)\\)   \\(\\Rightarrow f(0) = g(0) + g(0) = 2 + 1 = 3.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {e^x}\\log x\\), then \\(\\frac{{dy}}{{dx}}\\)is ','(a) \\(\\frac{{{e^x}}}{x}\\)','(b) \\({e^x}\\left( {\\frac{1}{x} + x\\log x} \\right)\\)','(c) \\({e^x}\\left( {\\frac{1}{x} + \\log x} \\right)\\)','(d) \\(\\frac{{{e^x}}}{{\\logx}}\\)    ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Differentiating \\(y = {e^x}\\log x,\\) w.r.t. x ,we get   \\(\\frac{{dy}}{{dx}} = {e^x} \\times \\frac{1}{x} + \\log x \\times {e^x} = {e^x}\\left({\\frac{1}{x} + \\log x} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {\\cot ^{ - 1}}\\left[ {\\frac{{\\sqrt {1 + \\sin x}  + \\sqrt {1 - \\sin x}}}{{\\sqrt {1 + \\sin x}  - \\sqrt {1 - \\sin x} }}} \\right]\\), then\\(\\frac{{dy}}{{dx}} = \\)','(a) \\(\\frac{1}{2}\\)','(b)\\(\\frac{2}{3}\\)','(c)3','(d)1 ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(y = {\\cot ^{ - 1}}\\left[ {\\frac{{\\sqrt {1 + \\sin x}  + \\sqrt {1 - \\sin x}}}{{\\sqrt {1 + \\sin x}  - \\sqrt {1 - \\sin x} }}} \\right]\\)\\(= {\\cot ^{ - 1}}\\left[ {\\frac{{2 + 2\\cos x}}{{2\\sin x}}} \\right] = {\\cot ^{ -1}}\\left[ {\\frac{{1 + \\cos x}}{{\\sin x}}} \\right]\\)\\(= {\\cot ^{ - 1}}\\left[ {\\cot \\frac{x}{2}} \\right] = \\frac{x}{2}\\) \\(\\therefore \\frac{{dy}}{{dx}} = \\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\sec {x^0}\\), then \\(\\frac{{dy}}{{dx}} = \\) ','(a)\\(\\sec x\\tan x\\)','(b)\\(\\sec {x^o}\\tan {x^o}\\)','(c) \\(\\frac{\\pi }{{180}}\\sec {x^o}\\tan {x^o}\\)','(d)  \\(\\frac{{180}}{\\pi }\\sec{x^o}\\tan {x^o}\\)  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)As\\({x^0} = \\frac{{\\pi x}}{{Answer = }\\)radian.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\sqrt {\\sin \\sqrt x } \\),  then \\(\\frac{{dy}}{{dx}} = \\) ','(a)\\(\\frac{1}{{2\\sqrt {\\cos \\sqrt x } }}\\)','(b) \\(\\frac{{\\sqrt {\\cos \\sqrt x }}}{{2x}}\\)','(c) \\(\\frac{{\\cos \\sqrt x }}{{4\\sqrt x \\sqrt {\\sin \\sqrt x } }}\\)','(d)\\(\\frac{1}{{2\\sqrt {\\sin x} }}\\) ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{{dy}}{{dx}} = \\frac{1}{{2\\sqrt {\\sin \\sqrt x } }} \\times \\cos \\sqrt x \\times \\frac{1}{{2\\sqrt x }}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If\\(y = {\\log _{10}}{x^2}\\), then \\(\\frac{{dy}}{{dx}}\\)  is equal to','(a) \\(\\frac{2}{x}\\)','(b)  \\(\\frac{2}{{x{{\\log }_e}10}}\\)','(c) \\(\\frac{1}{{x{{\\log }_e}10}}\\)','(d)  \\(\\frac{1}{{10x}}\\)   ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)Given \\(y = {\\log _{10}}{x^2}\\)\\(y = \\frac{{{{\\log }_e}{x^2}}}{{{{\\log }_e}10}}\\)\\(y = \\frac{{2{{\\log}_e}x}}{{{{\\log }_e}10}}\\),\\(\\left( {{{\\log }_a}b = \\frac{{{{\\log }_e}b}}{{{{\\log }_e}a}}} \\right),\\)\\(\\therefore \\frac{{dy}}{{dx}} = \\frac{2}{{x{{\\log }_e}10}}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = {3^{{x^2}}}\\), then \\(\\frac{{dy}}{{dx}}\\) is equal to','(a)\\(({x^2}){3^{{x^2} - 1}}\\)','(b)\\(3{x^2}.2x\\)','(c)\\({3^{{x^2}}}.2x.\\log 3\\)','(d)\\(({x^2} - 1).3\\)  ','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Given \\(y = {3^{{x^2}}}\\)\\(\\frac{d}{{dx}}({a^x}) = {a^x}{\\log _e}a\\)\\(\\therefore \\frac{{dy}}{{dx}} = {3^{{x^2}}}{\\log_e}3\\frac{d}{{dx}}({x^2})\\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} ={3^{{x^2}}}.2x.{\\log _e}3\\) .    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The first derivative of the function \\((\\sin 2x\\cos 2x\\cos 3x + {\\log _2}{2^{x +3}})\\) with respect to x at \\(x = \\pi \\)is ','(a)2','(b)-1','(c)\\( - 2 + {2^\\pi }{\\log _e}2\\)','(d)\\( - 2 + {\\log _e}2\\)   ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(f(x) = \\sin 2x.\\cos 2x.\\cos 3x + {\\log _2}{2^{x + 3}}\\)\\(f(x) = \\frac{1}{2}\\sin 4x\\cos 3x + (x + 3){\\log _2}2\\)\\(f(x) = \\frac{1}{4}[\\sin 7x + \\sin x] + x + 3\\)Differentiate w.r.t. x,  \\(f(x) = \\frac{1}{4}[7\\cos 7x + \\cos x] + 1\\)\\(f(x) = \\frac{7}{4}\\cos 7x + \\frac{1}{4}\\cos x + 1.\\)Hence \\(f(\\pi ) =  - 2 + 1 =  - 1\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The values of x, at which the first derivative of the function \\({\\left( {\\sqrtx  + \\frac{1}{{\\sqrt x }}} \\right)^2}\\)w.r.t. x is \\(\\frac{3}{4}\\), are','(a)\\( \\pm \\,2\\)','(b)\\( \\pm \\frac{1}{2}\\)','(c)\\( \\pm \\frac{{\\sqrt 3 }}{2}\\)','(d) \\( \\pm \\frac{2}{{\\sqrt 3 }}\\)  ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Given \\(f(x) = {\\left( {\\sqrt x  + \\frac{1}{{\\sqrt x }}} \\right)^2}\\)\\(f(x) = x + \\frac{1}{x} + 2\\) and \\(f(x) = 3/4\\)So, \\(1 - \\frac{1}{{{x^2}}} = \\frac{3}{4}\\) \\(\\Rightarrow{}\\)\\(\\frac{1}{{{x^2}}} = \\frac{1}{4}\\) \\(\\Rightarrow{}\\) \\(x =  + 2,\\, - 2\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(y = \\frac{{{{(1 - x)}^2}}}{{{x^2}}}\\), then \\(\\frac{{dy}}{{dx}}\\)is','(a) \\(\\frac{2}{{{x^2}}} + \\frac{2}{{{x^3}}}\\)','(b) \\( - \\frac{2}{{{x^2}}} +\\frac{2}{{{x^3}}}\\)','(c) \\( - \\frac{2}{{{x^2}}} - \\frac{2}{{{x^3}}}\\)','(d)  \\( - \\frac{2}{{{x^3}}} +\\frac{2}{{{x^2}}}\\) ','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(y = \\frac{{1 + {x^2} - 2x}}{{{x^2}}} = \\frac{1}{{{x^2}}} + 1 - \\frac{2}{x}\\Rightarrow \\frac{{dy}}{{dx}} =  - \\frac{2}{{{x^3}}} + \\frac{2}{{{x^2}}}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(pv = 81\\), then \\(\\frac{{dp}}{{dv}}\\) is at v = 9 equal to ','(a)1','(b)-1','(c)2','(d)None of these ','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(pv = 81 \\Rightarrow \\frac{{dp}}{{dv}} = \\frac{{ - 81}}{{{v^2}}} = \\frac{{ -81}}{{81}} =  - 1\\).    ',CURRENT_TIMESTAMP);
