INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\frac{d}{{dx}}\\cos \\,{{\\rm{h}}^{ - 1}}(\\sec x) = \\) ','(a)sec x','(b)sin x','(c)tan x','(d)cosec x    ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\frac{d}{{dx}}{\\cosh ^{ - 1}}(\\sec x)\\)\\( = \\frac{1}{{\\sqrt {{{\\sec }^2}x -1} }}\\frac{d}{{dx}}\\sec x\\)       ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\frac{1}{{\\sqrt {{x^2} + {a^2}}  + \\sqrt {{x^2} + {b^2}} }}\\), then\\(f(x)\\) is equal to','(a)\\(\\frac{x}{{({a^2} - {b^2})}}\\left[ {\\frac{1}{{\\sqrt {{x^2} + {a^2}} }} -\\frac{1}{{\\sqrt {{x^2} + {b^2}} }}} \\right]\\)','(b)\\(\\frac{x}{{({a^2} + {b^2})}}\\left[ {\\frac{1}{{\\sqrt {{x^2} + {a^2}} }} -\\frac{2}{{\\sqrt {{x^2} + {b^2}} }}} \\right]\\)','(c)\\(\\frac{x}{{({a^2} - {b^2})}}\\left[ {\\frac{1}{{\\sqrt {{x^2} + {a^2}} }} +\\frac{1}{{\\sqrt {{x^2} + {b^2}} }}} \\right]\\)','(d)\\(({a^2} + {b^2})\\left[ {\\frac{1}{{\\sqrt {{x^2} + {a^2}} }} - \\frac{2}{{\\sqrt{{x^2} + {b^2}} }}} \\right]\\)  ','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(f(x) = \\frac{1}{{\\sqrt {{x^2} + {a^2}}  + \\sqrt {{x^2} + {b^2}} }}\\)\\(f(x) = \\frac{1}{{\\sqrt {{x^2} + {a^2}}  + \\sqrt {{x^2} + {b^2}} }}.\\frac{{\\sqrt{{x^2} + {a^2}}  - \\sqrt {{x^2} + {b^2}} }}{{\\sqrt {{x^2} + {a^2}}  - \\sqrt{{x^2} + {b^2}} }}\\)\\(f(x) = \\frac{1}{{{a^2} - {b^2}}}\\left[ {\\sqrt {{x^2} + {a^2}}  - \\sqrt {{x^2} +{b^2}} } \\right]\\)\\(f(x) = \\frac{1}{{{a^2} - {b^2}}}\\left[ {\\frac{{2x}}{{2\\sqrt {{x^2} + {a^2}} }}- \\frac{{2x}}{{2\\sqrt {{x^2} + {b^2}} }}} \\right]\\)\\(= \\frac{1}{{\\tan x}}.\\sec x.\\tan x = \\sec x.\\)\\(f(x) = \\frac{x}{{{a^2} - {b^2}}}\\left[ {\\frac{1}{{\\sqrt {{x^2} + {a^2}} }} -\\frac{1}{{\\sqrt {{x^2} + {b^2}} }}} \\right].\\)',CURRENT_TIMESTAMP);
