INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In \\(( - 4,\\,4)\\) the function \\(f(x) = \\int\\limits_{ - 10}^x {({t^4} - 4){e^{ -4t}}dt} \\) has','(a)No extrema','(b)One extremum','(c)Two extrema ','(d)Four extrema    1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(V = \\frac{4}{3}\\pi {r^3}\\)Differentiate with respect to t,\\(\\frac{{dV}}{{dt}} = \\frac{4}{3}\\pi3{r^2}.\\frac{{dr}}{{dt}}\\)\\(\\Rightarrow{}\\)\\(\\frac{{dr}}{{dt}} = \\frac{1}{{4\\pi{r^2}}}.\\frac{{dV}}{{dt}}\\) \\(\\frac{{dr}}{{dt}} = \\frac{1}{{4 \\times \\pi  \\times 15 \\times 15}} \\timesAnswer = )\\( \\Rightarrow \\)\\(\\frac{{dr}}{{dt}} = \\frac{1}{\\pi } = \\frac{7}{{22}}.\\)1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' On ','(a)\\({e^2}\\)','(b)\\(\\frac{1}{e}\\log \\frac{1}{{\\sqrt e }}\\)','(c)\\({e^2}\\log \\sqrt e \\)','(d)None of these    1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{{dx}}{{dt}} = {v_x} = a \\Rightarrow \\frac{{{d^2}x}}{{d{t^2}}} = 0 ={a_x}\\)\\({a_x}\\)is acceleration in x-axis\\(\\frac{{{d^2}y}}{{d{t^2}}} =  - b{a^2}\\sin at \\Rightarrow {a_y} =  - {a^2}y\\)Hence, \\({a_y}\\)changes as y changes. 1',CURRENT_TIMESTAMP);
