INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If PQ  and PR are the two sides of a triangle, then the angle between them whichgives maximum area of the triangle is','(a)\\(\\pi \\)','(b)\\(\\pi /3\\)','(c)\\(\\pi /4\\)','(d)\\(\\pi /2\\)    1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(s = t + 6{t^2} - {t^3}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{ds}}{{dt}} = 1 + 12t - 3{t^2}\\)\\(\\Rightarrow{}\\)\\(v= 5\\,cm/\\sec \\)\\(\\Rightarrow{}\\)  \\(\\frac{{{d^2}s}}{{d{t^2}}} = 0\\) \\(\\Rightarrow{}\\) \\(12 - 6t= 0 \\Rightarrow t = 2\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(y = a(1 - \\cos x)\\) is maximum when \\(x = \\)','(a)\\(\\pi \\)','(b)\\(\\pi /2\\)','(c)\\( - \\pi /2\\)','(d)\\( - \\pi /6\\)   1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)The distance \\(s = 3{t^2} - 8t + 5\\) and velocity \\(v = \\frac{{ds}}{{dt}} =6t - 8\\)The body will be stopped when \\(v = 0\\)\\(\\Rightarrow{}\\)  \\(6t - 8 = 0\\)  \\(\\Rightarrow{}\\)  \\(t = 4/3\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The minimum value of \\(\\left( {{x^2} + \\frac{{}{x}} \\right)\\) is','(a)75','(b)50','(c)25','(d)55  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Let \\(y = \\sqrt {{x^2} + 16} \\) and \\(z = \\frac{x}{{x - 1}}\\)\\(\\Rightarrow{}\\)\\(\\frac{{dy}}{{dx}} = \\frac{1}{2}{({x^2} + 16)^{ -1/2}}(2x)\\) & \\(\\frac{{dz}}{{dx}} = \\frac{{x - 1 - x}}{{{{(x - 1)}^2}}} = \\frac{{- 1}}{{{{(x - 1)}^2}}}\\)\\(\\therefore{}\\) \\(\\frac{{dy}}{{dz}} = \\frac{{ - x}}{{\\sqrt {{x^2} + 16}}}\\,\\frac{1}{{1/{{(x - 1)}^2}}}\\)    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The minimum value of \\({x^2} + \\frac{1}{{1 + {x^2}}}\\) is at  ','(a)\\(x = 0\\)','(b)\\(x = 1\\)','(c)\\(x = 4\\)','(d)\\(x = 3\\)  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\({\\left( {\\frac{{dy}}{{dz}}} \\right)_{x = 3}} = \\frac{{ - 3{{(2)}^2}}}{5} =\\frac{{ - 12}}{5}.\\)\\(a + b{v^2} = {x^2}\\)  \\(\\Rightarrow{}\\)  \\(0 + b\\left({2v.\\,\\frac{{dv}}{{dt}}} \\right) = 2x\\,.\\,\\frac{{dx}}{{dt}}\\)\\(\\Rightarrow{}\\)  \\(v.b\\frac{{dv}}{{dt}} = x\\,.\\,\\frac{{dx}}{{dt}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{dv}}{{dt}} = \\frac{x}{b}\\) ,  \\(\\left({\\frac{{dx}}{{dt}} = v} \\right)\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(x - 2y = 4,\\) the minimum value of \\(xy\\) is','(a)- 2','(b)2','(c)0','(d)- 3  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(\\frac{{dx}}{{dt}} = 2at + b\\)\\(\\Rightarrow{}\\) \\(\\frac{{{d^2}x}}{{d{t^2}}}= 2a\\).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The minimum value of \\(2x + 3y,\\) when \\(xy = 6,\\) is','(a)12','(b)9','(c)8','(d)6 1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)If x is the length of each side of an equilateral triangle and A is its area,then \\(A = \\frac{{\\sqrt 3 }}{4}{x^2} \\Rightarrow \\frac{{dA}}{{dt}} = \\frac{{\\sqrt3 }}{4}2x\\frac{{dx}}{{dt}}\\)Here, \\(x = 10\\,\\,cm\\) and \\(\\frac{{dx}}{{dt}} = 2\\,\\,cm/sec\\)\\(\\Rightarrow{}\\) \\(A = 10\\sqrt 3 \\) Sq. unit per sec.    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = |px - q| + r|x|,\\) \\(x \\in ( - \\infty ,\\infty )\\) where\\(p > 0,q > 0,r > 0\\)assumes its minimum value only at one point if ','(a)\\(p \\ne q\\)','(b)\\(q \\ne r\\)','(c)\\(r \\ne p\\)','(d)\\(p = q = r\\)    1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Surface area \\(s = 4\\pi {r^2}\\) and \\(\\frac{{dr}}{{dt}} = 2\\)\\(\\therefore{}\\) \\(\\frac{{ds}}{{dt}} = 4\\pi  \\times 2r\\frac{{dr}}{{dt}}\\) =\\(8\\pi r \\times 2 = 16\\pi r\\)\\(\\Rightarrow{}\\)  \\(\\frac{{ds}}{{dt}} \\propto r\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The minimum value of function \\(f(x) = 3{x^4} - 8{x^3} + 12{x^2} - 48x + 25\\) on','(a)25','(b)- 39','(c)- 25','(d)39 1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Time of encounter \\(10 + 6t = 3 + {t^2}\\)\\(\\Rightarrow{}\\) \\({t^2} - 6t - 7 = 0\\), \\(t = 7\\)sec.At \\(t = 7\\sec \\)., \\({v_1} = \\frac{d}{{dt}}(10 + 6t) = 6\\,\\)cm/secAt\\(t = 7\\sec .\\)  \\({v_2} = \\frac{d}{{dt}}(3 + {t^2}) = 2t = 2 \\times 7 =14\\)cm/sec\\(\\therefore{}\\)Resultant velocity = \\({v_2} - {v_1} = 14 - 6 = 8\\,\\)cm/sec   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The maximum value of \\({x^{1/x}}\\) is','(a)\\(\\frac{1}{e}\\)','(b)\\({e^{1/e}}\\)','(c)e','(d)\\(\\frac{1}{{{e^e}}}\\)  1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)Acceleration in direction of x-axis = \\(\\frac{{{d^2}x}}{{d{t^2}}} =  - 2c\\) and acceleration in direction of y-axis  = \\(\\frac{{{d^2}y}}{{d{t^2}}} = 2b\\)Resultant acceleration is=\\(\\sqrt {{{( - 2c)}^2} + {{(2b)}^2}}  = 2\\sqrt {{b^2} + {c^2}} \\)    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The minimum value of \\(4{e^{2x}} + 9{e^{ - 2x}}\\) is  ','(a)11','(b)12','(c)10','(d)14   1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Given that \\(dV/dt = 30f{t^3}/min\\) and \\(r = 15ft\\)\\(V = \\frac{4}{3}\\pi {r^3};\\)\\(\\frac{{dV}}{{dt}} = 4\\pi {r^2}\\frac{{dr}}{{dt}}\\)\\(\\frac{{dr}}{{dt}} = \\frac{{dV/dt}}{{4\\pi {r^2}}} = \\frac{{30}}{{4 \\times \\pi \\times 15 \\times 15}} = \\frac{1}{{30\\pi }}\\)ft/min    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The point \\((0,\\,5)\\)is closest to the curve \\({x^2} = 2y\\) at','(a)\\((2\\sqrt 2 ,0)\\)','(b)(0, 0)','(c)\\((2,\\,2)\\)','(d)None of these   1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)  Given  \\(s = {t^3} - 3{t^2}\\)\\(\\therefore{}\\) \\(v = \\frac{{ds}}{{dt}} = 3{t^2} - 6t\\),  \\(a =\\frac{{{d^2}s}}{{d{t^2}}} = 6t - 6\\)Acceleration is zero, if \\(6t - 6 = 0\\)\\(\\Rightarrow{}\\) \\(t = 1\\)\\(\\therefore{}\\) Required velocity of particle at \\(t = 1\\) is \\(v = 3{(1)^2} -6(1)\\)   \\(\\Rightarrow \\,v =  - 3.\\)1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The maximum value of xy when \\(x + 2y = 8\\) is','(a)20','(b)16','(c)24','(d)8(e)4    1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Given \\(s = \\sqrt t \\). Now  \\(v = \\frac{{ds}}{{dt}} = \\frac{1}{{2\\sqrt t }}\\)Also \\(a = \\frac{{dv}}{{dt}} = \\frac{{ - 1}}{{2 \\times2{{(t)}^{3/2}}}}\\)\\(\\Rightarrow{}\\) \\(a \\propto \\frac{1}{{t\\sqrt t }}\\)or \\(a\\propto {v^3}\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The minimum value of \\(P(1,\\,1)\\) is','(a)\\(\\frac{{15}}{2}\\)','(b)\\(\\frac{{11}}{2}\\)','(c)\\(\\frac{{ - 13}}{2}\\)','(d)\\(\\frac{{71}}{8}\\) 1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a) \\({y^2} = 18x\\)Differentiate both sides w.r.t. t\\(2y\\left( {\\frac{{dy}}{{dt}}} \\right) = 18\\left( {\\frac{{dx}}{{dt}}} \\right)\\)\\(\\Rightarrow{}\\) \\(2y\\left( {2\\frac{{dx}}{{dt}}} \\right) = 18\\left({\\frac{{dx}}{{dt}}} \\right)\\),\\(\\left( {\\frac{{dy}}{{dt}} = 2\\frac{{dx}}{{dt}}}\\right)\\)\\(\\therefore{}\\) \\(4y = 18\\) or \\(y = \\frac{9}{2}\\) and \\(x =\\frac{{{y^2}}}{{18}} = \\frac{9}{8}\\)Hence the required point is \\(\\left( {\\frac{9}{8},\\frac{9}{2}} \\right)\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(P = (1,\\,1)\\), \\(Q = (3,\\,2)\\) and R is a point on x-axis  then the valueof \\(PR + RQ\\) will be minimum at ','(a)\\(\\left( {\\frac{5}{3},\\,0} \\right)\\)','(b)\\(\\left( {\\frac{1}{3},\\,0} \\right)\\)','(c)(3, 0)','(d)(1, 0)   1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(V = \\frac{4}{3}\\pi \\,{(x + 10)^3}\\) where x is thickness of ice.\\(\\therefore \\)\\(\\frac{{dV}}{{dt}} = 4\\pi {(10 + x)^2}\\frac{{dx}}{{dt}}\\)\\( = {a^x}{b^{2x - 1}}{(\\log a{b^2})^2}\\)  At \\(x = 5\\),\\(\\left({\\frac{{dx}}{{dt}}} \\right) = \\frac{1}{{18\\pi }}cm/\\min .\\)  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(f\\,(x) = 1 + 2{x^2} + {2^2}{x^4} + ..... + {2^{10}}{x^{20}}\\),  then\\(f(x)\\) has','(a)More than one minimum ','(b)Exactly one minimum','(c)At least one maximum','(d)None of these  1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)Volume =\\(V = \\frac{4}{3}\\pi {r^3}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{dV}}{{dt}} =4\\pi {r^2}.\\frac{{dr}}{{dt}}\\),  at \\(r = 7\\) cm35 cc/min =\\(4\\pi {(7)^2}\\frac{{dr}}{{dt}} \\Rightarrow \\frac{{dr}}{{dt}} =\\frac{5}{{28\\pi }}\\)Surface area, S = \\(4\\pi {r^2}\\)\\(\\frac{{dS}}{{dt}} = 8\\pi r\\frac{{dr}}{{dt}} = \\frac{{8\\pi .7.5}}{{28\\pi }} =10\\,\\)cm\\(^{2}\\)/min. 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Area of the greatest rectangle that can be inscribed in the ellipse\\(\\frac{{{x^2}}}{{{a^2}}} + \\frac{{{y^2}}}{{{b^2}}} = 1\\) is','(a)\\(\\sqrt {ab} \\)','(b)\\(\\frac{a}{b}\\)','(c)\\(2ab\\) ','(d)\\(ab\\)  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(p(t) = Answer =  + \\frac{{Answer = t}}{{Answer = + {t^2}}}\\)\\(\\frac{{dp}}{{dt}} = \\frac{{(Answer = + {t^2})\\,Answer =  - Answer = t\\,.\\,2t}}{{{{(Answer = +{t^2})}^2}}}\\)\\(= \\frac{{Answer = \\,(Answer = - {t^2})}}{{{{(Answer = + {t^2})}^2}}}\\)For extremum, \\(\\frac{{dp}}{{dt}} = 0 \\Rightarrow t = 10\\)Now \\({\\left. {\\frac{{dp}}{{dt}}} \\right|_{t\\, < \\,10}} > 0\\) and \\({\\left.{\\frac{{dp}}{{dt}}} \\right|_{t\\, > \\,10}} < 0\\)\\(\\therefore{}\\) At \\(t = 10\\), \\(\\frac{{dp}}{{dt}}\\)change from positive tonegative.\\(\\therefore{}\\) p is maximum at \\(t = 10\\).\\(\\therefore{}\\) \\({p_{\\max }} = p(10) = Answer =  + \\frac{{Answer = .10}}{{Answer = +{{10}^2}}} = Answer = \\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(\\frac{1}{{1 + {x^2}}}\\)is decreasing in the interval','(a)\\(( - \\infty ,\\, - 1]\\)','(b)\\(( - \\infty ,\\,0]\\)','(c)\\([1,\\infty )\\)','(d)\\((0,\\infty )\\)  1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(V = \\pi {r^2}h\\); \\(\\frac{{\\partial V}}{{\\partial t}} = \\pi \\left[{2r\\frac{{\\partial r}}{{\\partial t}}h + {r^2}\\frac{{\\partial h}}{{\\partial t}}}\\right]\\) \\(\\frac{{\\partial V}}{{\\partial t}} = \\pi \\,[2(4)\\,(3)\\,(6)\\, + \\,{(4)^2}( - 4)] =\\pi \\,[Answer = - 64] = 80\\,\\pi {\\rm{ }}cu\\,m/sec.\\)1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(\\frac{{x - 2}}{{x + 1}},(x \\ne  - 1)\\)is increasing on theinterval','(a)\\(( - \\infty ,\\,\\,\\,0]\\)','(b)[0, \\(\\infty \\))','(c)R','(d)None of these   1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(\\frac{{dy}}{{dx}} = 12 - 3{x^2} = 0 \\Rightarrow x =  \\pm 2\\)Hence points are (2,16) and (-2, -16). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the interval [0, 1], the function \\({x^2} - x + 1\\)is','(a)Increasing','(b)Decreasing','(c)Neither increasing nor decreasing','(d)None of these   1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Since \\(y = \\frac{{{a^2}}}{x},\\,\\,\\,\\therefore \\frac{{dy}}{{dx}} =  -\\frac{{{a^2}}}{{{x^2}}}\\)\\(\\therefore \\) At \\(({x_1},\\,{y_1}),\\)\\(\\frac{{dy}}{{dx}} = \\frac{{ -{a^2}}}{{x_1^2}}\\)Thus tangent to the curve will be \\(y - {y_1} = \\frac{{ - {a^2}}}{{x_1^2}}(x -{x_1})\\)\\(\\Rightarrow{}\\)  \\(yx_1^2 - {y_1}x_1^2 =  - {a^2}x + {a^2}{x_1}\\)\\(\\Rightarrow{}\\)  \\(y\\frac{1}{{\\sqrt {1 - {{\\left( {\\frac{{2x}}{{1 + {x^2}}}}\\right)}^2}} }}.\\frac{{2(1 + {x^2}) - 4{x^2}}}{{{{(1 + {x^2})}^2}}}\\), \\(({x_1}{y_1} = {a^2})\\)This meets the x-axis where \\(y = 0\\)\\(\\therefore {a^2}x = 2{a^2}{x_1},\\)\\(\\therefore x = 2{x_1}\\)\\(\\therefore \\)Point on the x-axis is \\((2{x_1},0)\\)Again tangent meets the y-axis where \\(x = 0\\)\\(x_1^2y = 2{a^2}{x_1},\\,\\;\\;\\;\\therefore y = \\frac{{2{a^2}}}{{{x_1}}}\\)So point on the y-axis is \\(\\left( {0,\\frac{{2{a^2}}}{{{x_1}}}} \\right)\\)Required area \\( = \\frac{1}{2}(2{x_1})\\left( {{{\\frac{{2a}}{{{x_1}}}}^2}}\\right) = 2{a^2}\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The interval in which the function \\({x^2}{e^{ - x}}\\)is non decreasing, is','(a)\\(( - \\infty ,\\,\\,2]\\)','(b)[0, 2]','(c)\\([2,\\,\\,\\infty )\\)','(d)None of these    1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(t = 2\\)for the point (2,-1)\\(\\frac{{dy}}{{dx}} = \\frac{{4t - 2}}{{2t + 3}} = \\frac{6}{7}\\)for \\(t = 2\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' On the interval \\(\\left( {0,\\frac{\\pi }{2}} \\right)\\), the function log sin x is','(a)Increasing','(b)Decreasing','(c)Neither increasing nor decreasing','(d)None of these   1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Given \\({y^2} = 2(x - 3)\\).....(i)Differentiate w.r.t. x, \\(2y.\\frac{{dy}}{{dx}} = 2 \\Rightarrow \\frac{{dy}}{{dx}}= \\frac{1}{y}\\)Slope of the normal \\( = \\frac{{ - 1}}{{\\left( {\\frac{{dy}}{{dx}}} \\right)}} = - y\\)Slope of the given line \\( = 2\\)\\(\\therefore y =  - 2\\)From equation (i), \\(x = 5\\)\\(\\therefore \\)Required point is \\((5,\\, - 2)\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(\\sin x - \\cos x\\)is increasing in the interval','(a)\\(\\left[ {\\frac{{3\\pi }}{4},\\frac{{7\\pi }}{4}} \\right]\\)','(b)\\(\\left[{0,\\frac{{3\\pi }}{4}} \\right)\\)','(c)\\(\\left[ {\\frac{\\pi }{4},\\frac{{3\\pi }}{4}} \\right]\\)','(d)None of these   1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Given curve \\({x^2} = 3 - 2y\\)...(i)Differentiate w.r.t. x,   \\(2x = 0 - 2\\frac{{dy}}{{dx}}\\)\\( \\Rightarrow\\)\\(\\frac{{dy}}{{dx}} =  - x\\)Slope of the tangent of the curve \\( =  - x\\)From the given line, slope \\( =  - 1\\),\\(\\therefore x = 1\\) and from equation(i), \\(y = 1\\). \\(\\therefore \\)Co-ordinate of the point is (1, 1).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(\\sin x - bx + c\\)will be increasing in the interval \\(( - \\infty,\\,\\,\\infty )\\), if','(a)\\(b \\le 1\\)','(b)\\(b \\le 0\\)','(c)\\(b <  - 1\\)','(d)\\(b \\ge 0\\)    1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(x = {t^2}\\)and \\(y = 2t\\)At \\(t = 1,\\;x = 1\\)and \\(y = 2\\)Now \\(\\left( {\\frac{{dy}}{{dx}}} \\right) = \\frac{{dy/dt}}{{dx/dt}} =\\frac{2}{{2t}} = \\frac{1}{t}\\)\\( \\Rightarrow \\)\\({\\left( {\\frac{{dy}}{{dx}}}\\right)_{t = 1}} = 1\\)\\(\\therefore \\)Equation of the normal at (1, 2) is \\(y - 2 =  -\\frac{1}{{\\frac{{dy}}{{dx}}}}(x - 1)\\)\\(\\Rightarrow{}\\) \\(y - 2 =  - 1(x - 1)\\)\\(\\Rightarrow{}\\) \\(x + y - 3 = 0\\).    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\({x^4} - 4x\\)is decreasing in the interval','(a)[-1, 1]','(b)\\(( - \\infty ,\\,\\,1)\\)','(c)\\([1,\\,\\, + \\infty )\\)','(d)None of these   1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(y = \\sin \\frac{{\\pi x}}{2}\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} =\\frac{\\pi }{2}\\cos \\frac{\\pi }{2}x\\)\\(\\Rightarrow{}\\) \\({\\left({\\frac{{dy}}{{dx}}} \\right)_{(1,\\,\\,1)}} = 0\\)\\(\\therefore{}\\) Equation of normal is \\(y - 1 = \\frac{1}{0}(x - 1)\\)\\(\\Rightarrow{}\\)\\(x = 1\\).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For which interval the given function \\(f(x) =  - 2{x^3} - 9{x^2} - 12x + 1\\) isdecreasing     ','(a)\\(( - 2,\\,\\infty )\\)','(b)\\(( - 2,\\, - 1)\\)','(c)\\(( - \\infty ,\\, - 1)\\)','(d)\\(( - \\infty ,\\,\\, - 2)\\)and \\(( - 1,\\,\\infty )\\) 1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(y = 2\\cos x\\)At \\(x = \\frac{\\pi }{4},\\;\\;y = \\frac{2}{{\\sqrt 2 }} = \\sqrt 2 \\)and\\(\\frac{{dy}}{{dx}} =  - 2.\\sin x\\)\\(\\therefore {\\left( {\\frac{{dy}}{{dx}}} \\right)_{x = \\pi /4}} =  - \\sqrt 2\\)\\(\\therefore \\) Equation of tangent at \\(\\left( {\\frac{\\pi }{4},\\sqrt 2 }\\right)\\) is \\(y - \\sqrt 2  =  - \\sqrt 2 \\left( {x - \\frac{\\pi }{4}} \\right)\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(f(x) = {x^3} - 27x + 5\\)is an increasing function, when','(a)\\(x <  - 3\\)','(b)\\(|x|\\, > 3\\)','(c)\\(x \\le  - 3\\)','(d)\\(|x|\\, < 3\\) 1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Let the point be \\(({x_1},\\,{y_1})\\), \\(\\therefore \\)\\({y_1} = b{e^{ -{x_1}/a}}\\)    .....(i)Also, curve \\(y = b{e^{ - x/a}}\\) \\(\\Rightarrow{}\\)  \\(\\frac{{dy}}{{dx}} =\\frac{{ - b}}{a}{e^{ - x/a}}\\)\\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{({x_1},\\,{y_1})}} = \\frac{{ - b}}{a}{e^{- {x_1}/a}} = \\frac{{ - {y_1}}}{a}\\) (by (i))Now, the equation of tangent of given curve at point \\(({x_1},\\,{y_1})\\) is \\(y- {y_1} = \\frac{{ - {y_1}}}{a}(x - {x_1})\\) \\(\\Rightarrow{}\\) \\(\\frac{x}{a} +\\frac{y}{{{y_1}}} = \\frac{{{x_1}}}{a} + 1\\)Comparing with \\(\\frac{x}{a} + \\frac{y}{b} = 1,\\) we get\\({y_1} = b\\,{\\rm{ and }}1 + \\frac{{{x_1}}}{a} = 1\\,\\, \\Rightarrow \\,\\,{x_1} = 0.\\)Hence, the point is (0, b).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = {x^2}\\)is increasing in the interval','(a)\\(( - 1,\\,1)\\)','(b)\\(( - \\infty ,\\,\\infty )\\)','(c)\\((0,\\,\\infty )\\)','(d)\\(( - \\infty ,\\,0)\\)  1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)For curve \\({y^2} = 4x\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} =\\frac{4}{{2y}}\\)\\(\\therefore{}\\)\\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{(1,\\,2)}} = 1\\) and forcurve \\({x^2} + {y^2} = 5\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = \\frac{{ -x}}{y}\\)\\(\\therefore{}\\) \\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{(1,\\,2)}} = \\frac{{ -1}}{2}\\)\\(\\therefore{}\\) Angle between the curves is  \\(\\theta  = {\\tan ^{ - 1}}\\left| {\\,\\frac{{\\frac{{ - 1}}{2}\\, - 1}}{{1 + \\left({\\frac{{ - 1}}{2}} \\right)}}\\,} \\right| = {\\tan ^{ - 1}}(3).\\)1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Function \\(f(x) = {x^4} - \\frac{{{x^3}}}{3}\\)is','(a)Increasing for \\(x > \\,\\frac{1}{4}\\)and decreasing for \\(x < \\frac{1}{4}\\)','(b)Increasing for every value of x','(c)Decreasing for every value of x','(d)None of these 1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b) \\(b{y^2} = {(x + a)^3} \\Rightarrow 2by.\\frac{{dy}}{{dx}} = 3{(x + a)^2}\\Rightarrow \\frac{{dy}}{{dx}} = \\frac{3}{{2by}}{(x + a)^2}\\)\\(\\therefore{}\\) Subnormal    = \\(y\\frac{{dy}}{{dx}} = \\frac{3}{{2b}}{(x +a)^2}\\)\\(\\therefore{}\\) Subtangent   = \\(\\frac{y}{{\\left( {\\frac{{dy}}{{dx}}} \\right)}}= \\frac{y}{{\\frac{{3{{(x + a)}^2}}}{{2by}}}} = \\frac{{2b{y^2}}}{{3{{(x +a)}^2}}}\\)= \\(\\frac{{2b{{\\frac{{(x + a)}}{b}}^3}}}{{3{{(x +a)}^2}}} = \\frac{2}{3}(x + a)\\)\\(\\therefore{}\\) (Subtangent)\\(^{2}\\)= \\(\\frac{4}{9}{(x + a)^2}\\)and \\(\\frac{{{{({\\rm{Subtangent}})}^2}}}{{{\\rm{Subnormal}}}} =\\frac{{\\frac{4}{9}{{(x + a)}^2}}}{{\\frac{3}{{2b}}{{(x + a)}^2}}} =\\frac{{8b}}{{27}}\\)\\(\\Rightarrow{}\\) (Subtangent)\\(^{2}\\)=  constant \\(\\times{}\\) (Subnormal).\\(\\therefore{}\\) (Subtangent)\\(^{2}\\)\\(\\propto{}\\) (Subnormal). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For every value of x function \\(f(x) = {e^x}\\)is','(a)Decreasing','(b)Increasing','(c)Neither increasing nor decreasing','(d)None of these   1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(y = a{x^2} + bx\\)\\(\\frac{{dy}}{{dx}} = 2ax + b \\Rightarrow {\\left( {\\frac{{dy}}{{dx}}}\\right)_{(2,\\, - 8)}} = 4a + b\\)Tangent is parallel to x-axis\\(\\therefore{}\\) \\(\\frac{{dy}}{{dx}} = 0 \\Rightarrow b =  - 4a\\)       \\(\\ldots{} \\)..(i)Now, point (2, -8) is on the curve of \\(y = a{x^2} + bx\\)\\(\\therefore{}\\)  \\( - 8 = 4a + 2b\\)        \\(\\ldots{} \\)\\(\\ldots{} \\)(ii)From (i) and (ii), we get \\(a = 2,\\,\\,b =  - 8\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x)\\)is zero in the interval (a, b) then in this interval it is','(a)Increasing function','(b)Decreasing function','(c)Only for a \\(>\\) 0and b\\(>\\)0is increasing function','(d)None of these  1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\sqrt x  + \\sqrt y  = a\\); \\(\\frac{1}{{2\\sqrt x }} + \\frac{1}{{2\\sqrt y}}\\frac{{dy}}{{dx}} = 0\\), \\(\\therefore{}\\) \\(\\frac{{dy}}{{dx}} =  - \\frac{{\\sqrty }}{{\\sqrt x }}\\)Hence tangent at (x, y) is \\(Y - y =  - \\frac{{\\sqrt y }}{{\\sqrt x }}(X - x)\\)or  \\(X\\sqrt y  + Y\\sqrt x  = \\sqrt {xy} \\,\\,\\left( {\\sqrt x  + \\sqrt y }\\right) = \\sqrt {axy} \\)or  \\(\\frac{X}{{\\sqrt a \\sqrt x }} + \\frac{Y}{{\\sqrt a \\sqrt y }} = 1\\).Clearly its intercepts on the axes are \\(\\sqrt a \\sqrt x \\) and \\(\\sqrt a \\sqrty \\).Sum of the intercepts = \\(\\sqrt a \\left( {\\sqrt x  + \\sqrt y } \\right) = \\sqrt a.\\sqrt a  = a\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For the every value of x the function \\(f(x) = \\frac{1}{{{5^x}}}\\)is','(a)Decreasing','(b)Increasing','(c)Neither increasing nor decreasing','(d)Increasing for x \\(>\\) 0and decreasing for x \\(<\\) 0   1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(y = x\\log x\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = 1 + \\log x\\)The slope of the normal = \\( - \\frac{1}{{(dy/dx)}} = \\frac{{ - 1}}{{1 + \\logx}}\\)The slope of the line \\(2x - 2y = 3\\) is 1.\\(\\therefore{}\\) \\(\\frac{{ - 1}}{{1 + \\log x}} = 1\\) \\(\\Rightarrow{}\\) \\(\\log x=  - 2\\) \\(\\Rightarrow{}\\) \\(x = {e^{ - 2}}\\)\\(\\therefore{}\\) \\(y =  - 2{e^{ - 2}}\\)\\(\\therefore{}\\) Co-ordinate of the point is \\(({e^{ - 2}},\\, - 2{e^{ - 2}})\\).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The interval for which the given function \\(f(x) = 2{x^3} - 3{x^2} - 36x + 7\\)is decreasing, is','(a)(- 2, 3)','(b)(2, 3)','(c)(2,- 3)','(d)None of these    1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c) Slope of the normal = \\( - \\frac{1}{{\\left( {\\frac{{dy}}{{dx}}} \\right)}}\\)This is parallel to x-axis \\(\\Rightarrow{}\\) \\( - \\frac{1}{{\\left({\\frac{{dy}}{{dx}}} \\right)}} = 0\\)\\(\\Rightarrow{}\\) \\(\\frac{{dx}}{{dy}} = 0\\).    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\sin x - \\frac{x}{2}\\) is increasing function, then  ','(a)\\(0 < x < \\frac{\\pi }{3}\\)','(b)\\( - \\frac{\\pi }{3} < x < 0\\)','(c)\\( - \\frac{\\pi }{3} < x < \\frac{\\pi }{3}\\)','(d)\\(x = \\frac{\\pi }{2}\\) 1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)Length of normal = \\(y\\sqrt {1 + {{\\left( {\\frac{{dy}}{{dx}}} \\right)}^2}} \\)Now, \\(\\frac{{dy}}{{dx}} = \\frac{{dy/d\\theta }}{{dx/d\\theta }} = \\frac{{a\\sin\\theta }}{{a(1 + \\cos \\theta )}} = \\frac{{\\sin \\theta }}{{1 + \\cos \\theta }} =\\frac{{2\\sin \\frac{\\theta }{2}\\cos \\frac{\\theta }{2}}}{{2{{\\cos }^2}\\frac{\\theta}{2}}}\\)\\(\\therefore{}\\)\\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{\\left( {\\theta  =\\frac{\\pi }{2}} \\right)}} = \\,{\\left[ {\\tan \\frac{\\theta }{2}} \\right]_{\\left({\\theta  = \\frac{\\pi }{2}} \\right)}} = 1{[y]_{\\left( {\\theta  = \\frac{\\pi }{2}}\\right)}} = a\\,\\left( {1 - \\cos \\frac{\\pi }{2}} \\right) = a\\)\\(\\therefore{}\\)  Length of normal = \\(a\\sqrt {1 + {{(1)}^2}}  = \\sqrt 2 a\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If x tends 0to \\(\\pi \\), then the given function \\(f(x) = x\\sin x + \\cos x + {\\cos ^2}x\\)is','(a)Increasing','(b)Decreasing','(c)Neither increasing nor decreasing','(d)None of these  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(y = a(\\sin \\theta  - \\theta \\cos \\theta ),\\,x = a(\\cos \\theta  + \\theta\\sin \\theta )\\)\\(\\frac{{dy}}{{d\\theta }} = a[\\cos \\theta  - \\cos \\theta  + \\theta \\sin \\theta ] =a\\theta \\sin \\theta\\)\\(\\frac{{dx}}{{d\\theta }} = a( - \\sin \\theta  + \\sin \\theta  + \\theta \\cos \\theta) = a\\theta \\cos \\theta\\)\\(\\therefore \\,\\,\\,\\frac{{dy}}{{dx}} = \\frac{{dy/d\\theta }}{{dx/d\\theta }} =\\frac{{a\\theta \\sin \\theta }}{{a\\theta \\cos \\theta }} = \\tan \\theta\\)\\(\\Rightarrow{}\\)  Slope of the tangent = \\(\\tan \\theta \\)\\(\\therefore{}\\)  Slope of the normal  = \\( - \\cot \\theta \\)Hence, equation of normal\\([y - a\\sin \\theta  + a\\theta \\cos \\theta ] =  - \\frac{{\\cos \\theta }}{{\\sin\\theta }}\\)\\([x - a\\cos \\theta  - a\\theta \\sin \\theta ]\\)\\(\\Rightarrow{}\\)  \\(y\\sin \\theta  - a{\\sin ^2}\\theta  + a\\theta \\sin \\theta\\cos \\theta \\)\\(=  - x\\cos \\theta  + a{\\cos ^2}\\theta  + a\\theta \\sin \\theta \\cos \\theta\\)\\(\\Rightarrow{}\\) \\(x\\cos \\theta  + y\\sin \\theta  = a({\\sin ^2}\\theta  + {\\cos^2}\\theta )\\)\\(\\Rightarrow{}\\)  \\(x\\cos \\theta  + y\\sin \\theta  = a\\)\\(\\therefore{}\\)Distance from origin = \\(\\frac{a}{{\\sqrt {{{\\sin }^2}\\theta  +{{\\cos }^2}\\theta } }} = {\\rm{a constant}}\\)    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(y = {x^2}{e^{ - x}}\\), then the interval in which y increases with respectto x is              ','(a)\\(( - \\infty ,\\infty )\\)','(b)\\(( - 2,\\,0)\\)','(c)\\((2,\\infty )\\)','(d)\\((0,\\,2)\\)   1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(x = 3{t^2} + 1,\\,y = {t^3} - 1\\)\\(\\therefore{}\\) \\(\\frac{{dx}}{{dt}} = 6t,\\) \\(\\frac{{dy}}{{dt}} = 3{t^2}\\)Now \\(\\frac{{dy}}{{dx}} = \\left({\\frac{{\\frac{{dy}}{{dt}}}}{{\\frac{{dx}}{{dt}}}}} \\right)\\) =\\(\\frac{{3{t^2}}}{{6t}} = \\frac{t}{2}\\)For \\(x = 1\\), \\(3{t^2} + 1 = 1 \\Rightarrow t = 0\\) \\(\\Rightarrow{}\\) Slope =\\(\\frac{0}{2} = 0\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(y = 2{x^3} - 9{x^2} + 12x - 6\\) is monotonic decreasing, when ','(a)\\(1 < x < 2\\)','(b)\\(x > 2\\)','(c)\\(x < 1\\)','(d)None of these  1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Let the point of contact be \\((h,k)\\), where \\(k = {h^4}\\). Tangent is \\(y -k = 4{h^3}(x - h)\\), \\(\\left[ {\\,\\frac{{dy}}{{dx}} = 4{x^3}} \\right]\\)It passes through (2, 0),  \\(\\therefore{}\\) \\( - k = 4{h^3}(2 - h)\\)\\(\\Rightarrow{}\\)  \\(h = 0\\)or 8/3 ,  \\(\\therefore{}\\) \\(k = 0\\) or(8/3)\\(^{4}\\)\\(\\therefore{}\\) Points of contact are (0, 0) and \\(\\left({\\frac{8}{3},\\,{{\\left( {\\frac{8}{3}} \\right)}^4}} \\right)\\)\\(\\therefore{}\\) Equation of tangents are\\(y = 0\\) and\\(y - {\\left( {\\frac{8}{3}} \\right)^4} = 4{\\left( {\\frac{8}{3}}\\right)^3}\\left( {x - \\frac{8}{3}} \\right)\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For which value of x, the function \\(f(x) = {x^2} - 2x\\) is decreasing ','(a)\\(x > 1\\)','(b)\\(x > 2\\)','(c)\\(x < 1\\)','(d)\\(x < 2\\)  1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(y = {x^2}\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = {m_1} = 2x\\)\\(\\Rightarrow{}\\)  \\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{(1,\\,1)}} = 2 ={m_1}\\) and \\(x = {y^2}\\) \\(\\Rightarrow{}\\) \\(1 = 2y\\,\\frac{{dy}}{{dx}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{dy}}{{dx}} = {m_2} = \\frac{1}{{2y}}\\)\\(\\Rightarrow{}\\) \\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{(1,\\,1)}} =\\frac{1}{2}\\)\\(\\therefore \\)Angle of intersection, \\(\\tan \\theta  = \\frac{{{m_1} - {m_2}}}{{1+ {m_1}{m_2}}}\\)=\\(\\frac{{2 - \\frac{1}{2}}}{{1 + 2 \\times\\frac{1}{2}}}\\)=\\(\\frac{3}{4}\\)\\(\\Rightarrow{}\\)\\(\\theta  = {\\tan ^{ - 1}}(3/4)\\).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = \\cos x - 2px\\) is monotonically decreasing for             ','(a)\\(p < \\frac{1}{2}\\)','(b)\\(p > \\frac{1}{2}\\)','(c)\\(p < 2\\)','(d)\\(p > 2\\) 1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(y = {x^3} - 3{x^2} - 9x + 5\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} =3{x^2} - 6x - 9.\\)We know that this equation gives the slope of the tangent to the curve. Thetangent is to parallel to x-axis, \\(\\therefore \\)\\(\\frac{{dy}}{{dx}} = 0\\).Therefore \\(3{x^2} - 6x - 9 = 0\\)   \\(\\Rightarrow{}\\) \\(x =  - 1,\\,3\\).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = k{x^3} - 9{x^2} + 9x + 3\\)is monotonically increasing in eachinterval, then      ','(a)\\(k < 3\\)','(b)\\(k \\le 3\\)','(c)\\(k > 3\\)','(d)None of these   1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)Clearly the point of intersection of curves is (0, 1). Now, slope of tangentof first curve \\({m_1} = \\frac{{dy}}{{dx}} = {a^x}\\log a\\)\\(\\Rightarrow{}\\) \\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{(0,\\,1)}} = {m_1} =\\log a\\)Slope of tangent of second curve \\({m_2} = \\frac{{dy}}{{dx}} = {b^x}\\log b\\)\\(\\Rightarrow{}\\) \\({m_2} = {\\left( {\\frac{{dy}}{{dx}}} \\right)_{(0,\\,1)}} =\\log b\\)\\(\\therefore{}\\) \\(\\tan \\alpha  = \\frac{{{m_1} - {m_2}}}{{1 + {m_1}{m_2}}} =\\frac{{\\log a - \\log b}}{{1 + \\log a\\log b}}\\).    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In which interval is the given function \\(f(x) = 2{x^3} - 15{x^2} + 36x + 1\\) ismonotonically decreasing','(a)[2, 3]','(b)(2, 3)','(c)\\(( - \\infty ,\\,2)\\)','(d)\\((3,\\,\\infty )\\)    1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)  \\({x^2} =  - 4y\\)\\(\\Rightarrow{}\\)\\(2x =  -4\\frac{{dy}}{{dx}}\\)\\(\\Rightarrow{}\\)\\(\\frac{{dy}}{{dx}} = \\frac{{ -x}}{2}\\)\\(\\Rightarrow{}\\)\\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{( - 4,\\, - 4)}} =2\\).We know that equation of tangent is,\\((y - {y_1})\\, = \\,{\\left( {\\frac{{dy}}{{dx}}} \\right)_{({x_1},\\,{y_1})}}(x -{x_1})\\) \\(\\Rightarrow{}\\) \\(y + 4 = 2(x + 4)\\)\\(\\Rightarrow{}\\) \\(2x - y + 4 = 0\\).    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = \\tan x - x\\)','(a)Always increases','(b)Always decreases','(c)Never decreases','(d)Sometimes increases and sometimes decreases    1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(y = 2{x^2} - x + 1\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = 4x - 1\\).We know that this equation gives the slope of tangent to the curve. Since thistangent is parallel to \\(y = 3x + 9,\\) therefore slope of the tangent is 3, so\\(4x - 1 = 3\\) or \\(x = 1.\\) Therefore \\(y = 2{x^2} - x + 1 = 2 - 1 + 1 = 2.\\)Thus the point \\((x,\\,y)\\) is (1, 2).    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = \\log (1 + x) - \\frac{{2x}}{{2 + x}}\\)is increasing on','(a)(0, \\(\\infty \\))','(b)(\\( - \\infty \\), 0)','(c)\\(( - \\infty ,\\infty )\\)','(d)None of these  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\({x^3} - 8{a^2}y = 0\\)  \\(\\Rightarrow{}\\)  \\(3{x^2} -8{a^2}\\,.\\,\\frac{{dy}}{{dx}} = 0\\)\\(\\Rightarrow{}\\)  \\(3{x^2} = 8{a^2}\\,.\\,\\frac{{dy}}{{dx}}\\)  \\(\\Rightarrow{}\\)\\(\\frac{{dy}}{{dx}} = \\frac{{3{x^2}}}{{8{a^2}}}\\)\\(\\therefore{}\\) Slope of the normal = \\( - \\frac{1}{{\\left( {\\frac{{dy}}{{dx}}}\\right)}}\\) = \\( - \\frac{1}{{\\frac{{3{x^2}}}{{8{a^2}}}}}\\)\\( =  -\\frac{{8{a^2}}}{{3{x^2}}}\\)Given \\(\\frac{{ - 8{a^2}}}{{3{x^2}}} = \\frac{{ - 2}}{3}\\) \\(\\therefore{}\\)\\((x,\\,y) = (2a,\\,a)\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the function \\(f(x) = \\frac{{K\\sin x + 2\\cos x}}{{\\sin x + \\cos x}}\\)isincreasing for all values of x, then','(a)\\(K < 1\\)','(b)\\(K > 1\\)','(c)\\(K < 2\\)','(d)\\(K > 2\\)  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(x = a(t + \\sin t)\\), \\(y = a(1 - \\cos t)\\)\\(\\therefore{}\\)\\(\\frac{{dy}}{{dx}} = \\frac{{dy/dt}}{{dx/dt}} = \\frac{{a(\\sint)}}{{a(1 + \\cos t)}}\\)=\\(\\tan \\frac{t}{2}\\)Length of the normal = \\(y\\sqrt {1 + {{\\left( {\\frac{{dy}}{{dx}}} \\right)}^2}}\\)= \\(a(1 - \\cos t)\\,\\sqrt {1 + {{\\tan }^2}(t/2)} \\)=\\(a(1 - \\cos t)\\,\\sec (t/2)\\)= \\(2a{\\sin ^2}(t/2)\\,\\sec \\,(t/2)\\)= \\(2a\\sin (t/2)\\,\\tan (t/2)\\).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of a in order that \\(f(x) = \\sqrt 3 \\) \\(\\sin x - \\cos x - 2ax + b\\)decreases for all real values of x, is given by','(a)\\(a < 1\\)','(b)\\(a \\ge 1\\)','(c)\\(a \\ge \\sqrt 2 \\)','(d)\\(a < \\sqrt 2 \\) 1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(y = {e^{2x}}\\) \\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = 2{e^{2x}}\\)\\(\\Rightarrow{}\\) \\(\\left( {\\frac{{dy}}{{dx}}} \\right)_{(0,\\,1)}^{} = 2\\)\\(\\therefore{}\\) Equation of tangent is, \\(y - 1 = 2(x - 0) \\Rightarrow y = 2x +1\\)This tangent meets x-axis, \\(\\therefore \\,\\,y = 0\\)\\(\\Rightarrow{}\\)  \\(0 = 2x + 1 \\Rightarrow x =  - 1/2\\)\\(\\therefore{}\\)  Co-ordinates of the point \\(\\left( { - \\frac{1}{2},\\,0}\\right)\\,\\,.\\)   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The interval in which the function \\({x^3}\\)increases less rapidly than\\(6{x^2}+ 15x + 5\\), is','(a)\\(( - \\infty ,\\, - 1)\\)','(b)(-5 , 1)','(c)(-1,5)','(d)(5 , \\(\\infty \\))    1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\((1 + {x^2})y = 2 - x\\)               ......(i)It meets x-axis, where \\(y = 0\\) i.e., \\(0 = 2 - x \\Rightarrow x = 2\\)So, (i) meets x-axis at the point (2, 0)Also from (i), \\(y = \\frac{{2 - x}}{{1 + {x^2}}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} = \\frac{{(1 + {x^2})\\,( - 1) - (2 -x)\\,(2x)}}{{{{(1 + {x^2})}^2}}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{dy}}{{dx}} =\\frac{{{x^2} - 4x - 1}}{{{{(1 + {x^2})}^2}}}\\)Slope of tangent at (2, 0)is,\\(\\frac{{4 - 8 - 1}}{{{{(1 + 4)}^2}}} = \\frac{{ - 5}}{{25}} = \\frac{{ -1}}{5}\\)\\(\\therefore{}\\) Equation of tangent at (2, 0) is ,\\(y - 0 =  - \\frac{1}{5}(x - 2) \\Rightarrow x + 5y = 2.\\)1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The values of a for which the function \\((a + 2){x^3} - 3a{x^2} + 9ax - 1\\)decreases monotonically throughout for all real x, are  ','(a)\\(a <  - 2\\)','(b)\\(a >  - 2\\)','(c)\\( - 3 < a < 0\\)','(d)\\( - \\infty  < a \\le  - 3\\) 1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)Curve is \\(y = b{e^{ - x/a}}\\)Since the curve crosses y-axis (i.e., x = 0) \\(\\therefore{}\\) \\(y = b\\)Now \\(\\frac{{dy}}{{dx}} = \\frac{{ - b}}{a}{e^{ - x/a}}\\). At point (0,b),\\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{(0,\\,b)}} = \\frac{{ - b}}{a}\\)\\(\\therefore{}\\)Equation of tangent is, \\(y - b = \\frac{{ - b}}{a}(x - 0)\\)    \\( \\Rightarrow \\)\\(\\frac{x}{a} + \\frac{y}{b} = 1.\\)1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = 2x + {\\cot ^{ - 1}}x + \\log (\\sqrt {1 + {x^2}}  - x)\\), then\\(f(x)\\)','(a)Increases in [0,\\(\\infty \\))','(b)Decreases in [0,\\(\\infty \\))','(c)Neither increases nor decreases in (0, \\(\\infty \\))','(d)Increases in (-\\(\\infty \\),\\(\\infty \\))   1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\(y = {x^2}\\) \\(\\Rightarrow{}\\) \\({\\left( {\\frac{{dy}}{{dx}}}\\right)_{(1,\\,1)}} = {m_1} = 2x = 2\\)\\(6y = 7 - {x^3} \\Rightarrow 6\\,.\\,\\frac{{dy}}{{dx}} =  - 3{x^2}\\)\\(\\Rightarrow{}\\)\\({\\left( {\\frac{{dy}}{{dx}}} \\right)_{(1,\\,1)}} = {m_2} =  -\\frac{1}{2}\\)Clearly \\({m_1}{m_2} =  - 1,\\) therefore angle of intersection is \\(\\frac{\\pi}{2}\\).  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For all real values of x, increasing function f(x) is','(a)\\({x^{ - 1}}\\)','(b)\\({x^2}\\)','(c)\\({x^3}\\)','(d)\\({x^4}\\) 1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)\\(y = 2{x^2} - x + 1\\). Let the coordinates of P is (h, k), then \\({\\left({\\frac{{dy}}{{dx}}} \\right)_{\\,\\,(h,\\,\\,k)}} = 4h - 1\\)Clearly \\(4h - 1 = 3\\) \\(\\Rightarrow{}\\) \\(h = 1\\) \\(\\Rightarrow{}\\) \\(k = 2\\);\\(\\therefore{}\\) P is (1, 2).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The least value of k for which the function \\({x^2} + kx + 1\\)is an increasingfunction in the interval \\(1 < x < 2\\)is','(a)- 4','(b)- 3','(c)- 1','(d)- 2 1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)\\(xy = {c^2}\\)\\(\\ldots{} \\)..(i)Subnormal = \\(y\\frac{{dy}}{{dx}}\\)\\(\\therefore{}\\) From (i), \\(y = \\frac{{{c^2}}}{x}\\) \\(\\Rightarrow{}\\)\\(\\frac{{dy}}{{dx}} = \\frac{{ - {c^2}}}{{{x^2}}}\\)\\(\\therefore{}\\)Subnormal\\( = \\frac{{y \\times ( - {c^2})}}{{{x^2}}} = \\frac{{ -y{c^2}}}{{{{\\left( {\\frac{{{c^2}}}{y}} \\right)}^2}}} = \\frac{{ -y{c^2}{y^2}}}{{{c^4}}} = \\frac{{ - {y^3}}}{{{c^2}}}\\)\\(\\therefore{}\\) Subnormal varies as \\({y^3}.\\)  1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The interval of the decreasing function \\(f(x) = {x^3} - {x^2} - x - 4\\)is','(a) \\(\\left( {\\frac{1}{3},\\,1} \\right)\\)','(b) \\(\\left( { - \\frac{1}{3},1}\\right)\\)','(c)\\(\\left( { - \\frac{1}{3},\\,\\frac{1}{3}} \\right)\\)','(d)  \\(\\left( { - 1, -\\frac{1}{3}} \\right)\\)    1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)If \\(\\sin x = \\cos x \\Rightarrow x = \\pi /4\\)If \\(y = \\sin x \\Rightarrow {\\left( {\\frac{{dy}}{{dx}}} \\right)_{x = \\pi /4}} =\\frac{1}{{\\sqrt 2 }}\\)If \\(y = \\cos x \\Rightarrow {\\left( {\\frac{{dy}}{{dx}}} \\right)_{x = \\pi /4}} =\\frac{{ - 1}}{{\\sqrt 2 }}\\)    \\(\\tan \\theta  = \\frac{{{m_1} - {m_2}}}{{1 + {m_1}{m_2}}} = 2\\sqrt 2  \\Rightarrow\\,\\theta  = {\\tan ^{ - 1}}(2\\sqrt 2 ).\\)1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = {x^3} - 3{x^2} - 24x + 5\\) is an increasing function inthe interval given below  ','(a)\\(( - \\infty ,\\, - 2) \\cup (4,\\infty )\\)','(b)\\(( - 2,\\infty )\\)','(c) (-2, 4)','(d)\\(( - \\infty ,\\,4)\\)   1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)We have, \\({y^2} = 5x - 1\\)\\(\\ldots{} \\)..(i)At \\((1, - 2)\\); \\(\\frac{{dy}}{{dx}} = {\\left[ {\\frac{5}{{2y}}} \\right]_{(1,\\, -2)}} = \\frac{{ - 5}}{4}\\)\\(\\therefore \\) Equation of normal at the point (1, -2) is,\\([y - ( - 2)]\\,\\left[ {\\frac{{ - 5}}{4}} \\right] + x - 1 = 0\\)\\(\\therefore 4x - 5y - 14 = 0\\)\\(\\ldots{} \\)\\(\\ldots{} \\)(ii)As the normal is of the form \\(ax - 5y + b = 0\\), comparing  this with (ii), weget \\(a = 4\\) and \\(b =  - 14\\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Which one is the correct statement about the function \\(f(x) = \\sin 2x\\)','(a)\\(f(x)\\) is increasing in \\(\\left( {0,\\frac{\\pi }{2}} \\right)\\) anddecreasing in \\(\\left( {\\frac{\\pi }{2},\\pi } \\right)\\)(b)\\(f(x)\\) is decreasing in \\(\\left( {0,\\frac{\\pi }{2}} \\right)\\) andincreasing in \\(\\left( {\\frac{\\pi }{2},\\pi } \\right)\\)(c)\\(f(x)\\) is increasing in \\(\\left( {0,\\frac{\\pi }{4}} \\right)\\) anddecreasing in \\(\\left( {\\frac{\\pi }{4},\\frac{\\pi }{2}} \\right)\\)(d)The statements ','','(b)\\(f(x)\\) is decreasing in \\(\\left( {0,\\frac{\\pi }{2}} \\right)\\) andincreasing in \\(\\left( {\\frac{\\pi }{2},\\pi } \\right)\\)(c)\\(f(x)\\) is increasing in \\(\\left( {0,\\frac{\\pi }{4}} \\right)\\) anddecreasing in \\(\\left( {\\frac{\\pi }{4},\\frac{\\pi }{2}} \\right)\\)(d)The statements (a), ','(b) and (c) are all correct   1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)Given \\(y = 6x - {x^2}\\)     .....(i)\\(\\frac{{dy}}{{dx}} = 6 - 2x\\)Since, tangent is parallel to the line \\(4x - 2y - 1 = 0\\)\\(\\therefore{}\\) \\(\\frac{{dy}}{{dx}} = 6 - 2x = \\frac{{ - 4}}{{ - 2}}\\Rightarrow 6 - 2x = 2 \\Rightarrow x = 2\\)Put the value of x in (i), we get \\(y = 8\\)Hence required point of tangency will be \\((2,\\,8)\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function f defined by \\(f(x) = (x + 2){e^{ - x}}\\) is','(a)Decreasing for all x','(b)Decreasing in \\(( - \\infty ,\\, - 1)\\) and increasing in \\(( - 1,\\infty )\\)','(c)Increasing for all x','(d)Decreasing in \\(( - 1,\\,\\infty )\\) and increasing in \\(( - \\infty ,\\, - 1)\\)   1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)Slope of normal \\( = \\frac{{ - dx}}{{dy}} = \\frac{{\\frac{{ - d\\left\\{ {a(1 +\\cos \\theta )} \\right\\}}}{{d\\theta }}}}{{\\frac{{d(a\\sin \\theta )}}{{d\\theta }}}}= \\frac{{a\\sin \\theta }}{{a\\cos \\theta }} = \\tan \\theta \\)Now, the equation of normal at \\(\\theta \\)is,\\(y - a\\sin \\theta  = \\tan \\theta [x - a(1 + \\cos \\theta )]\\)Clearly, this line passes through (a, 0).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = {x^3} - 10{x^2} +  - 10\\), then','(a)\\(f(x)\\)is decreasing in \\(] - \\infty ,10]\\) and increasing in \\([10,\\,\\infty[\\)','(b)\\(f(x)\\)is increasing in \\(] - \\infty ,10]\\) and decreasing in \\([10,\\,\\infty[\\)','(c)\\(f(x)\\)is increasing throughout real line','(d)\\(f(x)\\)is decreasing throughout real line    1','1','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (a)\\(\\frac{{dx}}{{d\\theta }} = a(1 + \\cos \\theta ),\\,\\frac{{dy}}{{d\\theta }} =a\\,(\\sin \\theta )\\)\\({\\left. {\\frac{{dy}}{{dx}}} \\right|_{\\theta  = \\frac{\\pi }{2}}} =\\frac{{\\frac{{dy}}{{d\\theta }}}}{{\\frac{{dx}}{{d\\theta }}}} = \\frac{{a\\sin \\theta}}{{a(1 + \\cos \\theta )}} = 1,\\)\\({\\left. y \\right|_{\\theta  = \\frac{\\pi }{2}}} = a\\)Length of sub-tangent ST = \\(\\frac{y}{{dy/dx}} = \\frac{a}{1} = a.\\)and length of sub-normal SN =\\(y\\frac{{dy}}{{dx}} = a\\,.\\,1 = a\\)Hence \\(ST = SN\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\frac{x}{{\\sin x}}\\)and \\(g(x) = \\frac{x}{{\\tan x}}\\), where \\(0 < x\\le 1\\), then in this interval [IIT Re-Exam]','(a)Both \\(f(x)\\) and \\(g(x)\\) are increasing functions','(b)Both \\(f(x)\\) and  \\(g(x)\\) are decreasing functions','(c)\\(f(x)\\)is an increasing function','(d)\\(g(x)\\) is an increasing function  1','3','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (c)\\({\\left. {x\\,} \\right|_{\\theta  = \\frac{\\pi }{4}}} = \\frac{2}{{2\\sqrt 2 }} =\\frac{1}{{\\sqrt 2 }},\\)\\({\\left. {y\\,} \\right|_{\\theta  = \\frac{\\pi }{4}}} = \\frac{3}{{2\\sqrt 2}},{\\left. {\\frac{{dy}}{{dx}}} \\right|_{\\theta  = \\frac{\\pi }{4}}}{\\left.{\\frac{{9\\,{{\\sin }^2}\\theta \\cos \\theta }}{{ - 6\\,{{\\cos }^2}\\theta \\sin \\theta}}} \\right|_{\\theta  = \\frac{\\pi }{4}}} = \\frac{{ - 3}}{2}.\\)\\(\\therefore \\) Equation of tangent is \\(\\left( {y - \\frac{3}{{2\\sqrt 2 }}}\\right) = \\frac{{ - 3}}{2}\\,\\left( {x - \\frac{1}{{\\sqrt 2 }}} \\right)\\)\\(\\Rightarrow{}\\) \\(3\\sqrt 2 x + 2\\sqrt 2 y = 6\\)\\(\\Rightarrow{}\\) \\(3x + 2y =3\\sqrt 2 \\).   1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Function \\(f(x) = 2{x^3} - 9{x^2} + 12x + 29\\) is monotonically decreasing, when','(a)\\(x < 2\\)','(b)x \\(>\\) 2','(c)x \\(>\\)1','(d)1\\(<\\) x \\(<\\) 2 1','2','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (b)Curve \\(x + y = {e^{xy}}\\)Differentiating with respect to x\\(1 + \\frac{{dy}}{{dx}} = {e^{xy}}\\left( {y + x\\frac{{dy}}{{dx}}} \\right)\\) or\\(\\frac{{dy}}{{dx}} = \\frac{{y{e^{xy}} - 1}}{{1 - x{e^{xy}}}}\\)\\(\\frac{{dy}}{{dx}} = \\infty  \\Rightarrow 1 - x{e^{xy}} = 0 \\Rightarrow 1 - x(x +y) = 0\\)This hold for \\(x = 1\\), \\(y = 0\\).    1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(2{x^3} + 18{x^2} - 96x + 45 = 0\\)is an increasing function when','(a)\\(x \\le  - 8,\\,x \\ge 2\\)','(b)\\(x <  - 2,x \\ge 8\\)','(c)\\(x \\le  - 2,x \\ge 8\\)','(d)\\(0 \\le x \\le  - 2\\)  1','4','DIFFERENTIATION AND APPLICATION OF DERIVATIVES','2','Answer =  (d)Given \\(f(x) = (x - 1){(x - 2)^2}\\)\\(f(x) = (x - 1)({x^2} + 4 - 4x)\\)\\(f(x) = ({x^3} - 5{x^2} + 8x - 4);\\)Now \\(f(x) = 3{x^2} - 10x + 8\\), \\(f(x) = 0\\)\\( \\Rightarrow 3{x^2} - 10x + 8 = 0\\Rightarrow (3x-4)(x-2)=0 \\Rightarrow x= \\frac{4}{3}, 2\\)Now \\(f(x) = 6x - 10\\)\\(f(4/3) = 6 \\times 4/3 - 10 < 0\\)\\(f(2) = 12 - 10 > 0\\)Hence at \\(x = \\frac{4}{3}\\) the function will occupy maximum value.\\(\\therefore \\) Maximum value =\\(f(4/3) = 4/27\\). 1',CURRENT_TIMESTAMP);
