INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' A labour throws 12 bricks per minute from the ground so as to just reach theroof 3.3 metres high. If each brick weights 3.75 kg, the horse power at which heis working, is','(a)0.0325','(b)0.325','(c)3.25','(d)None of these ','1','DYNAMICS','2','Answer =  (a) \\(P = \\frac{w}{t} = \\frac{{n \\times mgh}}{t}\\)Where \\(n = \\)No. of bricks, \\(m = \\)mass of each brickP \\( = \\frac{{12 \\times 3.75 \\times 10 \\times 3.3}}{{60}}\\)\\( = 3.75 \\times6.6\\)\\( = 24.Answer = )watt.= \\(\\frac{{24.Answer = }{{Answer = }\\)H.P. \\( = 0.Answer = \\)H.P.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' A weight of 10 metric tons is dragged in half an hour through a distance of etre up a rough inclined plane inclined at an angle of \\({30^o}\\) to thehorizon, the coefficient of friction being \\(1/\\sqrt 3 \\). The horse power(nearly) of the engine by which this work will be done is','(a)6','(b)8','(c)10','(d)20 ','2','DYNAMICS','2','Answer =  (b)Resisting forceF = Resisting force due to inclination + Resisting force due to friction \\( =mg\\sin {30^o} + \\mu R\\)\\(= mg\\sin {30^o} + \\left( {\\frac{1}{{\\sqrt 3 }}} \\right){\\rm{ }}mg\\cos {30^o}\\)\\( = 10 \\times Answer =  \\times 9.8\\)\\( = Answer = 0\\)Newtons\\(\\therefore{}\\) Work done per second\\( = \\frac{{Answer = 0 \\times Answer = }{{30 \\times60}} = \\frac{{Answer =  \\times 11}}{{3 \\times 6}}J\\)Horse power of the engine \\( = \\frac{{Answer =  \\times 11}}{{3 \\times 6 \\times Answer = }= 8\\)(approx.)  ',CURRENT_TIMESTAMP);
