INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Numerical value of the expression \\(\\left| {\\;\\frac{{3{x^3} + 1}}{{2{x^2} +2}}\\;} \\right|\\) for \\(x =  - 3\\) is','(a)4','(b)2','(c)3','(d)0   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\({\\left| {\\frac{{3{x^3} + 1}}{{2{x^2} + 2}}} \\right|_{x =  - 3}} = \\left|{\\frac{{3( - 27) + 1}}{{2(9) + 2}}} \\right| = \\left| {\\frac{{ - 80}}{{20}}}\\right| = 4\\).  ',CURRENT_TIMESTAMP);
