INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If  \\(g:','(a)\\( - 5 < P < 5\\)','(b)\\(P < 5\\)','(c)\\(P > 5\\)','(d)None of these ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(g(x) = {x^3} + \\tan x + \\frac{{{x^2} + 1}}{P}\\)\\(g( - x) = {( - x)^3} + \\tan ( - x) + \\frac{{{{( - x)}^2} + 1}}{P}\\)\\(g( - x) =  - {x^3} - \\tan x + \\frac{{{x^2} + 1}}{P}\\)\\(g(x) + g( - x) = 0\\)because \\(g(x)\\) is a odd function\\(\\therefore{}\\)\\(\\left[ {{x^3} + \\tan x + \\frac{{{x^2} + 1}}{P}} \\right]\\) \\( +\\left[ { - {x^3} - \\tan x + \\frac{{{x^2} + 1}}{P}} \\right] = 0\\)\\(\\Rightarrow{}\\) \\(\\frac{{2({x^2} + 1)}}{P} = 0\\) \\(\\Rightarrow{}\\) \\(0 \\le\\frac{{{x^2} + 1}}{P} < 1\\) because \\(x \\in [ - 2,\\,2]\\)\\(\\Rightarrow{}\\) \\(0 \\le \\frac{5}{P} < 1 \\Rightarrow P > 5\\). ',CURRENT_TIMESTAMP);
