INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{x^3}\\cot x}}{{1 - \\cos x}} = \\)','(a)0','(b)1','(c)2','(d)-2   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{{x^3}\\cot x}}{{1 - \\cos x}} =\\mathop {\\lim }\\limits_{x \\to 0} \\,\\left( {\\frac{{{x^3}\\cot x}}{{1 - \\cos x}}\\times \\frac{{1 + \\cos x}}{{1 + \\cos x}}} \\right)\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,{\\left( {\\frac{x}{{\\sin x}}} \\right)^3}\\times \\mathop {\\lim }\\limits_{x \\to 0} \\,\\cos x \\times \\mathop {\\lim }\\limits_{x\\to 0} \\,(1 + \\cos x) = 2\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{x({e^x} - 1)}}{{1 - \\cos x}} =\\)','(a)0','(b)\\(\\infty \\)','(c)-2','(d)2   ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\,\\frac{{x\\,({e^x} - 1)}}{{1 - \\cosx}} = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{2x\\,({e^x} - 1)}}{{4.{{\\sin}^2}\\frac{x}{2}}}\\)\\(= 2\\mathop {\\lim }\\limits_{x \\to 0} \\,\\left[ {\\frac{{{{(x/2)}^2}}}{{{{\\sin}^2}\\frac{x}{2}}}} \\right]\\,\\left( {\\frac{{{e^x} - 1}}{x}} \\right) = 2.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{1}{{|1 - x|}} =\\)','(a)0','(b)1','(c)2','(d)\\(\\infty \\) ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 1 - } \\,\\,\\frac{1}{{|\\,\\,1 - x\\,\\,|}} =\\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{1}{{1 - (1 - h)}} = \\infty \\)and \\(\\mathop {\\lim }\\limits_{x \\to 1 + } \\,\\,\\frac{1}{{|\\,\\,1 - x\\,\\,|}} =\\mathop {\\lim }\\limits_{h \\to 0} \\frac{1}{{1 + h - 1}} = \\infty \\)Hence \\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\frac{1}{{|\\,\\,1 - x\\,\\,|}} = \\infty.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{n \\to \\infty } \\frac{{n{{(2n + 1)}^2}}}{{(n + 2)({n^2} +3n - 1)}} =\\)','(a)0','(b)2','(c)4','(d)\\(\\infty \\)   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{n \\to \\infty } \\,\\frac{{n\\,{{(2n + 1)}^2}}}{{(n +2)\\,\\,({n^2} + 3n - 1)}} = \\mathop {\\lim }\\limits_{n \\to \\infty }\\,\\,\\frac{{4{n^3} + 4{n^2} + n}}{{{n^3} + 5{n^2} + 5n - 2}}\\)\\(= \\mathop {\\lim }\\limits_{n \\to \\infty } \\frac{{{n^3}\\,\\left( {4 + \\frac{4}{n}+ \\frac{1}{{{n^2}}}} \\right)}}{{{n^3}\\left( {1 + \\frac{5}{n} + \\frac{5}{{{n^2}}}- \\frac{2}{{{n^3}}}} \\right)}} = 4\\) ',CURRENT_TIMESTAMP);
