INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to a} \\frac{{\\sqrt {3x - a}  - \\sqrt {x + a} }}{{x -a}} =\\)','(a)\\(\\sqrt 2 a\\)','(b)\\(1/\\sqrt {2a} \\)','(c)2a','(d)\\(1/2a\\)  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{\\sqrt {3x - a}  - \\sqrt {x + a}}}{{x - a}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{\\sqrt {3x - a}  - \\sqrt {x + a}}}{{(x - a)}} \\times \\frac{{\\sqrt {3x - a}  + \\sqrt {x + a} }}{{\\sqrt {3x - a}  +\\sqrt {x + a} }}\\)\\(= \\frac{2}{{2\\sqrt {2a} }} = \\frac{1}{{\\sqrt {2a} }}\\)Aliter : Apply L-Hospitals rule\\(\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{\\sqrt {3x - a}  - \\sqrt {x + a} }}{{x- a}} = \\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{3}{{2\\,\\sqrt {3x - a} }} -\\frac{1}{{2\\,\\sqrt {x + a} }}\\)\\(= \\frac{3}{{2\\sqrt {2a} }} - \\frac{1}{{2\\sqrt {2a} }} = \\frac{1}{{\\sqrt {2a}}}.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{\\log x}}{{x - 1}} = \\)','(a)1','(b)-1','(c)0','(d)\\(\\infty \\) ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\,\\frac{{\\log \\,[(x - 1) + 1]}}{{x - 1}}= 1.\\)Aliter : Apply L-Hospitals rule, \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{\\logx}}{{x - 1}} = \\mathop {\\lim }\\limits_{x \\to 1} \\frac{{\\frac{1}{x}}}{1} = 1\\)    ',CURRENT_TIMESTAMP);
